INSERT INTO `plan_day_dtl` (`PLAN_CODE`, `TRV_DEG`, `LOC_SEQ`, `LOC_NAME`, `LOC_TYPE`, `AREA_CODE`, `LOC_ADDR`, `LOC_POST_CODE`, `CONTENT_ID`, `LOC_TEL`, `OVERVIEW`, `POSITION_X`, `POSITION_Y`, `LOC_IMG_URL`, `LOC_BDG`, `LOC_MEMO`, `LOC_DISTANCE`, `REGI_DATE`, `MODI_DATE`) VALUES
	('PLAN20190900000001', 1, 1, '가평 연인산마을', '12', NULL, '경기도 가평군 북면 백둔로 417-11', '12406', '129285', '', '백둔리 산촌마을은 북쪽으로 위치하고 있는 명지산의 깊은 산자락에 자리잡고 있는 마을로 백둔천을 따라 7개의 자연부락으로 구성되어 있는 산자수려한 산촌마을로,명지산과 백둔계곡은 높고 깊기로 유명하여 사계절 도시민이 즐겨찾는 휴양지로 유명하다.백둔리 산촌마을은 벼농사와 사과 과수원이 농가소득의 큰 비중을 차지하고 있으며, 여름철 백둔천을 찾는 피서객을 대상으로 한 민박 등의 수입과 밭작물, 표고버섯, 소 사육으로 소득을 올리고 있으며, 최근에는 명지산 군립공원, 용추구곡과 인접한 연인산 철쭉제, 허수아비 축제등 이벤트 행사를 통한 관광객 유치가 이루어지면서 수려한 자연환경과 조화되는 개발로 가족과 오붓하게 여행을 하기에 더 없이 좋은 마을이다.산자락 아래에 조성해 놓은 잔디광장에는 운동시설과 휴식을 위한 정자 등이 설치되어 있고 농사체험장과 야생화 단지를 조성하여 자연학습과 더불어 산촌의 정취를 만끽할 수 있도록 하였다. 또한, 마을에 설치되어 있는 기존의 노후된 상수도시설을 정비하여 안심하고 식수를 마실 수 있도록 하였으며 오수정화시설을 설치하여 청정지역이 오염되지 않고 천혜의 비경을 유지할 수 있도록 친환경적으로 산촌개발을 완료하였다. <br>', '127.4623783495', '37.9014407526', 'http://tong.visitkorea.or.kr/cms/resource/76/532876_image2_1.jpg', '3000', '', '0', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 1, 2, '가평 현충탑', '12', NULL, '경기도 가평군 가평읍 호반로 2540', '12422', '1104176', '', '경기도 가평군 가평읍 달전리에 있는 현충탑은 나라를 위해 싸우다가 단 하나뿐인 목숨을 조국의 재단에 바친 이 고장 출신 전몰용사 681위와 순직경찰관 33위의 숭고한 희생정신을 계승하기 위하여 세운것으로 탑은 가로 5.8m, 세로 4.1m, 높이 8m이며 탑의 높이와 분향대로 오르는 8계단은 8개 읍면을 상징하고 백색의 화강암 탑신은 평화를 사랑하는 군민의 순결한 마음을 나타내며 중앙의 청동제 불꽃은 탑에 새겨진 젊은 연령 714위의 꺼질 줄 모르는 조국애를 상징하고 있다. 탑 뒤 편의 현충비는 1959년 7월 평창읍 하리 34번지에 세웠던 것을 이 현충탑을 세우면서 이곳으로 옮겨진 것이다. <br>', '127.5157483134', '37.8126354086', 'http://tong.visitkorea.or.kr/cms/resource/84/2480684_image2_1.jpg', '4500', '111111', '10919', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 1, 3, '강영천효자문', '12', NULL, '경기도 가평군 북면 논남기길 402-19', '12400', '1201163', '', '효자 강영천은 진주강씨로 3세 때 부친을 여의고 홀어머니 연일정씨와 함께 살던 중 7세 때 모친이 병으로 몸져 눕게 되었다. 나이는 비록 어렸으나 효성이 지극하였던 영천은 병든 어머니의 수발을 들던 중 병이 악화되어 정신을 잃자 자신의 손가락을 문지방에 깨트려 피를 내어 입에 흘려놓고 쓰러지고 말았다. 어머니가 깨어나 피를 흘리며 쓰러진 어린아들을 발견하여 정성껏 간호한 끝에 깨어날 수 있었다. 정문앞에 걸린 ', '127.4214078804', '37.9689765715', 'http://tong.visitkorea.or.kr/cms/resource/98/2480698_image2_1.jpg', '3000', '', '19236', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 1, 4, '그린캠프유스호스텔', '32', NULL, '경기도 가평군 북면 화악산로 729-64', '12402', '137995', '031-581-5304', '삼림욕을 즐길수 있는 낙엽송과 잣난무숲이 병풍처럼 놓여있고, 옆으로는 맑고 깨끗한 물이 흐르는 그린캠프유스호스텔은 서울에서 1시간 거리에 위치하여 있으므로 주말을 가족이나 친구들, 동료와 함께 보내려는 이들에게는 안성맞춤이다.<br />푸르른 하늘과 아기자기한 가평의 산, 그리고 플라워아이랜드와 함께 멋진 시간을 보낼수 있으며 계곡 방갈로에서의 바베큐파티로 한층 더 즐거운 휴식을 즐길 수 있다. <br>', '127.5537524448', '37.9320833189', 'http://tong.visitkorea.or.kr/cms/resource/60/1786060_image2_1.jpg', '45000', '', '12332', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 1, 5, '나무아래오후', '39', NULL, '경기도 가평군 상면 임초밤안골로 254', '12448', '2024695', '031-585-3203', '※ 영업시간 10:30 ~ 20:30\n\n경기도 가평에 위치한 나무아래오후는 로스팅 커피와 화덕에 구운 피자를 제공하는 로스팅 하우스 카페이다. 카페에서 직접 로스팅 한 커피로 여러 가지 원두가 준비되어 있어 다양한 맛의 핸드드립 커피를 맛볼 수 있다. 또한 각종 차와 빙수 및 아이스크림을 제공하며, 화덕에 구운 피자와 파스타, 샐러드 등을 맛볼 수 있다.', '127.3636528734', '37.7504065171', 'http://tong.visitkorea.or.kr/cms/resource/74/2024674_image2_1.jpg', '8000', '', '26204', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 1, 6, '동기간', '39', NULL, '경기도 가평군 가평읍 보납로 459-158', '12410', '2011963', '031-581-5570', '※ 영업시간 11:00 ~ 20:00 (주말 10:30 ~ 21:00) \n※ 오리, 닭 메뉴는 1시간 전 예약 권장\n\n동기간은 가평군 가평읍에 위치한 닭, 오리요리 전문점으로 백숙, 훈제, 구이 등 다양한 메뉴를 맛볼 수 있다. 방갈로 형태의 별실과 야외 테이블에서 식사가 가능하며, 저녁에는 모닥불을 피워 놓기도 한다. 일부 메뉴의 경우 주문 즉시 바로잡아 요리를 내며, 조리시간이 길기 때문에 사전에 예약 후 방문하는 것이 좋다.', '127.5489884769', '37.8552782833', 'http://tong.visitkorea.or.kr/cms/resource/85/2010785_image2_1.jpg', '12000', '', '20048', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 2, 1, '용추폭포', '12', NULL, '경기도 가평군 가평읍 승안리', '12408', '129633', '', '용추폭포는 높이 5m 정도의 작은 폭포이지만 수량은 많은 편이다. 여기에 흐르는 물의 맑기가 구슬같이 흰 바위들과 어울려 뭍이 흐른다기 보다는 바위가 흐른다는 느낌을 준다. 이 기암괴석을 이름하여 와룡추라 하듯이 흡사 하늘에서 내려 온 용이 누워 있는 형상이다. 지금도 가끔씩 용녀가 내려와 목욕을 한다고 믿어지고 있을 정도이니 그 신비스런 경관을 상상할 수 있다. 이곳을 옥계동이라 부르며 용추폭포로 시작하여 아홉군데의 아름다운 경치가 있으니 이를 옥계구곡이라 부르는데 다음과 같다. <br />①와룡추 ②무송암 ③탁령뇌 ④고실탄 ⑤일사대 ⑥추월담 ⑦청풍협 ⑧귀유연 ⑨농완계\n<br /><br />용추폭포는 가평읍에서 북서쪽으로 계곡을 따라 6km쯤 올라간 곳에 위치하며, 폭포가 있는 주차장(조옥동)에서 북서로 이어지는 계곡을 따라 올라가면 장장 10km가 넘는 계곡이 올라갈수록 새로운 맛을 느끼게 한다. \n 특히 입구에서 30분쯤 올라간 지점의 크게 휘어져 들어간 굽이가 아름답다. 암벽과 능선을 바짝 끼고 굽이쳐 흐르는 물살이 보기 좋고 자연 풀장을 이룬 탕이 지나가는 사람을 부른다. \n 이후로도 올라가면서 펼쳐지는 정경이 각각 특이한 멋을 나타내고 있고 인적이 드물기 때문에 호젓한 맛을 즐길 수 있다. <br>', '127.4819279838', '37.8531717340', 'http://tong.visitkorea.or.kr/cms/resource/78/218878_image2_1.jpg', '5000', '', '5907', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 2, 2, '운악산 두부마을', '12', NULL, '경기도 가평군 조종면 현등사길 14', '12432', '613127', '', '기암괴석이 곳곳에 펼쳐져 있는 계곡이 아름다운 경기 5악산 중 하나인 운악산 자락에 위치한 두부마을은 손으로 직접 빚은 손두부를 맛볼 수 있다.<br>', '127.3489730873', '37.8653512035', 'http://tong.visitkorea.or.kr/cms/resource/32/2482132_image2_1.jpg', '30000', '', '11778', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 2, 3, 'HOTEL 자라', '32', NULL, '경기도 가평군 가평읍 가화로 41-27', '12420', '2618398', '010-4104-9614', 'HOTEL 자라는 가평터미널 도보 3분 거리, 가평역 차량 5분거리 위치에 있으며 주변에 빠지 및 워터 물놀이 파크같은 다수 업체들이 확보되있다. \n세련된 객실 디자인과 합리적인 가격으로 노하드 PC가 설치되어 있고 가평 마트 도보 1분 거리로 이용이 편리하다.', '127.5149342060', '37.8226120104', 'http://tong.visitkorea.or.kr/cms/resource/90/2618390_image2_1.JPG', '60000', '', '15359', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 2, 4, '동기간', '39', NULL, '경기도 가평군 가평읍 보납로 459-158', '12410', '2011963', '031-581-5570', '※ 영업시간 11:00 ~ 20:00 (주말 10:30 ~ 21:00) \n※ 오리, 닭 메뉴는 1시간 전 예약 권장\n\n동기간은 가평군 가평읍에 위치한 닭, 오리요리 전문점으로 백숙, 훈제, 구이 등 다양한 메뉴를 맛볼 수 있다. 방갈로 형태의 별실과 야외 테이블에서 식사가 가능하며, 저녁에는 모닥불을 피워 놓기도 한다. 일부 메뉴의 경우 주문 즉시 바로잡아 요리를 내며, 조리시간이 길기 때문에 사전에 예약 후 방문하는 것이 좋다.', '127.5489884769', '37.8552782833', 'http://tong.visitkorea.or.kr/cms/resource/85/2010785_image2_1.jpg', '9000', '', '4705', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 2, 5, '명지쉼터가든', '39', NULL, '경기도 가평군 북면 가화로 777', '12407', '1210331', '031-582-9462', '잣국수로 유명한 명지쉼터가든은 경기도 음식선정의 집으로 TV방송으로 여러번 방영되었던 음식점이다. 잣가루와 밀가루를 이용하여 면을 만들고 고소한 100% 잣국물을 부어 ‘김덕수잣국수’라는 이름으로 상표등록을 하고 ‘잣국수 및 그 외 제조방법’으로 특허등록을 하였다. 특히, 잣국수는 밑반찬으로 나오는 김치를 곁들어 먹으면 그 맛이 일품이다. 닭과 청둥오리는 냉장보관한 닭이 아니라 직접 잡아서 요리를 하고, 민물매운탕도 민물고기를 직접 잡아서 매운탕을 끓여 주고 있다. <br>', '127.5296896087', '37.8765948894', 'http://tong.visitkorea.or.kr/cms/resource/83/1927483_image2_1.jpg', '5000', '', '2912', '2019-09-24', '2019-09-24'),
	('PLAN20190900000001', 2, 6, '오성가든', '39', NULL, '경기도 가평군 상면 수목원로 133', '12448', '1213812', '031-585-5501', '아침고요수목원 입구에 있는 오성가든은 모범음식점으로 본관 및 별관, 야외에서도 식사가 가능하며, 단체예약은 미리 해야 한다.<br />\n10여 가지의 몸에 좋은 한약재와 엄선된 고기로 조리한 한방보쌈은 다시 바비큐기계에서 기름을 빼내었기 때문에 은은한 한방향과 잣향의 고소함이 어우러져 그 맛이 담백하며 건강에도 유익하며, 모듬보쌈정식은 한방보쌈과 훈제오리보쌈과 각종 야채, 명이나물과 산나물 등의 반찬과 함께 된장찌개가 나온다. 또한 막국수는 100% 봉평메밀을 사용하여 만들었다.\n<br>', '127.3633302289', '37.7659491474', 'http://tong.visitkorea.or.kr/cms/resource/91/1751891_image2_1.jpg', '9000', '', '19115', '2019-09-24', '2019-09-24'),
	('PLAN20190900000002', 1, 1, '강원 춘천시', '12', NULL, '강원 춘천시', '200-010', '127746', '', '강원도 서쪽 끝에 있으며 동쪽은 인제군, 서쪽은 경기도 가평군, 남쪽은 홍천군, 북쪽은 화천군과 양구군에 접해 있다.\n 전체 면적은 1,116.35㎢이며 총인구가 275,791명(2014년 12월 기준)에 이른다. 1읍 9면 15동으로 이루어져 있으며 시청소재지는 옥천동이다.\n<br><br>\n * 자연환경 \n <br>태백산맥 줄기들이 북북동에서 남남서의 방향으로 군의 남동부와 북서부를 지난다.\n 구절산, 연엽산, 대룡산, 가리산, 금병산, 응봉, 촛대봉, 가덕산, 북배산 등이 대표적인 산이다.\n 시내 한가운데에는 춘천의 상징이며 진산인 봉의산이 솟아 있다. 하천은 금강산에서 발원한\n 북한강이 춘천분지에 이르러 소양강과 합류하여 남서류하다 홍천군과의 경계를 따라 서류하는 홍천강과 가평군에서 만난다.\n 이밖에 지촌천이 춘천호로, 덕두원천이 의암호로 청평천이 소양호로 각각 유입되며, 춘천댐,\n 의암댐, 소양댐이 관광 및 홍수 조절에 큰 몫을 하고 있다.<br>\n<br>\n * 역사 \n <br>고대 - 부족국가시대에는 이지역에서 맥국이 번창하였으며 삼국시대에 이르러 5세기경 고구려의 영역에 들었다가 뒤에 신라의 영토가 되어 637년 (선덕여왕6) 우수주가 설치되었다.\n        757년(경덕왕16) 삭주로 되었다가 뒤에 광해주로 고쳐졌다.\n <br>고려 - 940년(태조23) 춘주로 개칭되었고 1018년(현종9)에는 안변도호부에 예속되었다.\n <br>조선 - 조선 건국직후에 강원도에 속하였고 1413년(태종13) 춘천군으로 되었다가 1415년(태종15) 춘천도호부로 승격되었다.\n<br>\n 근대 - 1888년에 유도부로 승격 되어 경기도에 속하였으며, 1895년 원주의 감영을 없애면서 \n        도부사를 이곳으로 옮김과 동시에 춘천군으로 복구되고 다시 강원도에 소속되었다.\n        그리고 다음해에는 강원도 관찰사가 소재하는 관찰부로서 춘천부로 승격되었으며 1910년 경술국치 후 관찰부를 도청으로 개칭함으로써 강원도청의 소재지가 되었다.\n        1931년 춘천면이 춘천읍으로 승격되었고, 1937년 경춘선 철도가 개통되어 이후 괄목할 만한 발전을 이룩하게 되었다.\n <br>현대 - 1946년 춘천읍이 춘천부로 승격되면서 나머지 지역 8개 면이 춘성군으로 분리, 독립하였다.\n        또한 1949년 지방자치제의 시행에 따라 부가 시로 개칭되면서 춘천시가 되었고 1964년 춘천댐이, 1973년 소양강 다목적 댐이 건설되었다.\n        1995년 춘천군과 춘천시가 통합되면서 춘천시가 되었다.<br>\n<br>\n * 문화<br> \n 문화제 - 춘천지방에서 매년 9월에 거행되는 소양제는 1983년 제1회가 개최되었으며 주요 행사로는 전야제로 봉의산제, 본행사로 동·면 대항 민속예술경연, 문예경연 등이 있다. <br><br>    \n 민속 - 차전놀이, 종경도놀이, 조조잡기 등이 있었으나 거의 자취를 찾아볼 수 없게 되었다.1985년 제66회 전국체전기념 및 제3회 소양제에서는 뗏목놀이가 \'춘천 아리랑\'과 함께 본격적으로 재현되었고, 유등 띄우기, 무당굿놀이등과 역대 전국민속예술경연대회에 참가했던\n 강원도의 민속놀이 전종목을 연희하여 전통문화예술의 뿌리를 정착시키는 새로운 계기를 마련하였다.<br>\n <br>동  제 - 소양강 \'신용연의 기우제\'는 그 유래가 오래된 것으로 추정된다. 신용연은 동쪽의\n 소양강과 서쪽의 자양강이 합쳐지는 곳에서 일어나는 소용돌이인데, 물이 좌우 어느 쪽으로\n 도느냐에 따라서 그 해에 강우량과 농사의 풍흉을 점칠 수 있어 신성시하였다.\n 기우단은 소양강과 자양강이 합치는 중도 남단 백로주의 백사장에 설치하여 가뭄이 계속되면 관단위로 관복 정장을 한 부사가 제를 올렸다.이 밖에도 1983년 제1회 소양제 때 봉의산 중턱에 순의비가 세워져 해마다 호국영령들의 명복과 춘천의 발전을 기원하는 봉의산제를 지내고 있다.<br>', '127.7301156566', '37.8813063972', 'http://tong.visitkorea.or.kr/cms/resource/44/1597344_image2_1.jpg', '3000', '', '0', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 1, 2, '강원 철원군', '12', NULL, '강원 철원군', '269-701', '125287', '', '강원도 북서부, 영서지방의 북부에 위치한 군으로, 동쪽은 화천군, 서쪽은 경기도 연천군, \n 남쪽은 경기도 포천군에 접하고 있으며,북쪽으로는 휴전선이 지난다. 면적은 898.47㎢이며,\n 인구는 47,590명(2014년 12월 기준)이다. 행정구역은 4읍 7면으로 되어 있으며 군청 소재지는 갈말읍 신철원리이다.<br>\n<br>\n * 자연환경 \n <br>북동부에는 태백산맥이 지나며 중앙부는 추가령열곡 안에 위치한다.\n 북서부에는 해발 200∼500m 높이의 철원용암지대가 있어 평지를 이루며, 남동부는 해발\n 1,000m 내외의 산지로 태백산맥 줄기에 연결되고, 대표적인 산으로는 오성산, 대성산, \n 백암산, 명성산, 금학산 등이 있다. \n 한탄강이 군의 중앙부를 북북동에서 남남서 방향으로 흐르면서 그 지류인 남대천을 합류하고 임진강으로 흘러 들어간다.<br>\n<br>\n * 역사\n <br>고대 - 삼국시대에는 고구려가 철원 또는 모을동비라 하였으며,삼국통일 이후 경덕왕 때 \n 철성으로 개칭되었다. 901년(효공왕5) 송악에서 후고구려를 건국한 궁예가 904년 국호를 \n 마진으로 고치고  다음해에는 지금의 철원읍 홍원리로 도읍을 옮겼으며 911년 국호를 다시 \n 태봉으로 고치고 918년까지 통치하였다.\n<br>\n 고려 - 918년(태조1) 왕건이 고려를 세우고 송악으로 천도하면서 이 지역을 동주라 하였고\n 995년(성종14) 단련사를 파견하였다.\n 1254년(고종41)직제를 낮추어 동주현이라 하였다가 뒤에 목으로 승격시켰으며, 1310년(총선왕2) 다시 낮추어 철원부로 고쳤다.\n 1390년(공양왕2) 교주도에서 경기좌도로 이속되었다.\n<br>\n 조선 - 1394년(태조3) 경기좌도에 속하였고, 13년(태종13) 철원도호부로 승격되었고, \n 1434년(세종16) 경기도에서 강원도로 이속되었다.\n <br>근대 - 1895년 춘천부 철원군으로 되었다가 다음해에 강원도 철원군이 되었다.\n 1931년 철원면이 읍으로 승격되었고, 1941년 경기도 삭녕군 내문면, 인목면, 마장면이 \n 철원군으로 편입되었다.\n <br>현대 - 1963년 구 김화군이 철원군 김화읍으로 개편되고 철원군 신서면이 경기도 연천군으로\n 편입되었다. 1972년 철원군 북면 유정리, 홍원리와 내문면 독검리가 철원읍에 편입되고 \n 전 평강군 남면 정연리가 철원군  갈말면에 편입되었으며  1973년 서면 청량리와 도창리가\n 김화읍에 편입되었다.  1979년 갈말면이 갈말읍으로, 1980년 동송면이 동송읍으로 승격되었다.<br>\n<br>\n * 문화 \n <br>문화제 - 태봉제는 철원군에서 매년 10월에 개최하는 문화예술행사다.\n 철원은 풍요롭고 광활한 곡창지대이며 또한 군사적 요충지이므로 옛 조상의 기개를 이어받아 통일의 전진기지로서의 긍지를 드높이기 위하여 군민축제일을 태봉제라고 하였다. \n 특히 1954년 10월21일 군정으로부터 행정권을 이양받아 실질적인 회복을 한 것을 기념하기 \n 위해 1982년부터 매년 개최하고 있다.\n 주요 행사는 전래 향토 문화 발굴 재현을 위한 민속경연을 비롯해서 씨름.그네뛰기.널뛰기\n 등의 민속경기와 체육경기, 백일장, 시화전, 시화, 가요경연대회 등을 거행한다.<br>\n<br>\n 민속 - 석전은 대교천을 사이에 두고 있는 동송읍 오덕리와 철원읍 화지리 사이에서 \n 정월 보름경에 행해졌던 놀이로, 이곳에서는 실용적인 수단으로 이용되고 있다. \n 예전부터 오덕리 사람은 강을 건너 화지리에 가서 땔감을 해야 했고, 화지리 사람은 \n 오덕리에 농토가 있어 해마다 대교천에 놓인 다리를 보수할  필요가 있어서 두 마을에서는\n 석전을 한 뒤 지는 쪽에서 그 돌들을 이용해 강의 다리를  손보았다 한다.<br>\n <br>동제 - 각 마을 단위로 산신당이나 서낭당이 있어 매년 제를 거행하였으나 지금은 갈말읍\n 문혜리의 가루개 서낭당을 비롯한 몇 개의 서낭당이 남아 있을 뿐이다. 관에서 주재하던 \n 김화의 서낭제가 유명하였다 하나 전승이 중단되었다.  김화서낭제의 특색은 제단이 고정되어 있는 것이 아니라 참나무로 임시 제단을 만들어 신위로 백마를 모신다는 것인데,  \n 제일은 백마신의 생일인 3월 17일이며 이때 간단한 굿을 하고 단오에 한 번 큰 굿을 거행한다.\n<br>\n 이밖에 삼부연의 기우제가 알려져 있는데, 이곳에서는 개를 잡아 그 피를 흘려서 기우제를 \n 지냈다 한다. 금학산과 용탕도 기우제를 지냈던 곳인데, 특히 금학산은 산에 서린 안개의 \n 상태로 일기를 점칠 수 있었다 한다.<br>', '127.3135708349', '38.1467816073', 'http://tong.visitkorea.or.kr/cms/resource/02/1138202_image2_1.jpg', '5000', '', '46973', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 1, 3, '가리왕산이야기 펜션', '32', NULL, '강원도 정선군 정선읍 청량길 32', '26119', '582215', '033-562-1665', '가리왕산 자연휴양림 입구에 자리한 가리왕산이야기 펜션은 가리왕산을 병풍처럼 두르고 2만여 평방미터의 대지에 조성되어 있다. 유럽식 고급 목조 주택으로 지어진 펜션동과 50여 년 이곳을 지켜온 고즈넉한 초가집을 개조해 만든 다실과 사장님께서 손수 황토로 지은 다실이 운영 중이다. 펜션동 본체는 총 네 개의 객실로 이루어져 있으며 깔끔한 인테리어와 사장님의 감성이 고스란히 담긴 예술 작품들로 꾸며져 가족과 연인들을 위한 공간으로 손색이 없다. 단체 이용객들을 위한 펜션동 별체는 복층으로 이루어져 있고 10여 명이 둘러앉고도 남을 널찍한 거실과 감각적인 인테리어의 주방이 눈에 띈다. 1층 침실과 2층 침실로 구성되어 있고 노래방 시설을 비롯하여 대형 TV 등이 마련되어 있다. 부대시설로는 황토로 잘 지은 황토방과 푸른 잔디밭에 멋지게 꾸며진 야외무대가 있다.', '128.5773415604', '37.4240753633', 'http://tong.visitkorea.or.kr/cms/resource/09/582509_image2_1.jpg', '90000', '', '137223', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 1, 4, '가진항 자매해녀횟집', '39', NULL, '강원도 고성군 죽왕면 가진해변길 123', '24744', '686401', '033-681-1213', '자연산 물회는 어부들이 지난날 숙취로 인한 속을 달래려고 새벽 출어를 나가기 전에 요기삼아 먹던 음식이다. 바다에서 갓 잡아 올린 자연산 가자미, 오징어, 해삼 등의 해산물과 각종 야채, 초고추장이 어우러져서 담백하고 신선한 맛을 느끼게 한다. <br>', '128.5098075871', '38.3674757297', 'http://tong.visitkorea.or.kr/cms/resource/58/689558_image2_1.jpg', '4000', '', '104904', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 1, 5, '가리산막국수', '39', NULL, '강원도 홍천군 두촌면 가리산길23번길 7', '25164', '608471', '033-435-2704', '※ 영업시간 10:00 ~ 21:00 (주말 07:00 ~ 21:00)\n\n가리산막국수는 넓은 주차장과 통나무집처럼 꾸려진 외관이 눈에 띄는 곳이다. 실내는 넓은 홀과 안쪽으로 같은 크기의 깨끗한 방으로 되어 있다. 막국수 전문점답게 새콤달콤한 양념과 신선한 야채가 곁들여져 있다.', '128.0138244871', '37.8381278478', 'http://tong.visitkorea.or.kr/cms/resource/30/610830_image2_1.jpg', '5000', '', '73119', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 2, 1, '광치막국수', '39', NULL, '강원도 양구군 남면 가오작리 1051-1', '24552', '626766', '033-481-4095', '※ 영업시간 11:00 ~ 19:30\n\n양구 광치 고개 근처에 자리 잡은 광치막국수는 강원도의 대표 음식인 막국수 등 청정지역의 맛을 느낄 수 있는 곳이다. 주인이 직접 채소 등 다양한 식재료를 재배하여 보다 신선함을 맛볼 수 있다. 또한 국토정중앙천문대가 인접해 있고 인제, 속초로 가는 길목에 위치하여 양구 관광은 물론 동해안의 관광까지 할 수 있는 여러 가지 장점을 갖춘 곳이기도 하다.', '128.0478514432', '38.1306666780', 'http://tong.visitkorea.or.kr/cms/resource/83/590883_image2_1.jpg', '3000', '', '32611', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 2, 2, '국향', '39', NULL, '강원도 정선군 정선읍 녹송로 10', '26125', '582714', '033-563-9967', '강원도 정선군 정선읍에 위치한 국향은 향토한방음식점으로 황토색 외벽과 독특한 외관에서도 그러한 느낌이 그대로 나타나 있다. 내부는 큰 홀은 없는 대신 정갈하고 깨끗한 여러개의 룸으로 되어있어 가족단위나 소규모 모임에 아주 좋다. 대표 메뉴는 곤드레 정식이며, 우리의 한방 약초를 이용한 메뉴가 많다. <br>', '128.6733842885', '37.3789490718', 'http://tong.visitkorea.or.kr/cms/resource/00/583000_image2_1.jpg', '6000', '', '100018', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 2, 3, '가리왕산이야기 펜션', '32', NULL, '강원도 정선군 정선읍 청량길 32', '26119', '582215', '033-562-1665', '가리왕산 자연휴양림 입구에 자리한 가리왕산이야기 펜션은 가리왕산을 병풍처럼 두르고 2만여 평방미터의 대지에 조성되어 있다. 유럽식 고급 목조 주택으로 지어진 펜션동과 50여 년 이곳을 지켜온 고즈넉한 초가집을 개조해 만든 다실과 사장님께서 손수 황토로 지은 다실이 운영 중이다. 펜션동 본체는 총 네 개의 객실로 이루어져 있으며 깔끔한 인테리어와 사장님의 감성이 고스란히 담긴 예술 작품들로 꾸며져 가족과 연인들을 위한 공간으로 손색이 없다. 단체 이용객들을 위한 펜션동 별체는 복층으로 이루어져 있고 10여 명이 둘러앉고도 남을 널찍한 거실과 감각적인 인테리어의 주방이 눈에 띈다. 1층 침실과 2층 침실로 구성되어 있고 노래방 시설을 비롯하여 대형 TV 등이 마련되어 있다. 부대시설로는 황토로 잘 지은 황토방과 푸른 잔디밭에 멋지게 꾸며진 야외무대가 있다.', '128.5773415604', '37.4240753633', 'http://tong.visitkorea.or.kr/cms/resource/09/582509_image2_1.jpg', '50000', '', '9872', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 2, 4, '㈜강원심층수', '12', NULL, '강원도 고성군 죽왕면 심층수길 124-19', '24747', '2360786', '033-638-9900', '<strong>믿고 마실 수 있는 깨끗한 물</strong><br />\n세계에서 해양심층수를 취수할 수 있는 나라는 미국, 일본, 노르웨이. 대만, 그리고 대한민국 5곳이며 특히 우리나라 동해는 세계 해양학계에서도 인정한 천혜의 심층수 해역으로 인정받고 있다. 해양심층수산업 선도업체로서 강원도 고성에 위치한 강원심층수는 동해 최북단 청정해역 수심 605m에서 취수한 해양심층수로 미네랄워터, 해양심층수소금, 김치 등 주요제품으로 다양한 상품과 서비스를 제공하고 있으며, 견학 프로그램을 운영중이다.<br>', '128.5216381039', '38.3343742648', 'http://tong.visitkorea.or.kr/cms/resource/75/2360775_image2_1.jpg', '0', '', '101180', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 2, 5, '가령폭포', '12', NULL, '강원 홍천군 내촌면 와야리', '250-863', '125658', '', '가령폭포는 자연속에 숨겨진 오지의 백암산(1,099m) 서남쪽 기슭에 숨어 있으며 개령폭포라고도 불리운다.   가령폭포는 최근 생태체험 등산 동호인들이 찾으면서 알려지기 시작한 백암산과 더불어  우렁찬 굉음을 토하며 수십미터(50여m)의 낭떠러지를 뒤흔들며 내려꽃는 자태가 웅장하다. 주위에는 수많은 종류의 산나물과 약초,  야생화가 자생하고 있어 산새들의 낙원이기도 하다. 해발 950m 어사리덕 작은 산골샘(약용샘물이 나오는 샘)에서 솟은 청정수가  400리 홍천강으로 발원하는 비레올 계곡의 무명담소와 가령 폭포가 시원함을 더해준다.  가령폭포는 숲에 가려 좀처럼 모습을 드러내지 않는 폭포로 알려져 있다.  폭포 주변에는 인적이 드물어, 아직도 깨끗한 폭포수와 자연스런 멋을 간직하고 있는 폭포 중의 하나이다.<br>', '128.1601919204', '37.8328951114', 'http://tong.visitkorea.or.kr/cms/resource/55/1842055_image2_1.jpg', '0', '', '64073', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 3, 1, '간현관광지', '12', NULL, '강원도 원주시 지정면 소금산길 26', '26359', '126644', '', '* 원주시에 있는 국민관광지, 간현관광지*<br /><br />원주시에서 서쪽으로 18km 떨어져 있는 간현관광지는 남한강 지류인 섬강과 삼산천이 합쳐지는 지점에 자리한 유원지이다. 강 양쪽에 40-50m 높이의 기암괴석들이 울창한 고목, 검푸른 강물과 조화를 이루며 강변에는 넓은 백사장이 펼쳐져 있어 경관이 빼어나다. 또한, 맑은 강물이 흐르고 백사장이 넓어 여름철이면 물놀이 피서객으로 붐빈다.<br /><br />* 간현관광지의 즐길거리 *<br /><br />관광 단지 맞은편의 간현암은 37개소의 바윗길이 생겨 암벽타기를 할 수 있다. 또한 수시로 변하는 유수의 흐름과 수심의 변화로 매우 위험하여\'삼산천 에서의 물놀이는 매우 각별한 주의가 필요하며, 하천에서의 야영,취사,모닥불 피우기, 낚시 등도 금지되어 있다. 간현관광지 내 소금산 등산로 입구에 있는 소금산 출렁다리는 왕복 1시간20분 정도가 걸리며 산악도보교 중 국내 최장, 최고의 규모(높이100m, 길이200m, 폭1.5m)를 자랑한다. 교랑바닥은 이용객들에게 짜릿함을 주기위한 스틸그레이팅으로 특수제작 했으며, 바닥을 내려보는 아찔함과 스릴을 온몸으로 느낄 수 있다.<br>', '127.8322604466', '37.3650792718', 'http://tong.visitkorea.or.kr/cms/resource/92/195092_image2_1.jpg', '0', '', '59458', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 3, 2, '갈천약수터', '12', NULL, '강원도 양양군 서면 구룡령로 1103-66', '25040', '127535', '033-670-2721', '갈천약수터는 양양군 갈천리 서쪽 약 11km 지점의 \n구룡령 계곡의 바위에서  솟아 나온다. 물에 철, 칼슘, 마그네슘, \n망간 등의 성분을 함유하고 있으며, 특히 철의 함량이 높아 위장병과 빈혈에 좋다고 \n한다.  오색약수와 비슷하고, 오래 전부터 진부의 방아다리 약수와 함께 \n명성이 높다.  56번 국도변에서 약 2㎞ 떨어져 있어 약 30분 정도 걸어서 \n들어가야 한다. 가을철 구룡령 단풍이 좋아서, 걸어갈 만하다.<br>', '128.5029086488', '37.9058931889', 'http://tong.visitkorea.or.kr/cms/resource/82/738182_image2_1.jpg', '0', '', '84314', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 3, 3, '가을동화펜션', '32', NULL, '강원도 평창군 봉평면 흥정계곡길 72', '25300', '2376711', '010-5213-3200', '가을동화펜션은 평창 흥정계곡입구에 위치한 펜션으로, 순수 황토 벽돌집과 통나무집 2동으로 구성되어 있다. 소음없는 안락한 환경, 순수한 천연 암반수, 맑은 공기 속에서 편안한 휴식을 즐길 수 있다.', '128.3519002081', '37.6272975840', 'http://tong.visitkorea.or.kr/cms/resource/93/2376693_image2_1.JPG', '60000', '', '33670', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 3, 4, '도암식당', '39', NULL, '강원도 평창군 대관령면 대관령로 103', '25344', '712505', '033-336-5814', '도암식당은 오징어불고기, 오삼불고기가 유명한 음식점으로, 신선한 국산 재료들을 고집한다. 여러 매체에 소개가 될 만큼 맛을 자랑하는 곳이다. <br>', '128.7080667110', '37.6725158468', 'http://tong.visitkorea.or.kr/cms/resource/83/2048583_image2_1.jpg', '0', '', '31836', '2019-09-23', '2019-09-23'),
	('PLAN20190900000002', 3, 5, '동광식당', '39', NULL, '강원도 정선군 정선읍 녹송1길 27', '26126', '631972', '033-563-3100', '아리랑의 고장 정선에서 황기, 오가피, 엄나무 등이 들어간 약초물에 삶아낸 족발을 맛볼 수 있는 곳이다. 또한 이 식당의 콧등치기는 집에서 직접 만든 된장으로 만든 육수에 직접 반죽한 메밀칼국수를 끓여 나오는데, 콧등치기의 구수한 된장국물이 일품이다.<br>', '128.6716009949', '37.3829485760', 'http://tong.visitkorea.or.kr/cms/resource/25/2363525_image2_1.jpg', '0', '', '32308', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 1, 1, '감포항', '12', NULL, '경북 경주시 감포읍', '780-830', '128677', '', '규모가 아기자기하면서도 멋진 등대가 우뚝 솟아있는 감포항은 드나드는 어선이 많은\n 동해남부의 중심 어항이다.  감포항 방파제는 동해 일출을 감상하기에 좋은 포인트로 \n 예전부터 널리 알려져 왔다.\n 감포항에는 바다를 전경으로 한 고유의 재래시장이 들어서 있으며 문무대왕릉에서 감포항으로 이어지는 해안도로에도 횟집과 민박집이 즐비하게 늘어서 있어  식도락가의 \n 발길을 붙잡는다.  3일과 8일에 장이 서는 감포장은 바다가 바로 인접해 새벽에 들어오는 오징어배와 멋진 일출이 어울릴 때면 가슴이 벅차오는 것을 느끼게 되는 곳이다.\n 도로를 따라 서는 시장은 경주를 비롯해  구룡포 등지에서 몰려든 상인들로 붐비며, \n 특히 포구가 바로 옆에 있어 갓 잡아온 싱싱한 생선류가 많은 것이 특징이다.', '129.5040374805', '35.8078329413', 'http://tong.visitkorea.or.kr/cms/resource/49/179449_image2_1.jpg', '3000', '', '0', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 1, 2, '경상북도 산림환경연구원', '12', NULL, '경상북도 경주시 통일로 367', '38174', '704151', '', '경주 남산자락에 위치한 경상북도 산림환경연구소에는 향토 희귀수목과 천연기념물, 야생화가 어우러진 아름다운 경관으로 도민의 휴식공간과 자연학습의 명소가 되고 있다. <br>', '129.2388605710', '35.8117358011', 'http://tong.visitkorea.or.kr/cms/resource/79/1891379_image2_1.jpg', '5000', '', '23984', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 1, 3, '경주 감은사지', '12', NULL, '경상북도 경주시 양북면 용당리', '38121', '128637', '', '감은사(感恩寺)는 신라 제30대 문무대왕이 삼국통일의 대업을 성취하고 난 후, 부처님의 힘으로 왜구의 침입을 막고자 이곳에 절을 세우다 완성하지 못하고 돌아가자 아들인 신문왕이 그 뜻을 좇아 즉위한 지 2년 되던 해인 682년에 완성한 신라시대의 사찰이었다. 문무대왕은 죽기 전  내가 죽으면 바다의 용이 되어 나라를 지키고자 하니 화장하여 동해에 장사지낼 것을 유언하였는데, 그 뜻을 받들어 장사한 곳이 바로 대왕암이며 부왕의 은혜에 감사하여 사찰을 완성하고 이름을 감은사라 하였다고 전하고 있다. 현재의 모습은 1979년부터 2년에 걸쳐 전면 발굴조사를 실시하여 얻어진 자료를 통해 창건  당시의 건물 기초대로 노출 정비한 것이며, 아울러 금당의 지하에는 바다용이 된 문무대왕의 휴식을 위한 상징적인 공간을 마련한 특수 구조와 동쪽으로 통로를 만들었다고 하는 흔적도 밝혀 놓은 것이다. 우뚝 솟은 두 삼층석탑은 만들어진 연대가  확실한  통일신라 초기의 석탑으로서 수십 개의 부분으로 나누어 만들어 조립식으로 세운 것으로 전체 높이 13.4m에 이르는 신라 삼층석탑 중 최대의 것이다.<br>', '129.4768554542', '35.7482196956', 'http://tong.visitkorea.or.kr/cms/resource/15/2371515_image2_1.jpg', '4000', '', '22655', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 1, 4, '경주 감포한옥펜션 (미동의)', '32', NULL, '경상북도 경주시 감포읍 감포로10길 40-11', '38125', '2570570', '010-7511-2688', '전통양식의 한옥에 내부는 현대적인 인테리어의 펜션이다.', '129.5008736523', '35.8153264806', 'http://tong.visitkorea.or.kr/cms/resource/63/2568463_image2_1.jpg', '50000', '1', '7761', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 1, 5, '석거돈', '39', NULL, '경상북도 경주시 외동읍 산업로 2838', '38210', '1827807', '054-746-6308', '[경북 경주시 외동읍에 있는 낙지전문점 석거돈]<br />외동읍에 있는 석거돈은 낙지전문점이다. 석거돈은 낙지+돼지고기를 뜻하며, 이곳의 메뉴는 석거돈과 낙지볶음 두가지 메뉴를 제공한다.<br>', '129.3133693071', '35.7636259685', 'http://tong.visitkorea.or.kr/cms/resource/27/1969327_image2_1.jpg', '12000', '', '17906', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 1, 6, '삼포쌈밥', '39', NULL, '경상북도 경주시 포석로1050번길 42', '38166', '686398', '054-749-5776', '※ 영업시간 11:00 ~ 20:00\n\n쌈 채소 외의 재료도 모두 경주 인근에서 직접 가져와 정성이 담겨있다. 특히, 쌈밥의 핵심이라고 할 수 있는 장맛과 젓갈의 맛이 일품이다. 쌈밥에 나오는 된장찌개는 밥과 알맞게 어울리고, 된장은 깊은 맛이 난다. 인근 감포 바닷가에서 잡아 올린 굵은 멸치젓갈도 진한 맛이 일품이다.', '129.2141204335', '35.8341661353', 'http://tong.visitkorea.or.kr/cms/resource/56/689556_image2_1.jpg', '8000', '', '11912', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 2, 1, '경주 석빙고', '12', NULL, '경상북도 경주시 인왕동', '38172', '126204', '', '일종의 천연냉장고로, 신라 지증왕 6년(505년)에 얼음을 저장했다는 기록에 의해 신라 때의 빙고로 알아왔으나, 현재의 석빙고는 조선 영조 14년(1738년)에 축조한것으로 밝혀졌다. 이 빙고는 입구가 월성 안쪽으로 나 있으며, 계단을 통해 내부로 들어가면 성의 경사를 따라 안이 깊어지며, 내부는 길이 12.27m, 폭 5.76m, 높이 5.21m 규모로 모두 가공된 석재로 되어 있고, 굴은 다섯개의 아치형 기둥 사이에 장대석이 걸쳐 있다. 이 장대석을 친 위치 세 군데에 외부와 통하는 환기공이 뚫려 있다. 바닥 중앙에는 배수로가 있어 경사를 따라 성 밖으로 흘러나가게 되어있고, 외부는 장대석위에 흙을 덮어 환기공만이 노출되어 있다. 보물 제66호로 지정되어 있다.<br>', '129.2239418227', '35.8332025414', 'http://tong.visitkorea.or.kr/cms/resource/89/2601689_image2_1.jpg', '3000', '', '894', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 2, 2, '경주 선덕여왕릉', '12', NULL, '경상북도 경주시 배반동', '38175', '126211', '', '선덕여왕릉은 둘레가 73m정도의 편이한 원형 봉토분이며, 자연석을 이용해 봉분 아래에 2단 보호석을 쌓은 것 외에는 별다른 장식이 없다. 선덕여왕은 아들이 없는 진평왕의 딸로 태어나 성골이라는 신라왕족의식에 의해 신라 최초의 여왕이 되었다. 재위 16년간 분황사와 첨성대를 세웠고, 특히 신라 최대의 황룡사 9층목탑을 세워 신라 불교건축의 금자탑을 이루기도 하였다. 또한, 뒷날 태종 무열왕이 된  김춘추와 명장 김유신 같은 영웅호걸을 거느리며, 신라가 삼국을 통일하는데 기초를 닦아 놓았다. 그러나 여성왕이라는 약점 때문에 왕권이 불안하여 재위 말년에 비담, 염종의 난이 일어났고, 이 과정에서 사망하였다.<br /><br />신라의 삼국통일과 관련하여 선덕여왕때 황룡사 구층탑, 분황사 등의 건립으로 그 통일의 뜻을 세웠다고 전해진다. 삼국유사는 선덕여왕이 죽을 날을 예언하며 도리천에 묻어 달라고 했다고 한다. 도리천은 불교설화의 수미산 밑을 이야기하는 데 어디를 말하는지 몰라 신하들이 묻자 낭산 기슭이라  대답하였고, 이 기슭에 여왕의 시체를 장사지낸 지 30여 년 만에 밑에 사천왕사가 창건되었고 사천왕이란 도리천의 호불신인데 무덤이 결국 도리천에 있는 셈이 되었다. 선덕여왕릉은 사적 제182호로 지정되어 있으며, 주변에 신문왕릉, 효공왕릉, 신무왕릉, 효소왕릉 등이 있다.<br>', '129.2329971093', '35.8147770141', 'http://tong.visitkorea.or.kr/cms/resource/19/179719_image2_1.jpg', '2000', '', '2203', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 2, 3, '위연재', '32', NULL, '경상북도 경주시 쪽샘길 7', '38164', '2570717', '010-9384-1000', '경주시 지정 한옥체험관이다. 호텔형 민박(객실별 욕실, 화장실)이다. 첨성대 도보 1분. 안압지, 대릉원과 천마총, 교촌마을, 계림, 월성, 월정교, 황리단길 5~7분 거리에 있다.', '129.2179867047', '35.8361576508', 'http://tong.visitkorea.or.kr/cms/resource/74/2568874_image2_1.JPG', '35000', '', '2734', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 2, 4, '[백년가게]숙영식당', '39', NULL, '경상북도 경주시 계림로 60', '38164', '2616265', '054-772-3369', '※ 영업시간 11:00 ~ 21:00\n\n[중소벤처기업부 2019년도 \'백년가게\'로 선정]\n경주 관광의 요지인 천마총과 첨성대 인근에 위치한 \'숙영식당\'이다. 황토색 외벽과 초가 형태의 건물이 토속적 분위기를 자아내는 한식당이다. 식사 메뉴는 두 가지로, 보리밥에 나물과 고추장을 넣고 비벼 먹는 2인 이상 주문 가능한 \'찰보리밥 정식\'과 같은 구성에 반찬 수를 몇 가지 추가해서 내어주는 1인을 위한 \'나홀로 정식\'이 있다. \'파전\'과 \'논고동 더덕무침\'은 안주 메뉴이지만 식사 메뉴에 추가해서 먹어도 훌륭하다. 찰보리밥과 집안 대대로 내려오는 비법으로 제조한 동동주는 식사 후에도 속이 불편하지 않은 깔끔한 맛으로 입소문 나있다. 건천지역 찰보리 및 찹쌀, 순두부 등을 사용하는 등 국내산 재료를 활용하고 있다.', '129.2138452987', '35.8397321690', 'http://tong.visitkorea.or.kr/cms/resource/22/2616322_image2_1.jpg', '12000', '', '546', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 2, 5, '황리단길김밥', '39', NULL, '경상북도 경주시 첨성로73번길 17', '38166', '2571614', '054-772-0888', '※ 영업시간 08:30 ~ 19:00\n\n경주에서 가장 핫플레이스 된 황리단길에 위치해 있으며 황리단길김밥은 경남 하동 최참판댁 앞 평사리 벌판에서 수확한 쌀로 밥을 지어 밥맛이 아주 뛰어나다, 또한 주인이 직접 농사지은 유기농 매실로 만든 장아찌를 곁들여 먹으면 더욱더 맛있는 김밥을 맛볼 수 있다.', '129.2103096451', '35.8345493538', 'http://tong.visitkorea.or.kr/cms/resource/04/2571604_image2_1.jpg', '6000', '', '658', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 2, 6, '[백년가게]외바우', '39', NULL, '경상북도 경주시 안강읍 구부랑3길 12', '38015', '2563238', '054-763-7733', '[중소벤처기업부 2018년도 \'백년가게\'로 선정] 초기 상호인 일성(一成, 정직하게 기본을 지킨다) 철학으로 2대째 가업을 이어오고 있다. 경주 양동마을 근처에 위치한 외바우는 한우와 한돈, 엄선된 국내산 식재료만을 이용해 건강한 요리를 제공한다. 담백한 맛의 \'버섯 한우 전골\'과 매콤한 맛의 철판 볶음 요리가 대표 메뉴. 대표자의 청결에 대한 고집스러운 노력으로 2017년 모범가게로 지정되었으며, \'경북으뜸음식점\', \'원산지표시우수업체\', \'경주모범음식점\' 지정 등 그 이력도 화려하다.<br>\n정보제공 : 소상공인협회 (Tel : 042-363-7981)<br>', '129.1955950654', '36.0011330290', 'http://tong.visitkorea.or.kr/cms/resource/38/2563338_image2_1.jpg', '9000', '', '18540', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 3, 1, '대명리조트 경주아쿠아월드', '12', NULL, '경상북도 경주시 보문로 402-12', '38117', '592417', '', '일상생활에서 벗어나 자연 안에서 휴식과 건강이 공존하는 아쿠아월드 경주는 다양한 아이템을 통한 흥미롭고 유쾌한 물놀이 공간을 연출한다. 사계절 내내 운영하는 테마 워터파크로 수(水)치료 개념을 도입한 비데풀, 웰빙 스파 시스템을 갖춘 실내 아쿠아존과 유수풀, 이벤트탕, 아쿠아 플레이존, 파도풀 시설을 갖춘 실외 아쿠아존으로 구성되어있다. 아쿠아월드 경주의 온천수에 몸을 담그면 몸에 온기가 전달되어 혈행이 좋아진다. 혈액순환이 되면서 체내에 있는 장기에 자극을 주어 신진대사가 잘 되어 건강을 돕는다.<br>', '129.2816363762', '35.8469298601', 'http://tong.visitkorea.or.kr/cms/resource/01/1748301_image2_1.jpg', '40000', '', '18799', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 3, 2, '마우나오션리조트 스파', '12', NULL, '경상북도 경주시 양남면 동남로 982', '38221', '127995', '', '마우나오션 리조트의 중심 위락지역인 엔터테인먼트플라자는 다양한 테마가 있는 쇼핑가, 레스토랑 등 온갖 위락시설이 즐비한 엔터테인먼트 스트리트와 다양한 슬라이드 시설 등 물놀이 시설이 구비된 물의 나라 워터파크 그리고 아웃도어레저(산책로, 등산로,)와 마우나오션 비치, 마우나오션 콘도미니엄과 마우나오션 컨트리클럽(18홀), 스파시설(아쿠아테라피) 등이 있다. 패밀리 스파 마우나 빌은 21세기 첨단형 신 휴양공간 패밀리 스파&바데풀로 영남최초의 고품격 스파시설의 다기능 바데풀과 최첨단수(水) 치료 장비를 갖춘 다양한 경락, 근육 마사지 등의 프로그램이 있다.<br /><br />* 산책로 <br /><br />산책로 거리 1.5km 소요시간 편도 25분거리 약 1.5km의 짧은 거리지만 가족,연인과 함께 해지는 오후 작은 담소를 나누며 양쪽으로 놓인 가로수와 왼쪽으로 산의 정취를 느낄 수 있으며, 오른쪽으로 마우나오션리조트 단지내의 아름다운 광경을 볼 수 있다.<br>', '129.3691861270', '35.6843665104', 'http://tong.visitkorea.or.kr/cms/resource/37/1885437_image2_1.jpg', '12000', '', '19709', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 3, 3, '망월사(경주)', '12', NULL, '경상북도 경주시 포석로 682-17', '38173', '304799', '', '망월사(望月寺)는 경주시 배동에 자리한 대한불교원효종 사찰이다. 망월사는 원효종의 중요한 사찰로 꼽힌다. 대한불교원효종은 우리 나라 18개 불교 종단 가운데 하나인데, 신라의 원효(元曉) 스님을 종조(宗祖)로 하여 1963년 경주에서 해인(海印) 스님이「대한불교원효종포교원」이라는 이름으로 창종하고 해인 스님이 초대 종정이 되었다. 그 뒤 1967년 8월 망월사가 총본원이 되었으며, 1977년 12월 대한불교원효종이 되었다. 현재 총무원은 서울 안양암(安養庵)에 있고, 주요 경전은 「원효종성전(元曉宗聖典)」인데 1967년 망월사에서 발간되었다. 현재 청운(晴雲) 스님이 주지로 주석하고 있다. 절 입구에는 망월사에서 관리 운영하는 전통찻집이 있다.<br>', '129.2104605767', '35.8013364389', 'http://tong.visitkorea.or.kr/cms/resource/11/1967511_image2_1.jpg', '5000', '', '19364', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 3, 4, '일성 경주보문콘도&amp;리조트', '32', NULL, '경상북도 경주시 보문로 365', '38116', '138363', '통합 1566-8113<br />경주 054-744-1199', '\'신라 천년의 숨결이 살아있는 예술의 휴양지\' 현대와 전통의 아름다운 조화 속에 고품격 레저를 선보이는 \'일성 경주보문콘도&리조트\' 는 신라 천년의 역사를 한눈에 볼 수 있는 다양한 관광지와 다채로운 행사로 휴양의 또 다른 묘미를 느낄 수 있다.', '129.2811558146', '35.8486157718', 'http://tong.visitkorea.or.kr/cms/resource/01/2378201_image2_1.jpg', '60000', '', '8270', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 3, 5, '계림식당', '39', NULL, '경상북도 경주시 불국로 289-17', '38126', '1963948', '054-740-5225', '경주 코오롱호텔 내에 위치한 한식 대중음식점으로, 가족끼리 풍성하게 즐길 수 있는 민속식당이다. 산지에서 직접 배달해온 싱싱한 재료로 음식을 제공한다.<br>', '129.3219123181', '35.7876956389', 'http://tong.visitkorea.or.kr/cms/resource/13/1963913_image2_1.jpg', '5000', '', '7702', '2019-09-23', '2019-09-23'),
	('PLAN20190900000003', 3, 6, '고구려가는길 (낙지랑 쭈꾸미랑)', '39', NULL, '경상북도 경주시 충효천길 66-6', '38058', '134161', '054-777-3389, 777-5589', ' 투박하지만 부드러운 황토 그 자체의 아름다움과 옛스러움을 그대로 간직하고 있는 외관의 음식점으로, 낙지요리와 쭈꾸미요리를 전문으로 하고 있다. <br>', '129.1914443958', '35.8428647259', 'http://tong.visitkorea.or.kr/cms/resource/42/1898042_image2_1.jpg', '6000', '', '13292', '2019-09-23', '2019-09-23'),
	('PLAN20190900000004', 1, 1, '국제시장 먹자골목', '12', NULL, '부산광역시 중구 중구로 36', '48945', '1018702', '', '부산시 창선동 국제시장 내 아리랑 거리 도입부에 위치하고 있는 대표적인 야외 음식점가로 60년이 넘는 역사를 자랑한다. 김밥, 순대, 국수 등을 좌판의자에 앉아서 즐기는 풍경은 한국 재래시장 식당가의 정감 넘치는 풍경 중 하나다. 이곳에 들른 내국인은 물론 외국 관광객들이 필수적으로 맛보는 대표 메뉴는 충무 김밥이다. 여러 가지재료를 쓰는 일반 김밥과 달리 밥과 김만으로 담백하게 맛을 낸 향토 음식이다.<br>', '129.0280793175', '35.1011311741', 'http://tong.visitkorea.or.kr/cms/resource/25/1020225_image2_1.jpg', '5000', '', '0', '2019-09-23', '2019-09-23'),
	('PLAN20190900000004', 1, 2, '국청사(부산)', '12', NULL, '부산광역시 금정구 북문로 42', '46237', '298099', '', '* 부산의 시작과 끝에 존재하는 사찰, 국청사 *<br /><br />금정산은 부산의 시작이며 끝이다. 그리 크지 않은 산세지만 곳곳에 울창한 숲과 골이 이루어져 있고 14개나 되는 약수터에서는 항시 맑은 물이 샘솟아 올라 부산시민들의 모태가 되는 산이기도 하다. 화강암의 풍화가 격렬했던 탓인지 곳곳에 기암절벽이 절묘하고, 크고 작은 금정의 봉우리들이 능선을 따라 연결된 곳에는 넓은 분지를 형성해 자리한 금정산성 마을이 있다. 산성마을은 나라를 지키기 위해 축조된 금정산성안 촌락으로 해발 8백10m의 산 정상에 서서 살펴보면 우리 조상들이 이곳에 왜 국내 최대규모의 산성을 쌓았는지 그 까닭을 알 수 있다. 서쪽으로 낙동강 하류 대저 땅과 북의 양산지계까지 내려다 보이고 동으로 수영강과 회동저수지, 온천천, 동래읍성지역 등이 손에 잡힐 듯 그려지는 탓에 왜구의 움직임을 소상히 알 수 있었으니 금정산성과 마을의 역사와 전설은 곧 호국의 이념으로 점철돼 있다해도 과언이 아니다. 바로 이 금정산성 안에 세 개의 신라고찰이 있는데 국청사, 미륵사, 정수암이 그것이며 모두 전통사찰로로 지정 보호되고 있다. 또 북문을 벗어나 3km 아래에는 범어사와 원효암을 비롯한 여러 부속암자가 자리하고 있으니 호국명산에 호국도량의 존재는 지극히 당연한 일일 것이다.<br /><br />* 국청사의 역사 *<br /><br />국청사는 신라시대 의상대사가 창건한 고찰로 조선 숙종 29년, 금정산성 축조와 함께 중수하여 이름을 국청사라 칭하고 순조 26년의 중건을 거쳐 오늘에 이르고 있다. 현판에 \'숙종 29년(1703년)금정산성 중성을 쌓은 후 적을 막고 지키어 나라를 보호하니 그 이름을 국청사라 칭한다.\'라고 기록되어 있는 것처럼 국난을 극복하는데 앞장서온 호국도량으로서 그 역사 또한 깊다. 무엇보다 이 사찰에는 당시의 승병장이 사용했던 `금정산성승장인(金井山城僧長印)\'이란 철제인이 보존되어 있는데 이것으로 승군의 사령부가 있었다는 것을 추측할 수 있을 뿐 아니라 `정발장군과 송상현부사, 윤흥신 장군, 승장 만홍(萬弘)스님, 정안(定安)스님, 성관(性寬)스님, 관찰(寬札)스님 등 등 수백명의 신위가 모셔져 제사를 봉행해 왔다\'는 국청사지(國淸寺誌)의 기록에 미루어 의승군이 주둔한 호국도량으로서 그리고 그분들의 천도제가 봉행되어 온 지장도량으로서 그 정통성을 이어온 사찰이라 할 수 있겠다.<br>', '129.0549922383', '35.2538895578', 'http://tong.visitkorea.or.kr/cms/resource/69/1570969_image2_1.jpg', '2000', '', '17131', '2019-09-23', '2019-09-23'),
	('PLAN20190900000004', 1, 3, '동래향교', '12', NULL, '부산광역시 동래구 동래로 103', '47748', '1607360', '', '1392년(태조 1) 지방에 향교를 지어 유생의 교육에 힘쓰라는 태조의 교서를 받들어 태조 때 와성(臥城, 지금의 동래고등학교 자리)에 설립되었으나 임진왜란 때 소실되었다. 1605년(선조 38)에 현유(賢儒)의 위패를 봉안, 배향하고 지방민의 교육과 교화를 위하여 중건되었다. 중건 당시에는 읍성(邑城) 동문(東門) 밖 지금의 동래고등학교 자리에 있었으나, 1705년(숙종 31)에 부(府) 동쪽 관노산(官奴山) 아래로 옮겼다가, 1745년(영조 21)에 동문 밖으로 다시 이건하였다. 그 뒤 1785년(정조 9)에 내곡동(內谷洞: 지금의 화성초등학교 자리)으로 이전하였으며, 1812년(순조 12)에 현재의 위치로 옮겼다. 현존하는 건물로는 대성전·명륜당·반화루(攀化樓)·내삼문(內三門)·외삼문(外三門)·동무(東廡)·서무(西廡)·동재(東齋)·서재(西齋)·좌우협문(左右夾門) 등이 있으며, 향교 밖에는 역대 부사들의 흥학비군(興學碑群)을 비롯하여 하마비(下馬碑) 등이 있다. 이 향교의 건물 배치는 문묘와 명륜당이 동서로 구분되어 있는 점이 특이하다. 좌측의 반화루를 들어서면 정면에 명륜당이 있고, 그 좌우 뜰에 동서 양재가 있으며, 우측의 외삼문을 들어서면 다시 내삼문이 있고, 이를지나 대성전과 동서 양무에 이르게 된다. 조선시대에는 국가에서 토지와 전적·노비 등을 지급받아 교관이 70인 정원의 교생을 가르쳤으나, 갑오개혁 이후 신학제 실시에 따라 교육 기능은 없어지고, 봄·가을에 석전(釋奠: 석전제, 공자에게 지내는 제사)을 봉행(奉行)하며 초하루와 보름에 분향을 하고 있다.<br>', '129.0841215255', '35.2077202717', 'http://tong.visitkorea.or.kr/cms/resource/41/1571141_image2_1.jpg', '1000', '', '5770', '2019-09-23', '2019-09-23'),
	('PLAN20190900000004', 1, 4, '동백공원', '12', NULL, '부산광역시 해운대구 우동', '48100', '128810', '', '* 울창한 동백나무와 바다의 경관을 즐기자, 동백공원 *<br>해운대해수욕장 남쪽 끝에 있는 동백섬을 공원으로  조성한 것으로, 1966년 9월 1일 공원대지로 지정되어 1990년대에 본격적으로 개발되어  현재에 이르고 있다. 동백섬은 동백나무와 소나무가 울창한 자연경관이 아름다운  곳으로 육지와 연결된 육계도이다.<br><br>2006년 부산에서 APEC이 개최되면서 해운대 환경이  새롭게 조성되고 산책길도 좋아져 연일 관광객의 발길이 끊이질 않는다. 산책로 외에  배드민턴장·의자·화장실 등의 편의시설이 조성되어 있다. 이중 화장실은  한국관광공사가 2001년 한국방문의 해 및 2002년 월드컵축구대회 등을 앞두고 화장실  문화개선을 위하여 벌인 전국 공중화장실 심사에서 가장 쾌적한 화장실로 선정되었다.<br><br>정상에는 신라말의 석학 고운 최치원의 동상과 비문이  있다. 순환도로 옆에 해운대석각(海雲臺石刻)이 있는데 이 일대가 해운대라고 불리게  한 유물로 최치원이 썼다고 전해진다. 신라 말기 학자 최치원이 난세를 피해 해인사로  들어가다 절경에 감탄하고 동백섬에 돌을 쌓아 대를 만든 후, 바다와 구름, 달과  산을 음미하면서 이 바위에 새겨 넣은 이름이 해운대(海雲臺)다.<br>* 동백공원을 알차게 이용하는 추천 산책로 *<br>동백공원은 산책로가 조성되어 있어, 바다의 경관을  즐기며 산책하기 좋다. 산책코스는 해운대 해변과 동백섬을 따라 도는 두 개가 있다.  해운대 해변 가로등을 따라서 잘 조성된 해변 산책로를 따라 걸어도 좋지만 운동도  할 겸 모래의 감촉을 느끼며 맨발로 걷는 것도 좋다.<br><br>동백섬 코스는 동이 트는 새벽 햇살을 받으며 바다  바위 위로 전망대를 따라 절경이 펼쳐진다. 달맞이 고개에서 이어지는 완만한 해운대  해변에서 광안대교의 시원한 모습으로 한 폭의 그림이 펼쳐진다. 최치원이 새겼다는  암석의 글을 음미해 보기도 하고, 때로는 누리마루 APEC하우스를 지나 몇 바퀴 속보로  걷는 것도 좋다.', '129.1521170882', '35.1539081129', 'http://tong.visitkorea.or.kr/cms/resource/56/566056_image2_1.jpg', '0', '', '8606', '2019-09-23', '2019-09-23'),
	('PLAN20190900000004', 1, 5, '두송반도 (부산 국가지질공원)', '12', NULL, '부산광역시 사하구 다대동', '49521', '2614718', '', '두송반도는 부산 사하구 다대포항 동쪽 해안에 위치하며, 공룡의 전성시대였던 백악기말의 부산지역 고환경을 보여주는 명소이다. 백악기말 경상분지 내에서 양산단층과 동래단층 사이에서 만들어진 다대포분지의 중부에 해당하는 곳으로 공룡알 둥지화석과 파편화석 등이 나타난다. 특히 과거 지진활동이 기록된 다양한 산출 상태의 쇄설성암맥과 고지진암이 선명히 관찰된다.또한 이곳에서 관찰되는 캘크리트 복합층은 백악기 당시의 고기후를 유추해 볼 수 있는 중요한 단서이며, 두송반도 일대에 나타나는 다양한 산상의 캘크리트는 국내에서 흔히 찾아볼 수 없어 높은 학술적 가치를 지니고 있다.<br>', '128.9862007564', '35.0595961635', 'http://tong.visitkorea.or.kr/cms/resource/17/2614717_image2_1.bmp', '3000', '', '18401', '2019-09-23', '2019-09-23'),
	('PLAN20190900000004', 1, 6, '가야할매 밀면', '39', NULL, '부산광역시 연제구 월드컵대로145번길 32', '47540', '849929', '051-865-8017', '가야할매 밀면은 연산동에 위치한 부산의 대표 음식 밀면을 맛볼 수 있는 곳이다.', '129.0798725132', '35.1851949389', 'http://tong.visitkorea.or.kr/cms/resource/15/1866815_image2_1.jpg', '6000', '', '16349', '2019-09-23', '2019-09-23'),
	('PLAN20190900000004', 1, 7, '간판없는횟집', '39', NULL, '부산광역시 수영구 광안해변로344번길 17-17', '48280', '2371880', '051-757-0038', '※ 영업시간 11:00 ~ 22:00\n\n부산시 수영구 광안리에 위치한 간판없는횟집은 대게와 랍스터를 싱싱하게 먹을 수 있는 곳이며, 관광지 내에 위치해 있어 다양한 이벤트를 진행하고 있다. 광안리 바다가 보이는 곳에서 식사를 즐길 수 있고, 남녀노소 불문 광안리에서 횟집으로 유명한 곳이다.', '129.1315660574', '35.1553631251', 'http://tong.visitkorea.or.kr/cms/resource/98/2371898_image2_1.jpg', '12000', '', '5759', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 1, 1, '거문오름 [세계자연유산]', '12', NULL, '제주특별자치도 제주시 조천읍 선교로 569-36', '63341', '636266', '', '화구 중심으로부터 유출된 용암류의 침식계곡은 도내 최대 규모로서 전방으로 유선형의 골짜기를 이루며 약 4km 정도 연속되어 나타난다. 오름 동남쪽의 목장지에는 화구 없는 화산체인 용암암설류의 원뿔꼴 언덕들이 집중 분포구좌읍 송당리에 있는 거미오름을 일명 동검은오름이라고 하는데, 이는 이오름(서검은오름)과 구별하기 위한 호칭이라고 한다. 지역주민들 사이에는 분화구의 별칭으로 거물창(거멀창) 이라고 불리기도 하고, 숲으로 덮여 검게 보인다 하여 검은오름이라 부르고 있으나, 학자들의 어원적 해석으로는 `검은`은 神이란 뜻의 고조선 시대의 ` ·감·검`에 뿌리를 두는 것으로 풀이하고 있다. 즉 `검은오름`은 `신령스런 산`이라는 뜻이다. 해송, 삼나무, 측백나무가 조림되어 있고, 울창한 숲을 이룬다.<br /><br />\n\n* 해발 456m의 복합형화산체인 거문오름으로부터 흘러나온 용암류가 지형경사를 따라 북동쪽의 방향으로 해안선까지 도달하면서 20여 개의 동굴 무리를 이루고 있는데, 용암동굴계 중에서 벵뒤굴, 만장굴, 김녕굴, 용천동굴, 그리고 당처물동굴은 세계자연유산으로 지정되었다.<br /><br />\n※ 복합형 화산체 - 산정부에는 커다란 화구가 깊게 패어 있고, 그 안에 자그만 봉우리가 솟아올라 있으면서 북동쪽으로 크게 터진 말굽형 화구 등으로 이루어진다.<br />\n* 표고 456.6m / 비고 112m / 둘레 4,551m  / 면적  809,860㎡  / 저경 1,188m\n<br>', '126.7147325126', '33.4569159249', 'http://tong.visitkorea.or.kr/cms/resource/89/656789_image2_1.jpg', '5000', '', '0', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 1, 2, '구엄어촌체험마을', '12', NULL, '제주특별자치도 제주시 애월읍 애월해안로', '63049', '129073', '', '320여 가구에 900여 명의 주민이 거주하고 있는 구엄마을은 제주시 서쪽 16km 지점에 있다. 해안도로 주변의 절경이 매우 뛰어나며 특히 구엄포구 부근에 선조들이 염전으로 사용하던  1,653m²(500여 평)의 평평한 천연 돌염전(빌레)은  조상들의 지혜를 엿볼 수 있는 곳이다.\n <br>', '126.3771834473', '33.4839519985', 'http://tong.visitkorea.or.kr/cms/resource/93/1618693_image2_1.jpg', '8000', '', '31520', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 1, 3, '금능으뜸원해변', '12', NULL, '제주특별자치도 제주시 한림읍 금능길', '63010', '591866', '', '* 2019 해수욕장 개장정보<br>- 기간: 6.24.~8.31.<br>- 시간: 10:00~19:00<br><br>투명한 맑은 물과 흰 모래와 조가비가 섞여 길게 이어진 아름다운 백사장, 낙조의 경관이 환상적인 이곳 앞바다에는 비양도가 있다. 얕고 새파란 바닷물 밑으로 비치는 새하얀 모래사장은 비양도까지 걸어서라도 갈 수 있을 것 같은 착각에 사로잡히게 한다.', '126.2353395628', '33.3898379598', 'http://tong.visitkorea.or.kr/cms/resource/28/1954528_image2_1.jpg', '2000', '', '16822', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 1, 4, '금호리조트 제주', '32', NULL, '제주특별자치도 서귀포시 남원읍 태위로 522-12', '63620', '138185', '064-766-8000', '금호리조트 제주는 남원관광지에 위치하고 있으며 초원과 해안절벽을 끼고 세워져 있다. 서귀포에서 동측방향으로 15km, 표선으로부터 서남방향으로 5km 지점에 위치하고 있어 서귀포 중문 관광단지와 제주민속촌, 성산일출봉 등 풍부한 주변관광지를 가지고 있다. 모든 객실에는 에어컨이 완비되어 있고 케이블/위성 채널, 냉장고 및 평면 TV도 제공하고 있다. 부대시설로는 편의점, 연회장, 특산품점, 아쿠아나(실내수영장) 등이 마련되어 있다.', '126.7028238197', '33.2737526275', 'http://tong.visitkorea.or.kr/cms/resource/38/1619938_image2_1.jpg', '50000', '', '45389', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 1, 5, '검은여닭(검은여쉼터)', '39', NULL, '제주특별자치도 서귀포시 검은여로 4', '63599', '2359165', '064-733-3030', '※ 영업시간 11:00 ~ 22:00\n\n제주특별자치도 서귀포시 토평동 검은여 해안 인근에 위치한 검은여닭(검은여쉼터)은 토종 닭도가니와 닭백숙 전문점이다. 올레길 코스에 위치하고 있어 간단하게 끼니를 때울 수 있는 다양한 메뉴를 판매하고 있다. 서귀포 칼호텔, 파라다이스 호텔이 인근에 위치해 있고, 바로 앞이 검은여 해안이기 때문에 관광객들이 즐겨 찾고 있다.', '126.5821627247', '33.2437595709', 'http://tong.visitkorea.or.kr/cms/resource/96/2046596_image2_1.jpg', '6000', '', '11725', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 1, 6, '고관심해장국', '39', NULL, '제주특별자치도 제주시 한경면 용고로 86', '63008', '404193', '064-772-2825', '고산리 시내 농협 사거리에 소박하게 위치한 관심해장국 식당은 여타 유명한 식당과는 달리 진한 해장국 국물 맛으로만 조용히 입소문이 난 식당이다. 이 곳은 쇠고기 선지 해장국이 주 메뉴이며 콩나물 해장국과 소 내장탕은 시골의 진한 국물 맛을 그대로 간직하고 있다.<br />쇠고기 선지 해장국은 뚝배기 한가득 아끼지 않고 담은 쇠고기와 선지의 양으로 보기만 하여도 푸짐하다. 콩나물 해장국 또한 진하고 시원한 국물 맛이 일품이며 콩나물 위에 살포시 얹혀지는 마른김은 더욱 입맛을 돋운다.<br>', '126.1795602716', '33.3011996689', 'http://tong.visitkorea.or.kr/cms/resource/42/401542_image2_1.jpg', '5000', '', '38047', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 2, 1, '만복해물탕', '39', NULL, '제주특별자치도 서귀포시 안덕면 산방로 53', '63530', '134993', '064-794-0013', '제주 산방산 가는길에 위치한 만복횟집은 주변 경관이 뛰어난 곳이다. 싱싱한 활어회와 다금바리, 갓돔, 광어 등의 다양한 메뉴를 선보이고 있으며, 자연산만을 취급하고 있다.<br>', '126.3269154157', '33.2436055602', 'http://tong.visitkorea.or.kr/cms/resource/26/1934526_image2_1.jpg', '13000', '', '15143', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 2, 2, '랜딩 라운지', '39', NULL, '제주특별자치도 서귀포시 안덕면 신화역사로304번길 38', '63522', '2561786', '064-908-2400', '※ 영업시간 11:00 ~ 22:00\n\n랜딩관 제주신화월드 로비 옆에 위치한 음악과 피아노 선율을 감상할 수 있는 라운지이다. 피아노 연주를 들으며 칵테일을 음미하는 여유, 제주신화월드 랜딩 리조트관 지하 1층에 위치한 랜딩 라운지에서는 부드러운 피아노 선율과 라이브 음악이 울린다. 오픈된 원형 구조의 카페바에서 음료나 디저트, 핑거푸드와 스낵류를 주문하면 널찍한 라운지에서 드실 수 있다. 가족과 휴식을 취할 때도 편하게 이용 가능하다. 비즈니스, 친교 등 다양한 목적의 만남을 가지거나 음악을 들으며 휴식을 취하기에 좋다.', '126.3164038528', '33.3049513971', 'http://tong.visitkorea.or.kr/cms/resource/02/2561902_image2_1.jpg', '6000', '', '6874', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 2, 3, '다희연', '12', NULL, '제주특별자치도 제주시 조천읍 선교로 117', '63341', '745449', '', '제주도 제주시 조천읍 선흘리 일대 6만 평 대지에 자리잡은 경덕원(炅德院)은 국내 최고 수준의 친환경 녹차를 생산하는 다원이다. 제주도는 연평균 온도가 섭씨 16도를 유지해야 하고, 뿌리가 쉽게 썩어 물이 잘 빠지는 녹차 재배의 최적의 조건을 갖추고 있다.에버그린루체 야외정원은 화산이 폭발하면서 흘러내린 용암에 의해 생성된 조그만 동굴들을 활용한 곳이다. 동굴연못과 동굴다리 그리고 제주 자생수와 야생화 등이 어우러져 있다. 이브홀은 동굴 천정이 뚫린 부분에 있다. 사방에 동공들이 뚫려 있는데 가장 긴 것은 30여 미터까지 된다. 여기에는 박쥐도 서식하고 있어 운이 좋으면 직접 볼 수도 있다.  이브홀은 터널같은 동굴을 통과하여야 들어갈 수 있다.경덕원 정상부(그린루체)와 입구(이브홀)에 각각 자리한 두 곳의 동굴 카페에서는 천연 녹차 발효빵, 녹차 쿠키, 녹차 머핀, 녹차라떼, 천연 녹차 발효즙 카테킹 등이 판매되고 있다. <br>', '126.7061797858', '33.4871596654', 'http://tong.visitkorea.or.kr/cms/resource/08/1620108_image2_1.jpg', '0', '', '41514', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 2, 4, '대발이파크', '12', NULL, '제주특별자치도 서귀포시 남원읍 태위로 536', '63620', '2606212', '', '대발이파크는 백악기 시대와 그 공룡들의 세계를 증강현실로 실감나게 구현한 제주도 소재 어드벤처 테마파크이다. 실내의 \'대발이연구소\'와 야외 \'공룡섬\'으로 분류되어 있으며, 실내외 복합형 가족 테마파크로써 공룡 캐릭터 대발이와 기타 공룡 조형물로 구성되어 있다.국내 최초 AR가이드를 이용해 파크 내 섹션 체험이 가능하며 짚투어, 정글짐, 국내 최장 고공 슬라이드 등이 있는 야외 공룡섬은 제주도 앞바다를 품은 해안절벽 위에 자리해, 웅장한 느낌의 자연경관을 자랑한다.<br>', '126.7052763696', '33.2744387019', 'http://tong.visitkorea.or.kr/cms/resource/84/2608484_image2_1.bmp', '0', '', '23594', '2019-09-23', '2019-09-23'),
	('PLAN20190900000005', 2, 5, '남원 큰엉해안', '12', NULL, '제주특별자치도 서귀포시 남원읍 태위로', '63620', '128795', '', '\'엉\'이란 제주도 방언으로 언덕을 뜻하는데, 남원 큰엉은 큰 바위가 바다를 집어 삼킬듯이 입을 크게 벌리고 있는 언덕이라 하여 붙여진 명칭으로 절벽 위는 평지로 부드러운 잔디가 깔려 있다. 바다를 향하여 높이 30m, 길이 200m의 기암절벽이 오랜 세월동안 쉬지 않고 밀려오는 파도를 감싸안은 2개의 자연동굴을 보노라면 세속을 떠난듯한 다른 세계, 때 묻지 않은 자연에 대한 감동을 느낄 수 있다. 해안절벽을 따라 2km의 산책길, 전망대, 간이휴게실, 기초체력단련시설, 화장실 등이 마련되어 있다. 갯바위 낚시터로, 조용한 휴식처로, 연인들의 데이트 코스로도 유명하다.<br>', '126.7037265276', '33.2733860157', 'http://tong.visitkorea.or.kr/cms/resource/68/725068_image2_1.jpg', '0', '', '186', '2019-09-23', '2019-09-23'),
	('PLAN20190900000007', 1, 1, '가우도', '12', NULL, '전라남도 강진군 도암면 월곶로 473', '59251', '2024432', '', '* 천혜의 관광자원이 풍부한 체험의 섬 *<br>강진군 도암면 망호(望湖)에 속한 강진만의 8개 섬 가운데 유일한 유인도인 가우도는, 강진읍 보은산이 소의 머리에 해당되고, 섬의 생김새가 소(牛)의 멍에에 해당 된다하여 ‘가우도(駕멍에가牛島)’라고 부르게 되었다고 전해 진다. 가우도는 사방으로 강진만과 무인도를 조망할 수 있으며 해안경관이 매우 우수하고 섬 내부에는 후박나무, 편백나무 군락지 및 곰솔 등 천혜의 관광 자원이 매우 풍부하다.<br><br>자동차가 다니지 않는 출렁다리가 양쪽으로 연결되어 걸어서 섬에 들어갈 수 있으며 섬에 도착하면 산과 바다를 감상하여 걸을 수 있는 2.5km의 생태탐방로가 조성되어있으며, 다양한 어종이 잡히는 복합낚시공원, 섬 정상에 있는 청자타워(높이25m)에서 출발하여 해상을 나는 체험을 할 수 있는 친환경 레저시설인 짚트랙(L=973m), 바다를 가르는 제트보트 등 다양한 레저 체험을 즐길 수 있다.', '126.7808236399', '34.5347608944', 'http://tong.visitkorea.or.kr/cms/resource/37/2483137_image2_1.jpg', '0', '', '0', '2019-09-23', '2019-09-23'),
	('PLAN20190900000007', 1, 2, '가우도 출렁다리', '12', NULL, '전라남도 강진군 도암면 신기리', '59251', '2514003', '', '가우도섬에 위치한 출렁다리는 해안선을 따라 약 3km가량의 산책로가 펼쳐져있고, 한폭의 그림같은 푸른 바닷길을 볼 수 있다.<br>', '126.7717356912', '34.5288669609', 'http://tong.visitkorea.or.kr/cms/resource/37/2483137_image2_1.jpg', '0', '', '1060', '2019-09-23', '2019-09-23'),
	('PLAN20190900000007', 1, 3, '별바라기집', '32', NULL, '전라남도 강진군 성전면 달빛한옥길 46', '59200', '2572908', '010-4623-1375', '예쁜 돌담 담장 너머 아름다운 한옥민박집이다. 마당엔 잔디밭이 깔려 있고 멋진 정자가 있어 즐겁게 힐링할 수 있는 공간이 있다. 정자에서 바라다 보면 한눈에 월출산이 보이고 수려한 녹차밭을 내려다볼수 있다.', '126.7177575224', '34.7408006097', 'http://tong.visitkorea.or.kr/cms/resource/52/2571852_image2_1.jpg', '60000', '', '24026', '2019-09-23', '2019-09-23'),
	('PLAN20190900000007', 1, 4, '해태식당', '39', NULL, '전라남도 강진군 강진읍 서성안길 6', '59235', '133479', '061-434-2486', '강진만의 청정해역에서 생산된 각종 어패류에다 채소, 육류 등을 고루 갖춘 전통 한정식집으로 돔배젓, 토하젓 같은 토산젓갈 뿐만 아니라 깻잎무침 하나도 정성이 깃들여 있다. 생선회와 찌개는 철마다 메뉴가 달라지며 무엇보다 깔끔하고 푸짐하다.<br>', '126.7672445245', '34.6392406167', 'http://tong.visitkorea.or.kr/cms/resource/10/1974310_image2_1.jpg', '6000', '', '12145', '2019-09-23', '2019-09-23'),
	('PLAN20190900000007', 1, 5, '청자골종가집', '39', NULL, '전라남도 강진군 군동면 종합운동장길 106-11', '59223', '404070', '061-433-1100', '※ 영업시간 11:00 ~ 21:00\n\n강진의 청자골종가집은 남도 최고의 한정식집을 만들겠다는 취지에 맞게 음식 맛은 물론이고 전통한옥으로 된 고풍스러운 건물, 수려한 주변 경관, 넓은 주차공간까지 명가의 호칭이 오히려 부족할 정도이다. 건물을 신축하는 데만 4년이 걸렸다는 고풍스러운 전통한옥에 앉아 잘 꾸며진 정원을 바라보며 큰 상을 받으니 마치 사대부 양반이 된 것 같은 기분이 들어 절로 마음이 흐뭇해진다. 돼지고기 편육, 산낙지, 장어구이, 참숭어알, 홍어찜, 굴전, 매생이국 등 각종 요리에 강진의 갯내음을 더하는 돈배, 토하, 밴댕이, 바지락 젓갈, 그리고 매실과 무장아찌 등 맛깔진 음식들이 강진 한정식의 정수를 보여준다.', '126.7839095318', '34.6472205531', 'http://tong.visitkorea.or.kr/cms/resource/11/401311_image2_1.jpg', '8000', '', '1766', '2019-09-23', '2019-09-23'),
	('PLAN20190900000007', 2, 1, '무위사', '12', NULL, '전라남도 강진군 성전면 무위사로 308', '59201', '126357', '', '무위사 사적을 보면 875년(헌강왕 1) 도선(道詵)국사가 갈옥사(葛屋寺)로 창건한 것이 첫 번째 중창이다. 그리고 이로부터 얼마 지나지 않은 905년(효공왕 9) 이후 선종인 가지산문(迦智山門) 계통의 선각국사(先覺國師) 형미(逈微, 864∼917)가 고려 태조 왕건의 요청으로 무위갑사(無爲岬寺)에 머무르면서 절을 중수하고 널리 교화를 펴 대중적 지지를 받았다고 한다. 따라서 무위사는 형미 스님이 주석했던 10세기 초 이전에 무위갑사라는 절로 창건되었음을 알 수 있다. 그런데 무위사 사적에 의하면 고려 시대인 946년(정종 1)에 형미가 제3창을 하면서 모옥사(茅屋寺)로 절 이름을 바꾸었다고 한다. 그러나 946년은 이미 형미가 입적한 30년 뒤이며, 최언위가 지은 \'고려국고무위갑사선각대사편광영탑비\'가 세워진 해라서 믿기 어렵다. 이것은 아마 형미가 무위갑사를 왕건의 후원을 받아 크게 중창하면서 교화를 펼쳤던 사실을 후세 사람들이 잘못 이해한 것으로 보인다. 어쨌든 무위사는 10세기 초 이전에 창건되었고, 형미에 의해 중창되었으며 가지산문 소속의 선종 사찰이었음은 분명하다.<br>', '126.6847659578', '34.7418943841', 'http://tong.visitkorea.or.kr/cms/resource/96/2550996_image2_1.bmp', '7000', '', '13886', '2019-09-23', '2019-09-23'),
	('PLAN20190900000007', 2, 2, '백련사(강진)', '12', NULL, '전라남도 강진군 도암면 백련사길 145', '59248', '127285', '', '백련사의 원래 이름은 만덕사로 신라 문성왕때 무염국사가 창건했다고 전해온다. 그 뒤 고려 희종 7년 원묘국사 요세스님이 옛터에 중창하고 백련결사로 크게 이름을 날려 백련사로 불리우게 되었다. 원묘국사의 백련결사는 120년간 고려 8분의 국사(원묘국사, 정명국사, 원환국사, 진정국사, 원조국사, 원혜국사, 진감국사, 목암국사)를 배출하고 번청하였다.이후 다산 정약용 선생이 강진으로 유배를 왔을때 아암 혜장선사와 종교, 나이를 뛰어넘은 소통과 교류를 했던 공간으로 잘 알려진 곳이기도 하다. 백련사의 자랑은 무엇보다도 천연기념물 제 151호로 지정된 동백나무숲이다. 사적비 옆 허물어진 행호토성 너머에 아름드리 동백나무들이 숲을 이루고 서 있는데 3,000여 평에 달하는 숲 안에는 고려, 조선시대의 부도 네기가 숨바꼭질하듯 흩어져 있다. 숲속은 사시사철 푸르고 두터운 잎으로 인해 대낮에도 고즈넉한 분위기를 형성한다. 11월부터 동백꽃이 피어서 만개하면 고즈넉한 숲은 붉게 물들어 감동을 안겨준다. 이 동백나무숲을 지나 다산초당 가는 산책길에는 백련사에서 재배하는 차밭과 야생차가 군락을 이루고 있다. 백련사가 있던 산은 고려시대 때부터 자생해온 이 야생차밭이 있어서 \'다산\'이라고 불렸다고 한다. 때문에 정약용이 이곳에 유배와 지냈다는 의미로 \'다산\'이라는 호를 지어 대표적으로 사용했다. 또한 이곳은 아암 혜장스님과 다산 정약용 선생이 서로 유학과 불교를 주고 받으며 아름다운 인연을 맺었던 곳이다.<br /><br />* 여행정보<br /><br />다산초당으로 이어지는 2.5km의 오솔길은 꼭 30여분 정도 부담없는 트레킹 코스이다. 또한 9,917m²(3000여 평)의 산비탈에는 수령 3~400년이 넘는 동백나무 1,500여 그루가 있어, 3월 말이 되면 화려한 동백 천하를 만날 수 있다.백련사에는 선다원이라는 찻집이 있어 여유롭게 차 한잔할 수 있다.백련사와 다산초당, 다산 유물 전시관은 숲길로 서로 연결되어 있다. 그런데 백련사와 다산초당의 출입로가 다르기 때문에 차를 주차하고 만덕산 숲길을 이용하여 다산초당 입구로 이동하는 것이 수월하다. 답사 순서를 정할 때 세 곳 중 주차하기 가장 편리한백련사를 먼저 관람한 후 차량으로 다산 유물 전시관으로 이도하고 숲길을 따라 다산초당을 관람하는 것도 괜찮다. <br>', '126.7482502188', '34.5878099623', 'http://tong.visitkorea.or.kr/cms/resource/25/2434125_image2_1.jpg', '5000', '', '18057', '2019-09-23', '2019-09-23'),
	('PLAN20190900000007', 2, 3, '별유풍경', '32', NULL, '전라남도 강진군 성전면 달빛한옥길 32', '59200', '2570339', '010-2038-7755', '강진한옥 별유풍경은 현대식 한옥으로 목재와 황토, 토기와로 건축했다. 소나무향과 편백나무향이 가득하며 침구는 깨끗하고 청경하여 대도시의 피로를 풀수 있으며, 전라도의 풍미를 느낄 수 있는 맛있는 아침 밥상이 제공된다. \n남쪽에 위치한 강진달빛한옥마을은 경관이 아름답고 주변에 월출산국립공원, 설록다원이 있다.', '126.7190018729', '34.7401645907', 'http://tong.visitkorea.or.kr/cms/resource/62/2568162_image2_1.jpg', '45000', '', '17113', '2019-09-23', '2019-09-23'),
	('PLAN20190900000007', 2, 4, '명동식당', '39', NULL, '전라남도 강진군 강진읍 서성안길 5', '59235', '133734', '061-433-2147', '강진에는 유달리 한정식집이 많다. 어느곳을 가야하나 결정하는 것도 강진여행의 행복한 고민. 그럴 땐 택시에 올라타서 기사아저씨에게 물어보자. ', '126.7673445240', '34.6389073225', 'http://tong.visitkorea.or.kr/cms/resource/97/1973397_image2_1.jpg', '0', '', '12075', '2019-09-23', '2019-09-23'),
	('PLAN20190900000007', 2, 5, '설성식당', '39', NULL, '전라남도 강진군 병영면 삼인리 334-14', '59214', '2371885', '061-433-1282', '※ 영업시간 11:00 ~ 19:00 (브레이크타임 15:00 ~ 17:00)\n\n조선 호국 역사유적지인 전라병영성이 있는 전라남도 강진군 병영면에 위치한 음식점이다. 질 좋은 돼지고기에 양념을 해 연탄불에 구워내는 불고기와 푸짐하게 차려지는 한정식을 맛볼 수 있다.', '126.8165772757', '34.7130998091', 'http://tong.visitkorea.or.kr/cms/resource/32/2371932_image2_1.jpg', '0', '', '9386', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 1, 1, '광안리해수욕장', '12', NULL, '부산광역시 수영구 광안해변로 219', '48303', '126078', '', '* 2019 해수욕장 개장정보<br>- 기간: 7.1.~8.31.<br>- 시간: 09:00~18:00<br>  - 성수기 7.20~8.6(18일) 30분 연장<br><br>* 남태평양 코발트빛 해변을 닮은 도심 속 해변, 광안리해수욕장 *<br>광안리해수욕장은 부산광역시 수영구 광안2동에 있으며 해운대 해수욕장의 서쪽에 위치하고 있다. 총면적 82,000㎡, 길이 1.4km, 사장폭은 25~110m의 질 좋은 모래사장이 있고, 지속적인 수질 정화를 실시하여 인근의 수영강에 다시 고기가 살 수 있을 정도로 깨끗한 수질을 자랑하며, 특히 젊은이들이 즐겨 찾는 명소이다. 광안리에서는 해수욕뿐 아니라 독특한 분위기를 자아내는 레스토랑, 카페 등과 시내 중심가 못지않은 유명 패션상가들이 즐비하며, 다양한 먹을거리, 볼거리가 있어서 피서의 즐거움을 더해준다. 특히 밤이 되면 광안대교의 아름다운 야경이 장관이다.<br><br>해수욕장 주변에는 낭만이 깃든 카페거리와 300여 곳의 횟집이 있고 야외무대가 설치되어 있어서 부산 바다축제를 비롯한 다양한 축제가 개최되고 있으며, 해변을 찾는 피서객을 위한 공연도 있다. 인근의 수영강에서는 낚시를 할 수도 있고, 싱싱한 회를 즉석에서 맛볼 수도 있으며 올림픽 요트 경기장이 있어서 요트를 탈 수도 있다. 숙박시설도 잘 갖추어져 있다. 해변과 인접해 있는 호텔을 이용해도 되고 알뜰한 피서를 원한다면 인근 금련산에 소재한 청소년수련원를 이용하면 된다. 이곳에는 텐트 설치가 가능하며 숙박동도 대여해 주고 취사시설도 완비되어 있다. 해수욕장 인근에는 다양한 문화시설들이 있는데 남천해변의 자유바다를 비롯하여 KBS, MBC 방송국이 있으며, MBC 내에는 개봉관인 시네마홀 극장도 있다. 피서철에는 다양한 축제가 열리므로 피서객들에게 즐길 수 있는 문화공간도 제공한다.<br><br>* 광안리해변 카페거리 *<br>광안리해변에는 100여 개의 카페가 있다. 음악과 칵테일과 낭만이 깃든 카페에서 바라보는 해수욕장과 광안대교는 아름답기 그지없다. 광안대교에서 이곳을 바라보면 마치 동화 속 유럽의 한 도시를 여행하고 있는 듯한 착각을 할 만큼 예쁘게 꾸며져 있다. 광안리 해수욕장과 인접해 있어 가족단위나 친구·연인과의 만남을 위한 장소이기도 하다. 또한 이곳에서는 음식과 술뿐만 아니라 야외음악도 감상할 수 있다.', '129.1184922375', '35.1537908369', 'http://tong.visitkorea.or.kr/cms/resource/72/2612872_image2_1.jpg', '0', '', '0', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 1, 2, '광복로문화패션거리', '12', NULL, '부산광역시 중구 광복동', '48953', '126857', '', '현재 사람들과 차량이 내왕하는 광복로 도로는원래 하천이었다. 복개되기 전, 송현천(松峴川 솔재내)이란 이름을 지니고 있었다. 1600년대까지는, 용두산과 복병산에서 내린 맑은 물이 이 하천을 거쳐, 용미산 우측, 즉 영도대교 입구 쯤에서 초량항 바다로 들어 갔다. 조선후기 초량왜관 시대에, 왜관 내의 하천이 되었고, 1876년 개항 후에 일본인들이 살게 되면서, 하천변에 벚나무를 심어, 사쿠라 가와, 즉 앵천(櫻川)으로 불리다가 초량왜관이 일본인들의 공식 전관거류지가 되고, 거주자가 만명 이상으로 늘자, 앵천이 불결해지고 악취가 나서, 복개 필요성이 거론되었다. 이후 앵천을 복개하여 오늘의 광복로 도로가 생겨 났으며 광복로는, 일제강점기 때 장수통(長手通), 또는 변천정(辯天町)으로 불리었는데, 거리 양쪽에 2층 높이의 상설점포들이 쭉 들어서는 번화가가 되었다.<br>', '129.0312902050', '35.0991036826', 'http://tong.visitkorea.or.kr/cms/resource/02/1804802_image2_1.jpg', '100000', '', '10004', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 1, 3, '더베이101', '12', NULL, '부산광역시 해운대구 동백로 52', '48100', '2350092', '', '아름다운 바다와 산 그리고 현대적인 건물들이 공존하는 세계적인 휴양지, 해운대. 문화와 예술을 위한 공간, 바다가 주는 흥분을 조금 더 느낄 수 있는 공간, 누구나 머물 수 있는 재미있는 공간.더베이는 해운대에 위치한 복합문화예술공간으로, 많은 관광객이 야경명소로 찾는 곳이다.', '129.1521197895', '35.1566855796', 'http://tong.visitkorea.or.kr/cms/resource/64/1972564_image2_1.jpg', '50000', '', '12738', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 1, 4, '감나무집', '39', NULL, '부산광역시 금정구 금성동 889', '46238', '849941', '051-517-1378', '금정산 400m 고지에 자리잡은 감나무집은 흑염소불고기와 오리, 토종닭요리를 전문으로 하고 있다.<br>', '129.0562841621', '35.2404327572', 'http://tong.visitkorea.or.kr/cms/resource/30/1982630_image2_1.jpg', '15000', '', '12753', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 1, 5, '갈비곳간', '39', NULL, '부산광역시 부산진구 서면문화로 32', '47256', '2607508', '051-714-4457', '※ 영업시간 11:30 ~ 24:00 / (11:30 ~ 22:00 일요일, 공휴일)\n※ 포장 시 10% 할인\n\n건강한 외식문화를 지향하는 프리미엄 수제갈비라는 컨셉의 갈비곳간은 최상 품질의 소고기 구이(KOREA BBQ / 焼き肉) 전문점이다. 모던한 인테리어로 라이트한 회식문화를 원하는 직장인과 가족 또는 연인끼리 외식과 데이트를 하고 싶은 공간이다.\n\n낮에는 가심비 있는 수제덮밥을 한정으로 즐길 수 있고, 저녁에는 웻에이징 안심, 왕양념갈비·왕생갈비 등의 최상의 소고기 구이로 특별한 외식을 선물하는 곳이다. 매장 내부에는 살아있는 생화로 포인트를 줘 매장 내 습도조절, 공기정화, 미세먼지 케어까지 가능하며 단체행사나 모임장소를 위한 홀도 준비되어 있다.', '129.0563669161', '35.1599862176', 'http://tong.visitkorea.or.kr/cms/resource/11/2607511_image2_1.jpg', '20000', '', '8929', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 1, 6, '가야할매 밀면', '39', NULL, '부산광역시 연제구 월드컵대로145번길 32', '47540', '849929', '051-865-8017', '가야할매 밀면은 연산동에 위치한 부산의 대표 음식 밀면을 맛볼 수 있는 곳이다.', '129.0798725132', '35.1851949389', 'http://tong.visitkorea.or.kr/cms/resource/15/1866815_image2_1.jpg', '6000', '', '3524', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 1, 7, '롯데호텔 부산', '32', NULL, '부산광역시 부산진구 가야대로 772', '47285', '142998', '051-810-1000', '\'한국 근대화와 관광산업에 이바지하고 싶다’는 정신과 서울의 발전을 상징할 만한 수준의 규모와 시설을 갖춘 호텔을 세운다는 것을 목표로 1973년에 지어진 롯데호텔은 서울을 시작으로 현재 서울점, 월드점, 울산점, 제주점, 부산점 등 5개 체인을 보유한 국내 최대, 최고 수준의 특급 체인호텔이다.\n롯데호텔은 국내를 넘어 글로벌 리딩 호텔로 성장한다는 장기적 비전 아래 지속적으로 변화와 혁신을 추구하며 모든 부분에 걸친 ‘Quality-Up’을 위해 롯데호텔 전 직원이 지켜나가는 롯데호텔만의 정신이 있다. 고객만족 극대화를 바탕으로 처음 정신을 이어나가는 변함없는 모습으로 세계 최고 수준의 비즈니스호텔이 될 것이다.\n부산롯데호텔은 객실, 레스토랑과 바, 8개 국어 동시통역 시설을 완비한 2,500명 수용 규모의 크리스탈볼룸 등 국제회의장과 예식장, 면세점, 호랑이가 있는 커피숍 등 다양한 볼거리와 부대시설을 갖춘 초대형 비즈니스 및 엔터테인먼트 호텔이다. 그리고 감각적인 라이프스타일을 추구하는 고객님을 위한 최고의 미각을 선사하는 오트 퀴진, 웰빙라이프를 위한 고품격 피트니스 클럽과 국내 최대 면세점이 함께 하는 곳이다.\n부산롯데호텔은 세련되고 모던한 감각, 편안하고 안락한 객실과 세심한 배려가 묻어나는 서비스가 있으며 도심 속의 편안한 휴식 공간, 비즈니스를 위한 최상의 파트너, 끊임없는 리노베이션과 서비스 개선을 통해 고객의 휴식이 편할 수 있도록 항상 최선을 다해 노력하고 있다.', '129.0541782189', '35.1599500699', 'http://tong.visitkorea.or.kr/cms/resource/86/1346286_image2_1.jpg', '200000', '', '3652', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 2, 1, '남도해양관광열차 S-트레인(S-train)', '12', NULL, '부산광역시 동구 중앙대로 206', '48760', '1846830', '', '영남과 호남을 연결하는 남도해양관광열차S-train의 ‘S’는 영문 ‘Sea(바다)’의 이니셜과 곡선 모양의 경전선, 리아스식 해안인 남해안 모양을 형상화했다. 남도해양권을 즐길 수 있는 해양관광열차로 열차의 외관은 한반도 남해안을 지키던 이순신 장군의 거북선을 본따 만들었다. 5량의 객차로 1호차는 힐링실, 2호차는 가족실, 그리고 카페실과 커플실로 채워진 3호차와 다례를 체험할 수 있는 4호차가 있다. 마지막으로 레포츠실과 이벤트실을 겸한 5호차로 구성되어 있다.<br>', '129.0424332391', '35.1152715223', 'http://tong.visitkorea.or.kr/cms/resource/02/1847102_image2_1.jpg', '10000', '', '5073', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 2, 2, '두송반도 (부산 국가지질공원)', '12', NULL, '부산광역시 사하구 다대동', '49521', '2614718', '', '두송반도는 부산 사하구 다대포항 동쪽 해안에 위치하며, 공룡의 전성시대였던 백악기말의 부산지역 고환경을 보여주는 명소이다. 백악기말 경상분지 내에서 양산단층과 동래단층 사이에서 만들어진 다대포분지의 중부에 해당하는 곳으로 공룡알 둥지화석과 파편화석 등이 나타난다. 특히 과거 지진활동이 기록된 다양한 산출 상태의 쇄설성암맥과 고지진암이 선명히 관찰된다.또한 이곳에서 관찰되는 캘크리트 복합층은 백악기 당시의 고기후를 유추해 볼 수 있는 중요한 단서이며, 두송반도 일대에 나타나는 다양한 산상의 캘크리트는 국내에서 흔히 찾아볼 수 없어 높은 학술적 가치를 지니고 있다.<br>', '128.9862007564', '35.0595961635', 'http://tong.visitkorea.or.kr/cms/resource/17/2614717_image2_1.bmp', '0', '', '8031', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 2, 3, '다대포해수욕장', '12', NULL, '부산광역시 사하구 몰운대1길 14', '49527', '126079', '051-207-6041', '* 2019 해수욕장 개장정보 *<br>- 기간: 7.1.~8.31.<br>- 시간: 09:00~18:00<br><br>* 완만한 수심과 모래사장으로 가족단위 피서객이 많이 찾는 곳, 다대포해수욕장 *<br>부산시내에서 서남쪽으로 약 8km 거리에 있는 해수욕장이다. 백사장 길이 900m, 폭 100m에 평균수온 21.6℃의 조건을 갖추었다. 낙동강 상류에서 실려온 양질의 모래밭과 완만한 경사, 얕은 수심과 따뜻한 수온으로 널리 알려진 피서지이다. 민박이나 야영이 안 되는 곳으로 주변에 숙박과 식당 등의 편의시설이 갖추어져 있다. 해안에서 300m 거리의 바다까지도 수심이 1.5m 안팎이어서 가족단위의 피서지로 적합하다. 1987년 낙동강 하굿둑이 준공되면서 강물의 유입이 막혀 해수욕장의 기능이 예전만 못해졌기 때문에 하굿둑에서 낚시를 하거나 간조 때 해수욕장 모래밭에서 소라나 게를 잡으려는 관광객이 더 많이 찾는다.<br><br>* 다대포해수욕장의 또다른 볼거리, 다대포 낙조분수 *<br>‘다대포 꿈의 낙조분수’는 다대포해수욕장 입구 2519m²의 광장에 분수 원형지름 60m, 최대 물높이 55m, 물 분사 노즐수 1046개, 조명 511개, 소분수 24개 등 세계 최대 규모를 갖추고 있다. 세계의 유명 분수들이 대부분 호수 안 또는 벽면에 설치됐지만 이처럼 바닥분수로 음악과 조명이 어우러진 장관을 연출하는 경우는 드물기 때문에 다대포의 낙조분수는 이미 한국기록원으로부터 국내 최대 규모의 분수대로 정식인증을 받았다. 이제는 세계 기네스북 등재를 신청해놓은 상태이다. 매년 3월부터 11월까지 운영되는 다대포 낙조분수는 이곳을 찾은 사람들이 직접 참여하여 좀 더 다양하게 즐길 수 있는 계절별, 테마별 다양한 프로그램을 마련해놓았다. 물론 화려한 장관을 연출하는 분수 쇼를 보는 것만으로도 좋은 볼거리가 되지만, 많은 사람들과 함께 즐길 수 있는 프로그램을 통해 분수 쇼와 더불어 좋은 추억거리가 하나 더 생기는 셈이다.', '128.9680526796', '35.0468750311', 'http://tong.visitkorea.or.kr/cms/resource/43/2605543_image2_1.jpg', '0', '', '2176', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 2, 4, '구포집', '39', NULL, '부산광역시 중구 보수대로36번길 14-1', '48976', '133227', '051-244-2146', '중구 부평동에 위치한 구포집은 부산을 대표하는 경상도식 추어탕집으로 1959년 개업하여 오랫동안 한자리를 지켜온 집이다. 진한 추어탕 국물에 고사리를 넣어 추어탕의 구수한 맛이 더욱 발한다. 된장을 풀어 넣어 수수한 맛이 나는 추어탕에 우거지, 콩나물 등을 넣어 시원한 맛을 더했다. 야채 종류들을 다양하게 넣어 국물은 거친 감이 가시고 부드러워진다. <br>', '129.0246573900', '35.1012422102', 'http://tong.visitkorea.or.kr/cms/resource/02/612202_image2_1.jpg', '30000', '', '7943', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 2, 5, '고향집칼국수', '39', NULL, '부산광역시 남구 신선로 397-2', '48536', '849871', '051-622-2366', '고춧가루와 쌀, 콩 및 계절 채소를 직접 재배하여 음식을 만들고 있다.<br>', '129.0981282334', '35.1212302164', 'http://tong.visitkorea.or.kr/cms/resource/18/1866518_image2_1.jpg', '5000', '', '7059', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 2, 6, '급행장', '39', NULL, '부산광역시 부산진구 서면문화로 4', '47256', '850093', '051-809-2100', '숯불갈비 전문점인 급행장은 1950년 이래 대를 이어 고객들을 모시고 있다. 인정 받은 1급 한우 중에서도 암소만을 사용하며, 매일 등급 판정서를 게시하여 고객께서 확인할 수 있도록 하고 있다.<br>', '129.0580307117', '35.1581031257', 'http://tong.visitkorea.or.kr/cms/resource/62/1866862_image2_1.jpg', '20000', '', '5488', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 2, 7, '모닝듀 게스트 하우스(모닝듀)', '32', NULL, '부산광역시 동구 초량중로21번길 5-2', '48819', '2572851', '010-4465-5842', '싱가포르인 남편과 부산 아내가 운영하는 민박집이다. 2017년 건축상 본상 수상한 인상적인 건물이다. 영어와 중국어가 가능하다.', '129.0364337920', '35.1141493149', 'http://tong.visitkorea.or.kr/cms/resource/58/2571658_image2_1.jpg', '50000', '', '5261', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 3, 1, '마린시티', '12', NULL, '부산광역시 해운대구 우동', '48119', '2617724', '', '밤이 되면 화려하게 불을 밝히는 마린시티는 세계적으로 유명한 홍콩이나 상하이도 부럽지 않은 아경을 가지고 있는 곳이다. 마린시티의 야경을 보고 있으면, 외국의 도시에 있는 듯한 착각을 불러일으킨다. 거리에는 외국 음식점은 물론이고 고풍스러운 카페와 주점이 있어 많은 관광객들이 찾고 있다.<br>', '129.1436566548', '35.1560327184', 'http://tong.visitkorea.or.kr/cms/resource/19/2617719_image2_1.jpg', '10000', '', '10825', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 3, 2, '민락수변공원', '12', NULL, '부산광역시 수영구 광안해변로 361', '48289', '126799', '', '* 바다와 어우러지는 공간, 민락수변공원*<br /><br />수영구는 오랜 역사와 전통을 지닌 곳으로 경관이 빼어나다. 뒤로는 산세가 수려한 금련산이 있고 앞으로는 광안리해수욕장이 있어 전형적인 배산임해의 명승지이다. 조선시대에는 경상좌도 수군절도사영이 있어 군사적으로 대단히 중요한 지역이었다. 수영이란 지명도 수군절도사영의 준말이었는데 현재의 지명으로 굳어졌다. 이곳에 조성된 민락수변공원은 1992년 8월 공사가 시작돼 1997년 5월 길이 543m, 너비 60m의 공원이 완공되었다. 이 공원은 바다와 휴식공간을 결합한 국내 최초의 수변공원으로 해운대와 광안리의 중간 지점에 위치하고 있다. 면적 1만평 규모로 4만명을 수용할 수 있으며, 바닥에는 컬러 블록을 깔고 화단과 그늘집, 벤치 등의 편의시설을 갖추고 있다. 바다를 바라보며 각종 행사도 관람할 수 있는 3,040㎡에 달하는 스탠드는 만조 때 바닷물이 들어와 발을 담글 수도 있는 공간이다.<br>', '129.1308882887', '35.1571823531', 'http://tong.visitkorea.or.kr/cms/resource/17/1573617_image2_1.jpg', '0', '', '1171', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 3, 3, '마하사(부산)', '12', NULL, '부산광역시 연제구 봉수로 138', '47610', '304695', '', '* 위대한 사찰이라는 의미의 마하사 *<br />\n고대 인도 산스크리트어로 ‘마하’는 ‘훌륭한’ \n‘존귀한’ ‘위대한’이라는 뜻을 의미하고 결국 마하사는 ‘위대한 사찰’이라는 \n해석을 낳게 한다. 이 위대하고 큰 마하사는 해발 400m의 금련산에 자리잡고 있는 \n1천5백년된 고찰이다. 연제구 연산로를 따라 연제구청을 조금 지난 위치에서 마주보는 \n산이 마하사가 위치한 금련산 자락이다.<br />\n<br />\n시내버스 노선이 있는 큰 도로변은 아파트며 상가들이 \n즐비하지만 오르막 길로 접어 들어 5분 남짓되면 이내 울창한 숲이 눈앞에 다가선다. \n마하사 앞 주차지는 약간 경사진 지형이나 도시에서 맛볼 수 없는 나무숲이 신선한 \n내음과 함께 그늘을 제공하고 절 입구 반야약수터는 등산객들이나 마을사람들이 심심찮게 \n찾기도 한다.<br />\n<br />\n우리나라 산천엔 연꽃과 관련된 산명이 유난히 많다. \n이는 산봉우리 형상이 연꽃 봉우리처럼 둥근 양감과 부드러운 생명감을 동시에 지녔기 \n때문이다. 마하사가 위치한 금련산(金蓮山)의 금련(金蓮)도 금(金)색 빛나는 연꽃이라는 \n뜻이니 금이라는 광물이 지니는 성질처럼 ‘불변의 연꽃’이라는 큰 상징성을 지닌다 \n해도 과언은 아니다. <br />\n부산의 동남해안에 뻗어 있는 황령산의 서북쪽 여러 \n산봉 중 하나인 이 ‘금빛 연꽃 산’에는 금학이 알을 품고 있는 금학포란(金鶴包卵)의 \n형세를 한 고찰이 있으니 바로 5세기경 아도화상이 창건한 마하사(摩河寺)이다. 넓지는 \n않으나 새가 알을 낳아 품고 있는 듯 둥지 같이 오붓한 분위기를 자아내는 마하사는 \n불법(佛法)으로 중생을 따뜻하게 품어, 청정하고 지혜로운 불자들을 키워낼 영원한 \n요람이라는 깊은 의미를 지닌다. 이처럼 마하사는 깊은 산중사찰이지만 실은 부산의 \n중심인 시청에서 10분거리 남짓할 뿐 아니라 우리민족의 오랜 신앙으로 자리잡은 \n나한신앙의 근본도량으로 정통성을 인정받고 있다. 도심과 산지지형이 한데 어우러져 \n공존하는 부산만의 특색이랄 수 있겠다<br />\n<br />\n* 알면 재미있는 마하사의 나한설화 *<br />\n‘불씨를 구해 준 나한과 동지팥죽’설화며 ‘참새를 \n쫒아낸 나한’‘소리나지 않은 금구(金口)’등 전해져 오는 마하사의 나한설화는 \n6신통(神通)과 8해탈을 모두 갖추어 인간과 천인들의 소원을 성취시켜 주는 복전(福田)인 \n16나한의 신통력을 다시금 감흥하게 하는 묘한 매력이 있다. 또한 금련산 마하사(반야암, \n바라밀암)에서 들려오는 저녁 종소리를 단연 으뜸이라 하여 부산 수영팔경(水營八景)중 \n하나인 연산모종(蓮山暮鐘)으로 칭했으며 범어사, 운수사, 선암사와 더불어 부산의 \n4대 사찰로 그 사격이 날로 드높다. <br>', '129.0875779857', '35.1637835905', 'http://tong.visitkorea.or.kr/cms/resource/24/1561824_image2_1.jpg', '0', '', '4015', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 3, 4, '마산게낙찜', '39', NULL, '부산광역시 해운대구 마린시티1로 91', '48119', '851936', '051-747-7773', '마산게낙찜은 연평도에서 직송해온 국내산 암꽃게에 새벽시장의 신선한 낙지, 경북 영양 고추와 의성 마늘을 사용하고 있으며, 조미료 대신 다시마와 멸치, 양송이와 대파 등을 넣고 뽑아낸 육수로 조리해 맛이 깊고 담백하다. 여기에 유기농 콩을 재료로 한 무공해 콩나물, 갓 찧은 쌀로 지은 밥은 마산게낙찜 맛에 한 몫을 하고 있다.', '129.1445010556', '35.1549189697', 'http://tong.visitkorea.or.kr/cms/resource/73/1867173_image2_1.jpg', '20000', '', '5281', '2019-09-23', '2019-09-23'),
	('PLAN20190900000008', 3, 5, '동백섬횟집', '39', NULL, '부산광역시 해운대구 해운대해변로209번나길 17', '48093', '135827', '051-741-3888', '해운대해수욕장은 수심이 얕고 조수의 변화도 심하지 않으며 시설이 잘 정비된 1급 해수욕장으로 해마다 수만명이 찾는 여름 명소이다. 주변 맛집으로 동백섬횟집이 있는데 3층짜리 하얀색 건물로 실내에서 밖을 볼 수 있는 반원형 창가는 많은 사람들에게 인기가 있다. 한 접시 푸짐하게 담겨 나오는 모듬회가 이 집의 대표 메뉴라 할 수 있는데, 저렴하면서도 그 양이 많아 회로 배를 채운다는 말이 나올 정도다. 부산의 싱싱한 회를 맛볼 수 있으며 회덮밥과 매운탕도 그 맛이 좋다.<br>', '129.1546889075', '35.1604296524', 'http://tong.visitkorea.or.kr/cms/resource/87/218487_image2_1.jpg', '30000', '', '1112', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 1, 1, '금마저수지', '12', NULL, '전라북도 익산시 금마면 고도9길 41-64', '54572', '1582934', '063-859-3818', '<b>또 다른 한반도가 있다, 금마저수지!<br /></b>미륵산 정상에서 남쪽으로 내려다보면 대한민국 지도와 똑 닮은 모양이 나타난다. 다름 아닌 금마저수지로서 그 모양이 대한민국 지도를 닮았다고 해서 지도연못이라고도 불린다. 새벽 물안개 사이로 보이는 한반도 지형은 너무도 아름답고 독특한 재미를 느끼게 한다. 미륵산을 발원지로 안정적으로 용수가 공급되는 금마저수지는 1년 365일 물이 마르지 않으며 주변에 축사나 공장 등 오염원이 없어 1급수의 수질을 자랑한다. \n<br />\n저수지 주변에는 서동공원과 마한관이 있는데 공원에는 백제무왕의 동상과 서동선화공주 조각상 등 많은 조각 작품이 전시되어 있고 마한관에서는 익산의 역사와 문화를 한눈에 볼 수 있다. \n<br />\n탁 트인 푸르른 금마저수지에서 여유로운 한때를 보내보자! 주말에 가족과 함께, 또는 연인과 친구와 함께 나들이를 나오기에 안성맞춤인 곳이다.<br>', '127.0568399915', '36.0018246830', 'http://tong.visitkorea.or.kr/cms/resource/23/1651523_image2_1.jpg', '0', '', '0', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 1, 2, '남원사(익산)', '12', NULL, '전라북도 익산시 여산면 서촌1길 34-3', '54520', '298206', '', '* 보기 드문 평지 사찰, 남원사 *<br /><br />우리나라 사찰은 대체로 산속에 위치해 있다. 그런 면에서 본다면 남원사는 보기 드문 평지 사찰이다. 벼 이삭이 익어가는 8월이 어느 날. 장마 뒤끝에 눈부시게 푸른 하늘을 시샘하듯 들녘을 가득 메운 벼들은 빗물에 씻긴 채 물기를 머금고 초록빛이 더욱 짙어져 간다. 시원하게 뚫린 서해안고속도로를 달려 울창한 나무와 숲을 가로질러 난 고불고불한 산길을 지나 노인분들께 물어물어 남원사를 찾아간다. 그러다 더 이상 물어볼 사람이 없어 두리번거릴 때 파란 들판 한 가운데 그림같이 서 있는 사찰이 나타난다. 바로 남원사를 만나게 되는 순간이다. 남원사는 논두렁 사이에 파랗게 익어가는 벼 이삭 사이로 서 있는 아담한 사찰이다. 그곳에서는 장맛비를 맞아 환하게 피어 올라온 분홍빛 연꽃이 우선 맞이해 준다. 일주문을 거쳐 경내로 들어서면 유난히 아름다운 화단이 또 지친 나그네를 맞아준다. 낮은 꽃밭에는 정겨운 우리 꽃들이 수줍게 꽃망울을 터뜨리고 있다. 아마도 이곳에 도착하게 되면 계절 따라 철철이 피는 또 다른 꽃을 만나게 되고, 그 꽃을 보면 부처님의 진리를 깨닫게 될 것이다. 그러다가 미륵전을 들어가면 이곳 소박한 시골의 사찰을 닮은 순박한 모습의 부처님을 만나게 된다. 이제, 이 부처님께 절을 올리면서 불타의 법문을 듣게 되길 희구해 본다.<br /><br />* 여산장에 들린 후 마국수 한 그릇으로 여행을 장식하자 *<br /><br />남원사는 전라북도 익산시 여산면에 위치하고 있으며, 인근에서는 매월 1일과 6일 5일장인 여산장이 열린다. 여행길에서 만나는 사람 사는 정취를 느끼고 싶다면 1일과 6일에 맞춰 길을 떠나 남원사를 거쳐 여산장을 들리는 것도 좋은 여행 일정이라 할 수 있다. 또한, 익산시의 명물 서동마국수 한 그릇으로 주린 배를 채우는 것도 여행의 재미를 키워줄 것이라 할 수 있다.<br>', '127.0827695904', '36.0557273173', 'http://tong.visitkorea.or.kr/cms/resource/38/1306238_image2_1.jpg', '0', '', '6421', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 1, 3, '웨스턴라이프호텔', '32', NULL, '전라북도 익산시 동서로 380', '54630', '2564295', '063-720-3000', '웨스턴라이프 호텔&컨벤션은 익산 최초, 최고의 호텔로 약 7000여평의 대지에 지하2층, 지상12층으로 건축되어 모던하고 세련된 객실과 동시수용 1,500석 규모의 연회장, 대형 컨벤션 홀을 운영하고 있으며, 호텔 이용고객들을 위한 500여대의 대형 주차시설을 갖추고 있다. 익산을 방문하는 비즈니스 고객과 여행객에게 기억에 남을 만한 멋진 경험을 제공해 드리기 위해 최선을 다해 노력하고 있다.', '126.9760013325', '35.9503874810', 'http://tong.visitkorea.or.kr/cms/resource/04/2564304_image2_1.jpg', '0', '', '15142', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 1, 4, '동보성', '39', NULL, '전라북도 익산시 하나로 437', '54546', '820754', '063-838-8822', '동보성은 전통성 강한 정통 중화요리 전문점이다. 세계화에 발맞추기 위해 중국에서 각지 입맛에 능한 요리 대가를 초청해서 각국의 손님들을 맞이하고 있다. 개인룸부터 대형 연회장까지 그리고 모든 개인 식사도 가능하도록 준비되어 있는 곳이다.', '126.9810811992', '35.9594032077', 'http://tong.visitkorea.or.kr/cms/resource/21/818721_image2_1.jpg', '0', '', '1100', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 1, 5, '만나먹거리촌', '39', NULL, '전라북도 익산시 금마면 무왕로 1790-8', '54573', '1851929', '063-834-1110', '국내산 황태를 재료로 한 황태찜과 황태구이를 전문으로 요리하고 있으며, 이밖에 오리주물럭, 민물새우탕, 참게장을 함께 하고 있다. 밑반찬으로는 직접 재배한 유기농 야채를 활용한 다양한 반찬이 손님을 맞이할 준비를 하고 있으며, 객실은 일반손님을 위한 1층(80석_룸 2개)과 단체를 위한 2층(64석)이 마련되어 있다. 또한 넓은 주차장이 있어 편리하게 이용할 수 있다.<br>', '127.0454748267', '35.9832849334', 'http://tong.visitkorea.or.kr/cms/resource/75/1977375_image2_1.jpg', '0', '', '6384', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 2, 1, '[백년가게]진미식당', '39', NULL, '전라북도 익산시 황등면 황등로 158', '54533', '2607967', '063-856-4422', '※ 영업시간 10:30~19:00 (11월~2월) / 11:00~19:00 (3월~10월) 재료소진시 마감\n\n[중소벤처기업부 2018년도 \'백년가게\'로 선정]\n\n1931년 익산 황등장터에서 시작하여 현재까지 이어오고 있는 진미식당은 황등 비빔밥 전문점이다. 깊은 맛이 우러난 국물로 토렴을 한 밥에 직접 만들어 숙성시킨 장을 넣어 당일분만 받아쓰는 육회를 올려 한그릇씩만 비벼서 만드는 육회비빔밥과 조미료를 최소화하고 비린맛없이 풍미 높은 선짓국이 대표 메뉴이다. 또한 순대국밥과 순대도 사람들이 많이 찾는 메뉴이다. 착한가게, 향토음식점, 익산시 ‘대물림맛집’ 지정 및 여러 매스컴에 소개된 전국에서 사람들이 찾아오는 맛집이다.', '126.9448582161', '36.0059389404', 'http://tong.visitkorea.or.kr/cms/resource/79/2607979_image2_1.jpg', '0', '', '9414', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 2, 2, '[백년가게]무진장갈비촌', '39', NULL, '전라북도 익산시 선화로31길 58', '54620', '2609122', '063-843-3070', '※ 영업시간 11:00 ~ 22:00\n\n[중소벤처기업부 2019년도 \'백년가게\'로 선정]\n\n무진장갈비촌은 갈비탕의 주재료인 사골을 24시간 이상 푹 고아 내는 부모님의 방식을 그대로 유지하며 31년 동안 운영해왔다. 한우를 잘하는 집으로 유명해, 갈비탕 뿐만 아니라 육회, 구이 등 모든 메뉴의 맛이 좋다. 특히 상에 오르는 모든 식재료의 특성과 궁합에 따라 갖은 양념을 만들어 반찬을 만들고, 육회 및 육사시미는 당일 구매 및 소비를 원칙으로 하고 있어 신선한 음식을 맛볼 수 있다. 모든 테이블이 룸 형태이기 때문에 아이와 함께 방문하거나 가족모임에 제격이다.', '126.9560281092', '35.9506148877', 'http://tong.visitkorea.or.kr/cms/resource/74/2609274_image2_1.jpg', '0', '', '6221', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 2, 3, '익산그랜드팰리스호텔', '32', NULL, '전라북도 익산시 목천로1길 9', '54674', '2492855', '063-843-2200', ' 익산 그랜드팰리스호텔의 객실타입은 5가지이고, 총 54객실이다. 객실 내 TV, 냉장고, 냉난방기, 세면도구, 드라이기, 와이파이가 구비되어있다. 객실당 2인 기준이고, 인원 1명 추가비용은 1만원이며, 침구추가 및 1인조식 추가도 1만원이다. 부대시설은 카페, 스카이라운지, 사우나, 마사지, 주차장, 조식식당, 연회장, 세미나실이 있다. 전화, 홈페이지, 카카오톡, 제휴 어플리케이션, 방문으로 예약이 가능하다. <br>', '126.9447074267', '35.9303475593', 'http://tong.visitkorea.or.kr/cms/resource/35/2492835_image2_1.jpg', '0', '', '2470', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 2, 4, '함열향교', '12', NULL, '전라북도 익산시 함라면 함라교동길 27-4', '54502', '128957', '', '1437년(세종 19)에 현유(賢儒)의 위패를 봉안, 배향하고 지방민의 교육과 교화를 위하여 창건되었다. 1491년(성종 22) 명륜당이 건립되었으며, 1591년(선조 24) 금곡(金谷)으로 이건하였다가 1592년 임진왜란으로 소실되었다. 영조 때 중건되었다가 1831년(순조 31) 현재의 위치로 이전하였다. 현존 건물로는 대성전·영소전(靈昭殿)·명륜당·동재(東齋)·서재(西齋)·난심재(蘭心齋)·교직사 등이 있다. 대성전에는 5성(五聖), 송조4현(宋朝四賢), 우리 나라 18현(十八賢)의 위패가 봉안되어 있다. 조선시대에는 국가로부터 토지와 전적·노비 등을 지급받아 교관 1명이 정원 30명의 교생을 가르쳤으나, 갑오개혁 이후 신학제 실시에 따라 교육적 기능은 없어지고 봄·가을에 석전(釋奠)을 봉행(奉行)하며 초하루·보름에 분향을 하고 있다.<br>', '126.9095987422', '36.0637206248', 'http://tong.visitkorea.or.kr/cms/resource/59/573759_image2_1.jpg', '0', '', '15134', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 2, 5, '익산교도소세트장', '12', NULL, '전라북도 익산시 성당면 함낭로 207', '54505', '2594360', '', '성당면 와초리 성당초등학교 남성분교 폐교부지 위에 세워진 국내 유일의 교도소 세트장이다. 이곳에서는 영화 \'홀리데이\',\'아이리스\',\'전설의 마녀\',\'7번방의 선물\',\'내부자들\',등 200여 편의 영화와 드라마가 촬영 되었다. 세트장에 들어서면 \'영화가 탄생하는 마술상자\' 라는 말을 증명하듯 그동안 촬영된 영화 포스터들을 전시 하고 있다. 또한 운동장에 깔린 천연잔디는 관광객들의 휴식공간으로 각광받고 있다.<br>', '126.9306828160', '36.0695231043', 'http://tong.visitkorea.or.kr/cms/resource/02/1821002_image2_1.jpg', '0', '', '2006', '2019-09-23', '2019-09-23'),
	('PLAN20190900000009', 2, 6, '익산 토성', '12', NULL, '전북 익산시 금마면 서고도리', '570-913', '126945', '', '* 백제의 숨겨진 문화를 찾아서, 익산토성(益山土城) *<br /><br />익산토성은 금마면의 면 소재지에서 서북쪽 약 1.5km 떨어진 오금산의 구릉상에 위치하고 있으며, 뒤에는 미륵산이 있고 앞쪽은 익산평야가 내려다 보인다. 삼국통일 후 고구려의 왕족인 안승(安勝)이 보덕국(報德國)을 세웠던 곳이라고도 전하여 보덕성이라 부르기도 하며, 사적 제92호이다.1980년도에 원광대학교 마한·백제문화연구소 학술발굴조사에 의하면 이 성이 석성임과 성 시설 중 남문지가 밝혀졌고, 수구도 새로 발견되었다. 발견된 유물에 의하면 백제 말기에 와당류와 토기류가 주종을 이루고 있는 것으로미루어 무왕(600∼641, 재위)대의 성으로 추정된다.<br /><br />또한 전하는 바에 의하면 이 성은 삼국통일 후, 신라 문무왕(661∼681년, 재위)때 고구려 왕족 안승이 보덕국을 세웠던 곳이라 하여 보덕성이라 부르기도 한 곳이다. 혹은 서동설화와 관계를 맺고 있어 오금산성이라 부르기도 한 곳이다. 오금산(五金山)의 구릉에 계곡을 감싸고 쌓은 포곡식(包谷式) 성으로 둘레가 약 450m이다. 1980년 발굴조사한 결과, 문이 있었던 것으로 보이는 구역의 성벽 기초부분은 앞면에 높이 45cm, 너비 60cm, 두께 50∼60cm 크기의 직사각형 석재를 사용하여 단벽(段壁)을 쌓았고,벽석의 뒷면에는 깬돌 등의 잡석을 너비 5.2m 두께로 메웠음이 밝혀졌다. 두께는 5.5m에 이르고 남문 터에는 문루(門樓)가 설치되었던 것으로 추정된다.<br /><br />* 익산에 백제 유물이 많은 이유 *<br /><br />익산토성에서 발굴된 유적은 벼루 조각 ·세발토기조각 등이다. 대다수의 유물은 백제시대의 유물이다. 이로 인해 익산토성은 백제의 전성기에 축성한 것으로 짐작하게 되는 것이다.익산 지역에는 유난히 백제시대의 유물이 많이 나온다. 그 이유는 5세기 중엽부터 백제의 지방 중심세력이었고 7세기를 전후하여 금강변의 웅포 지역보다 내륙에 위치한 금마 지역을 중심으로 백제문화의 꽃을 피웠다. 즉, 7세기를 전후한 백제문화의 전성기 때 백제의 중심지가 바로 익산이었기 때문에 익산에 유난히 백제유물이 많은 것으로 추정된다.<br>', '127.0372808731', '35.9924420801', 'http://tong.visitkorea.or.kr/cms/resource/29/1308629_image2_1.jpg', '0', '', '12863', '2019-09-23', '2019-09-23'),
	('PLAN20190900000010', 1, 1, '경장각', '12', NULL, '광주광역시 광산구 용아로 460', '62233', '1622544', '', '경장각은 조선인조(仁祖1551∼1627) 때의 문신(文臣)이었던 이신의(李信儀)을 배양하는 곳이다. 이 곳에는 이신의의 유물로 선조왕(宣祖王) 친필병풍(親筆屛風)과 거문고가 보존되어 있다. 병풍은 인조가 그의 충절을 가상히하여 선조왕 친필을 하사(下賜)한 것이며, 거문고는 석탄이 평소 애용하던 거문고이다. 정묘호란,(丁卯胡亂)때 왕을 대동하여 강화도로 가던 중 병으로 인천에 체류하다가 수원(水原) 마정리에서 객사(客死)하였다. <br>', '126.8052666353', '35.1843631887', 'http://tong.visitkorea.or.kr/cms/resource/19/1587419_image2_1.jpg', '0', '', '0', '2019-09-23', '2019-09-23'),
	('PLAN20190900000010', 1, 2, '고원희가옥', '12', NULL, '광주광역시 남구 압촌길 78', '61751', '1621158', '', '임진왜란 때 금산(錦山) 전투에서 전사한 의병장 고경명(高敬命)의 옛 집터를 그 후손들이 여러차례 고쳐지은 집이다. 이 집은후손들이 여러 차례 고쳐 지은 집으로 현존 가옥은 1917년 건축한 것이다. 예전에는 더 많은 건물이 있었는데 현재는 사랑채, 안채, 곳간채, 사당, 대문등이 남아있다. 대문은 정면 세칸 측면 한칸이다. 사랑채는 정면 네칸 측면 한칸으로 앞뒤로 마루가 있는 팔작 기와집이다. 다듬돌 바른층 쌓기의 두벌대 기단위에 덤병주초석을 놓고 원형기둥을 세웠다. 기둥 위에는 우미량과 굴도리, 장혀를 보아지와 함께 결합하였다. 평면은 좌로부터 한칸의 방 3개와 한칸의 대청을 1단 높혀 누(樓)마루로 되어 있다. 안채는 정면 여섯칸, 측면 한칸에 전후좌우에 마루가 있는 팔작 기와집이다. 다듬돌 바른층 쌓기의 외벌대 기단위에 덤벙 주초석을 놓고 앞열은 원형 그 외는 사각기둥을 세웠다. 원형 기둥위에는 우미량과 굴도리, 장혀를 보아지와 함께 결합하였다. 내부가구는 고주위에 대량을 걸치고 판대공을 놓았으며 구 위에 종도리를 놓았다. 평면은 좌로부터 한칸반의 부엌, 두칸의 대청, 한칸의 방으로 되어 있다. 창호는 전체가 양쪽으로 열어지는 문이며, 방으로 되어 있고 그 위에 넉살창이 있다. 숭정기원후오갑정사윤이월초구일(崇禎紀元後五甲丁巳閏二月初九日)이라는 상량문에 글씨가 쓰여 있는데 이것으로 건립연대가 1917년임을 알수 있다. 사당은 고경명장군과 그 아들 종후, 인후 형제의 부조묘이다. 이 사당은 정면 세칸 측면 한칸으로 앞에만 마루가 있으며 맞배지붕이다. 외별대의 낮은 기단위에 덤벙 주초석을 놓고 현형기둥을 세웠다. 기둥 위에는 첨자형의 포로 결합하였고, 초익공 위에는 용두를 빼내고 장혀와 굴도리를 얹었다. 내부가구는 대량위에 동자주를 얹고 종보를 걸친 뒤 그 위에 다시 판대공을 놓았으며, 겹처마에 조잡한 모조 단청을 하고 있다. 건립연대는 안채보다 빠른 시기로 추측된다. <br>', '126.8385854265', '35.0924046905', 'http://tong.visitkorea.or.kr/cms/resource/07/1587507_image2_1.jpg', '0', '', '10644', '2019-09-23', '2019-09-23'),
	('PLAN20190900000010', 1, 3, '금수장관광호텔', '32', NULL, '광주광역시 동구 무등로321번길 2', '61401', '136416', '062-525-2111', '20년 전통을 자랑하는 금수장 관광호텔은 동구 계림동(구 시청 계림동 청사 앞)에 위치해 편리한 지리적 여건을 갖춘 실속 있고 알찬 ', '126.9185071751', '35.1621231670', 'http://tong.visitkorea.or.kr/cms/resource/46/1587546_image2_1.jpg', '0', '', '10625', '2019-09-23', '2019-09-23'),
	('PLAN20190900000010', 1, 4, '금강식육식당', '39', NULL, '광주광역시 서구 천변우하로 373', '61902', '2484460', '062-514-9111', '※ 영업시간 11:00 ~ 22:00\n\n광주에 위치한 한우전문점이다. 특히 직접 한우농장을 운영하여, 보다 좋은 품질의 한우를 소비자들에게 제공하고 있으며, 넓은 인테리어와 분위기 있는 실내로 가족, 회식, 단체모임을 할 수 있는 곳이다.', '126.8611343006', '35.1689075428', 'http://tong.visitkorea.or.kr/cms/resource/33/2484433_image2_1.jpg', '0', '', '5281', '2019-09-23', '2019-09-23'),
	('PLAN20190900000010', 1, 5, '금다연한정식', '39', NULL, '광주광역시 서구 상무연하로 72', '61949', '135533', '062-385-0002', ' 광주광역시 서구 상무지구 중심가에 위치하고 있는 금다연 한정식은 우리나라를 방문한 외국관광객에게 한국의 전통음식의 멋과 맛을 선보이고 더 나아가 과학적이고 미적감각이 풍부한 우리의 음식문화를 널리 홍보하여 국위를 선양하고자 한다. 또한 감칠맛나고 깔끔한요리, 최고의 서비스, 전통한실의 훌륭한 분위기는 금다연의 자랑이다.<br />특히 주방장님의 경우 30년 조리경력에서 나오는 요리는 환상적인 맛을 내며, 예약고객의 경우 고객층의 입맛에 맞추어 전통과 퓨전을 병행하여 고객의 만족도를 더하고 있으며 돌잔치, 회갑연 등 각종 모임을 가질 수 있는 공간을 확보하고 있으며 대형 연회장도 별도로 준비되어 있다.<br>', '126.8505022648', '35.1546367535', 'http://tong.visitkorea.or.kr/cms/resource/44/1892744_image2_1.jpg', '0', '', '1856', '2019-09-23', '2019-09-23'),
	('PLAN20190900000011', 1, 1, '수원 화성 [유네스코 세계문화유산]', '12', NULL, '경기도 수원시 팔달구 정조로 910', '16253', '125555', '', '조선 태종때 도호부로 승격된 수원은 한성 4진의 하나였다. 한양을 중심으로 동서남북 방향인 광주, 강화, 수원, 개성에 진을 설치하여 수도 한양을 보위케 한것이다. 그 수원땅을 지키고 있는 화성은 유네스코에 의해 세계문화유산으로 등재되면서 역사적 가치를 높이고 있다.또한 다양한 상설 공연을 진행하고, 매년 가을이면 수원화성문화제를 개최하여 수원 화성을 알리고 있다.팔달산(143m)을 중심으로 쌓은 화성(사적 제3호)은 총길이가 5,700m에 달한다. 화성은 단순한 하나의 \'성\'이 아니다. 역사적 의미와 함께 건축학적으로도 귀중한 문화유산이다.화성의 축성은 정조의 한과 효심에서 비롯됐다. 1762년 영조 38년 윤 5월21일 사도세자 (장조:고종때 추존)는 당쟁으로 인하여 한여름 뒤주 속에 갇혀 8일만에 죽었다. 사도세자의 아들 정조는 당시 11세였다. 할아버지 영조의 뒤를 이은 정조는 즉위 13년만에 부친의 고혼을 위로키 위해 묘를 양주 땅 배봉산(지금의 서울시 전농동)에서 수원 화산으로 옮기고 수원을 자신이 이상으로 꿈꾸는 신도시로 건설하고자 정조 18년 정월부터 20년9월까지에 걸쳐 성곽을 축성하였다.<br /><br />화성은 조선 성곽제도의 최고 완성형이다. 한국성곽 발달사에서도 가장 중요한 비중을 차지한다. 이 성은 석성과 토성의 장점만을 살려 축성됐다. 화성축성에 매달린 선조들은 한국성곽의 약점을 보완하기 위해 중국과 서양의 축성술을 본뜨기도 했다.이 성에 관련된 사항은 ', '127.0102456179', '37.2807935228', 'http://tong.visitkorea.or.kr/cms/resource/36/2613036_image2_1.jpg', '50000', '', '0', '2019-09-24', '2019-09-24'),
	('PLAN20190900000012', 1, 1, '광치기해변', '12', NULL, '제주특별자치도 서귀포시 성산읍 고성리', '63641', '2564158', '', '\n성산일출봉에서 섭지코지로 향하는 길목에 있는 광치기 해변은 제주올레 1코스의 마지막이자 2코스가 시작되는 곳이다. 펄펄끓던 용암이 바다와 만나 빠르게 굳어지며 형성된 지질구조가 특징이며, 특히 썰물때는 바닷물에 가려있던 비경들이 속속들이 들어나 숨은 비경을 선사한다. 용암 지질과 녹색 이끼가 연출하는 장관은 전 세계 어디서도 볼 수 없는 풍경을 자아내어 많은 사진작가들이 찾는 사진명소이기도 하다. 특히 성산일출봉 옆으로 뜨는 일출을 한 프레임에 담을 수 있어 연말연시에 많은 사람들이 찾는다. 광치기 해변의 모래는 현무암의 풍화작용으로 오랜 세월에 걸쳐 만들어진 입자로, 검은 색을 띄는 것이 특징이다.<br>', '126.9239478125', '33.4523086956', 'http://tong.visitkorea.or.kr/cms/resource/55/2564155_image2_1.jpg', '0', '', '0', '2019-09-24', '2019-09-24'),
	('PLAN20190900000012', 1, 2, '관음사(제주)', '12', NULL, '제주특별자치도 제주시 산록북로 660', '63244', '126456', '', '제주시 아라동 한라산 동북쪽에 기슭에 있다. 언제 누가 창건했는지 알 수 없다. \n 조선 숙종때 제주목사였던 이형상이 제주에 잡신이 많다고 하여 사당과 함께 절 5백동을\n 폐사시켰을 때 폐허가 되었다. \n 그 뒤 1912년 비구니 봉려관이 다시 창건하여 법정암이라고 했다. 봉려관은 원래 떠돌이\n 무당이었으나 1901년 비앙도로 가던 중 우연히 풍랑을 만나 죽을 지경이 되었는데, 관음보살의 신력으로 살아나게 되었다.  \n 이에 감응하여 비구니가 되어 이 절을 짓고 불상을 모셨다. \n 한 때 주민의 반대로 한라산으로 피신을 했는데, 1912년 승려 영봉과  지사 도월의 도움으로 법정암을 창건했다. 그 뒤로 신도가 늘어나자 절 이름을 관음사로 바꿨다. \n 조계종의 제 23교구 본사로 웅장한 모습을 하고 있다. \n 1964년 중건하여 오늘에 이르고 있는데,  한라산 등산의 기점 지역이라 사람들의 발길이 잦다. <br>', '126.5581440803', '33.4237615317', 'http://tong.visitkorea.or.kr/cms/resource/92/1618292_image2_1.jpg', '0', '', '34163', '2019-09-24', '2019-09-24'),
	('PLAN20190900000012', 1, 3, '관음사가는길펜션', '32', NULL, '제주특별자치도 제주시 산록북로 819', '63244', '2486105', '064-724-9931', '한라산 등반코스인, 관음사 3분, 성판악 10분, 어리목 25분, 영실 35분 정도만 소요될 정도로 제주도에서는 한라산 등반코스 입구와 가장 가까운 펜션이다.', '126.5544218683', '33.4375793887', 'http://tong.visitkorea.or.kr/cms/resource/96/2486096_image2_1.JPG', '0', '', '1571', '2019-09-24', '2019-09-24'),
	('PLAN20190900000012', 1, 4, '고관심해장국', '39', NULL, '제주특별자치도 제주시 한경면 용고로 86', '63008', '404193', '064-772-2825', '고산리 시내 농협 사거리에 소박하게 위치한 관심해장국 식당은 여타 유명한 식당과는 달리 진한 해장국 국물 맛으로만 조용히 입소문이 난 식당이다. 이 곳은 쇠고기 선지 해장국이 주 메뉴이며 콩나물 해장국과 소 내장탕은 시골의 진한 국물 맛을 그대로 간직하고 있다.<br />쇠고기 선지 해장국은 뚝배기 한가득 아끼지 않고 담은 쇠고기와 선지의 양으로 보기만 하여도 푸짐하다. 콩나물 해장국 또한 진하고 시원한 국물 맛이 일품이며 콩나물 위에 살포시 얹혀지는 마른김은 더욱 입맛을 돋운다.<br>', '126.1795602716', '33.3011996689', 'http://tong.visitkorea.or.kr/cms/resource/42/401542_image2_1.jpg', '0', '', '38025', '2019-09-24', '2019-09-24'),
	('PLAN20190900000012', 2, 1, '가메창(암메)', '12', NULL, '제주특별자치도 제주시 한경면 청수로 13-3', '63006', '1884202', '064-728-2742', '저지오름 남서쪽 기슭 자락 도로변에 가마솥 모양으로 패여 있는 원형분화구를 품고 있는 자그마한 오름으로, 마을에서는 분화구가 솥바닥처럼 생겼다 하여 가메창이라 부르고 있다. 한경면 저지리 저청초등학교 사거리에서 수동 방향으로 조금 가면 도로 우측에 위치해 있어, 해송이 있는 언덕 위로 몇발자국만 오르면 분화구를 볼 수 있다. 분화구는 둘레가 약 300m, 깊이가 16m 정도로 외륜의 사면경사인 비고(6m)보다 더 깊이 패여있는 형태이다. 오름은 잔디밭 등성이로 곱게 둘러져 있고, 오름옆에 있는 외딴 민가로 들어가는 돌담을 넘는 순간 고운 잔디밭과 듬성듬성한 해송 사이로 움푹패인 굼부리에 놀라지 않을 수 없다. 분화구 바닥은 화구원과 사면사이에 돌담을 둘러 그 안에는 농경지로 이용되고 있다.<br>', '126.2488091788', '33.3295309529', 'http://tong.visitkorea.or.kr/cms/resource/01/1884201_image2_1.jpg', '0', '', '7174', '2019-09-24', '2019-09-24'),
	('PLAN20190900000012', 2, 2, '가파도', '12', NULL, '제주특별자치도 서귀포시 대정읍 가파로', '63514', '126446', '', '대정읍 모슬포에서 남쪽으로 5.5㎞ 떨어져 있는 섬으로 모슬포와 마라도의 중간지점에 위치하고 있다. 면적은 0.84㎢로 마라도보다 약 2.5배 더 크다.\n 19세기 중엽부터 사람이 살기 시작한 이 섬의 주민들은, 주로 어업에 종사하며 살고 있다.\n 주변 바다는 파도가 거칠어서 가끔 파선하는 일이 있는데 1653년 네덜란드의 선박 \n 스펠웰호의 가파도 표착은 널리 알려진 사건이다. 그 배에 승선하고  있던 헨드릭 \n 하멜은 고국으로 돌아간 뒤에 \'난선 제주도 난파기와 조선 국기\'를 저술하여 서양에서는 처음으로 우리나라를 소개했다.\n 현재 서귀포시 안덕면 사계리 산방산 아래의 용머리해안 입구에는 ', '126.2735884192', '33.1664326869', 'http://tong.visitkorea.or.kr/cms/resource/18/1999118_image2_1.jpg', '0', '', '18237', '2019-09-24', '2019-09-24'),
	('PLAN20190900000012', 3, 1, '강정천', '12', NULL, '제주특별자치도 서귀포시 강정통물로', '63563', '127050', '', '서귀포시 강정동 마을 동쪽에 있는 강정천은 평소에는 말라버리는 제주도의 일반 하천과는 달리 사계절 맑은 물이 흐르는 곳이다. 예로부터 대가내천, 대가래천, 큰내 등으로 불렸으며, 강정천을 인접하여 강정취수원과 정수장이 있으며 서귀포시 식수의 70%를 강정천에서 생산 공급하고 있다. <br />\n 강정천은 용천수로 매년 11월부터 이듬해 4월까지는 용출 수량이 다소 줄어들지만, 풍수기인 6월∼9월에는 많은 수량이 용출되어 바다로 흐르고 있다.\n 하천길이가 총 16km, 소 7곳, 교량 6개, 폭포 13개가 있고, 맑은 물이 흐르는 하천 양쪽에는 기암절벽과 노송이 우거져 절경을 이루고 있다. 강정천에는 다른 지역에서 보기 어려운 1급수 어종인 은어가  서식하고  있으며, 천연기념물 제327호인 원앙이 무리지어 사는게 종종 목격되고 있다. 수려한 자연경관과  맑은 물이  조화를 이루는 명소로 한여름에는 더위를 피해 찾아오는 피서객으로 만원을 이룬다. <br>', '126.4829350511', '33.2509905350', 'http://tong.visitkorea.or.kr/cms/resource/72/197272_image2_1.jpg', '0', '', '21655', '2019-09-24', '2019-09-24'),
	('PLAN20190900000012', 3, 2, '거문오름 [세계자연유산]', '12', NULL, '제주특별자치도 제주시 조천읍 선교로 569-36', '63341', '636266', '', '화구 중심으로부터 유출된 용암류의 침식계곡은 도내 최대 규모로서 전방으로 유선형의 골짜기를 이루며 약 4km 정도 연속되어 나타난다. 오름 동남쪽의 목장지에는 화구 없는 화산체인 용암암설류의 원뿔꼴 언덕들이 집중 분포구좌읍 송당리에 있는 거미오름을 일명 동검은오름이라고 하는데, 이는 이오름(서검은오름)과 구별하기 위한 호칭이라고 한다. 지역주민들 사이에는 분화구의 별칭으로 거물창(거멀창) 이라고 불리기도 하고, 숲으로 덮여 검게 보인다 하여 검은오름이라 부르고 있으나, 학자들의 어원적 해석으로는 `검은`은 神이란 뜻의 고조선 시대의 ` ·감·검`에 뿌리를 두는 것으로 풀이하고 있다. 즉 `검은오름`은 `신령스런 산`이라는 뜻이다. 해송, 삼나무, 측백나무가 조림되어 있고, 울창한 숲을 이룬다.<br /><br />\n\n* 해발 456m의 복합형화산체인 거문오름으로부터 흘러나온 용암류가 지형경사를 따라 북동쪽의 방향으로 해안선까지 도달하면서 20여 개의 동굴 무리를 이루고 있는데, 용암동굴계 중에서 벵뒤굴, 만장굴, 김녕굴, 용천동굴, 그리고 당처물동굴은 세계자연유산으로 지정되었다.<br /><br />\n※ 복합형 화산체 - 산정부에는 커다란 화구가 깊게 패어 있고, 그 안에 자그만 봉우리가 솟아올라 있으면서 북동쪽으로 크게 터진 말굽형 화구 등으로 이루어진다.<br />\n* 표고 456.6m / 비고 112m / 둘레 4,551m  / 면적  809,860㎡  / 저경 1,188m\n<br>', '126.7147325126', '33.4569159249', 'http://tong.visitkorea.or.kr/cms/resource/89/656789_image2_1.jpg', '0', '', '31419', '2019-09-24', '2019-09-24'),
	('PLAN20190900000013', 1, 1, '㈜강원심층수', '12', NULL, '강원도 고성군 죽왕면 심층수길 124-19', '24747', '2360786', '033-638-9900', '<strong>믿고 마실 수 있는 깨끗한 물</strong><br />\n세계에서 해양심층수를 취수할 수 있는 나라는 미국, 일본, 노르웨이. 대만, 그리고 대한민국 5곳이며 특히 우리나라 동해는 세계 해양학계에서도 인정한 천혜의 심층수 해역으로 인정받고 있다. 해양심층수산업 선도업체로서 강원도 고성에 위치한 강원심층수는 동해 최북단 청정해역 수심 605m에서 취수한 해양심층수로 미네랄워터, 해양심층수소금, 김치 등 주요제품으로 다양한 상품과 서비스를 제공하고 있으며, 견학 프로그램을 운영중이다.<br>', '128.5216381039', '38.3343742648', 'http://tong.visitkorea.or.kr/cms/resource/75/2360775_image2_1.jpg', '0', '', '0', '2019-09-24', '2019-09-24'),
	('PLAN20190900000013', 1, 2, '가령폭포', '12', NULL, '강원 홍천군 내촌면 와야리', '250-863', '125658', '', '가령폭포는 자연속에 숨겨진 오지의 백암산(1,099m) 서남쪽 기슭에 숨어 있으며 개령폭포라고도 불리운다.   가령폭포는 최근 생태체험 등산 동호인들이 찾으면서 알려지기 시작한 백암산과 더불어  우렁찬 굉음을 토하며 수십미터(50여m)의 낭떠러지를 뒤흔들며 내려꽃는 자태가 웅장하다. 주위에는 수많은 종류의 산나물과 약초,  야생화가 자생하고 있어 산새들의 낙원이기도 하다. 해발 950m 어사리덕 작은 산골샘(약용샘물이 나오는 샘)에서 솟은 청정수가  400리 홍천강으로 발원하는 비레올 계곡의 무명담소와 가령 폭포가 시원함을 더해준다.  가령폭포는 숲에 가려 좀처럼 모습을 드러내지 않는 폭포로 알려져 있다.  폭포 주변에는 인적이 드물어, 아직도 깨끗한 폭포수와 자연스런 멋을 간직하고 있는 폭포 중의 하나이다.<br>', '128.1601919204', '37.8328951114', 'http://tong.visitkorea.or.kr/cms/resource/55/1842055_image2_1.jpg', '0', '', '64073', '2019-09-24', '2019-09-24'),
	('PLAN20190900000013', 1, 3, '가리산(홍천)', '12', NULL, '강원도 홍천군 두촌면 가리산길 260-9', '25164', '125593', '', '해발 1,050.9m의 고산으로 춘천시와 홍천군의 경계지역에 위치하며, 산 정상에 서면 탁트인 시야와 발 아래로 펼쳐진 소양호의 풍경이 등산객들의 발을 묶는 곳이다. \n 산자락 밑에 위치한 조그마한 폭포의 물소리가 사람들의 마음을 포근히 감싸주며, 이 곳에서부터 계곡과 능선을 따라 등산이 시작된다. \n 이 산은 우거진 숲과 노송들이 등산객들을 맞아주고 정상을 오르게 되면, 1봉 남쪽에는 홍천강으로 발원하는 사시사철 끓이지 않는 석청수 작은 샘물이 등산객을 기다리고 있는 곳이기도 하다.\n 소양호 쪽으로 하산길을 택하면 배를 타고 피로를 풀 수 있는 등 각 코스마다의 즐거움이 기다리고 있는 아름다운 산이다. \n 이 일대를 홍천군에서 자연 휴양림 지역으로 조성, 1995년 7월 홍천군이 개장한 가리산 자연휴양림이 있어 통나무집, 야영장, 체육시설을 이용하는 가족휴양지로 각광을 받고 있다.<br>* 면적 305ha<br>', '127.9566453426', '37.8716589858', 'http://tong.visitkorea.or.kr/cms/resource/86/609386_image2_1.jpg', '0', '', '18425', '2019-09-24', '2019-09-24'),
	('PLAN20190900000013', 1, 4, '가리벨리 관광펜션(가리벨리관광펜션휴양타운)', '32', NULL, '강원도 인제군 인제읍 대목리길 137', '24643', '2571998', '010-8211-8877', '설악산국립공원 천연보호림(84만평)내 1만2천평 개인 소유의 자연친화적 환경(숲,계곡,폭포)과 청정지역에서만 볼수 있는 동ㆍ식물들이 살고 있다. 주변 20분내 거리에(내린척 래프팅,원대리 자작나무 숲,인제오토테마파크,필례약수,인제스피디움)이 있고, 주변 1시간내 거리에(한계령,오색약수,양양,속초,백담사)가 있어 자연속에서 여행과 힐링을 함께 할수 있는 인제군 제1호 관광펜션휴양타운이다.', '128.3383212190', '38.0731317037', 'http://tong.visitkorea.or.kr/cms/resource/37/2571137_image2_1.jpeg', '0', '', '40313', '2019-09-24', '2019-09-24'),
	('PLAN20190900000013', 1, 5, '가리왕산이야기 펜션', '32', NULL, '강원도 정선군 정선읍 청량길 32', '26119', '582215', '033-562-1665', '가리왕산 자연휴양림 입구에 자리한 가리왕산이야기 펜션은 가리왕산을 병풍처럼 두르고 2만여 평방미터의 대지에 조성되어 있다. 유럽식 고급 목조 주택으로 지어진 펜션동과 50여 년 이곳을 지켜온 고즈넉한 초가집을 개조해 만든 다실과 사장님께서 손수 황토로 지은 다실이 운영 중이다. 펜션동 본체는 총 네 개의 객실로 이루어져 있으며 깔끔한 인테리어와 사장님의 감성이 고스란히 담긴 예술 작품들로 꾸며져 가족과 연인들을 위한 공간으로 손색이 없다. 단체 이용객들을 위한 펜션동 별체는 복층으로 이루어져 있고 10여 명이 둘러앉고도 남을 널찍한 거실과 감각적인 인테리어의 주방이 눈에 띈다. 1층 침실과 2층 침실로 구성되어 있고 노래방 시설을 비롯하여 대형 TV 등이 마련되어 있다. 부대시설로는 황토로 잘 지은 황토방과 푸른 잔디밭에 멋지게 꾸며진 야외무대가 있다.', '128.5773415604', '37.4240753633', 'http://tong.visitkorea.or.kr/cms/resource/09/582509_image2_1.jpg', '0', '', '75072', '2019-09-24', '2019-09-24'),
	('PLAN20190900000013', 1, 6, '가을동화펜션', '32', NULL, '강원도 평창군 봉평면 흥정계곡길 72', '25300', '2376711', '010-5213-3200', '가을동화펜션은 평창 흥정계곡입구에 위치한 펜션으로, 순수 황토 벽돌집과 통나무집 2동으로 구성되어 있다. 소음없는 안락한 환경, 순수한 천연 암반수, 맑은 공기 속에서 편안한 휴식을 즐길 수 있다.', '128.3519002081', '37.6272975840', 'http://tong.visitkorea.or.kr/cms/resource/93/2376693_image2_1.JPG', '0', '', '30104', '2019-09-24', '2019-09-24'),
	('PLAN20190900000013', 1, 7, '가리산막국수', '39', NULL, '강원도 홍천군 두촌면 가리산길23번길 7', '25164', '608471', '033-435-2704', '※ 영업시간 10:00 ~ 21:00 (주말 07:00 ~ 21:00)\n\n가리산막국수는 넓은 주차장과 통나무집처럼 꾸려진 외관이 눈에 띄는 곳이다. 실내는 넓은 홀과 안쪽으로 같은 크기의 깨끗한 방으로 되어 있다. 막국수 전문점답게 새콤달콤한 양념과 신선한 야채가 곁들여져 있다.', '128.0138244871', '37.8381278478', 'http://tong.visitkorea.or.kr/cms/resource/30/610830_image2_1.jpg', '0', '', '37896', '2019-09-24', '2019-09-24'),
	('PLAN20190900000013', 1, 8, '가진항 자매해녀횟집', '39', NULL, '강원도 고성군 죽왕면 가진해변길 123', '24744', '686401', '033-681-1213', '자연산 물회는 어부들이 지난날 숙취로 인한 속을 달래려고 새벽 출어를 나가기 전에 요기삼아 먹던 음식이다. 바다에서 갓 잡아 올린 자연산 가자미, 오징어, 해삼 등의 해산물과 각종 야채, 초고추장이 어우러져서 담백하고 신선한 맛을 느끼게 한다. <br>', '128.5098075871', '38.3674757297', 'http://tong.visitkorea.or.kr/cms/resource/58/689558_image2_1.jpg', '0', '', '73119', '2019-09-24', '2019-09-24'),
	('PLAN20190900000014', 1, 1, '만성리 검은모래해변', '12', NULL, '전라남도 여수시 만성리길 15-1', '59622', '127547', '', '여수엑스포역에서 해안도로를 타고 북쪽으로 3km쯤 떨어진 곳에 위치해 있어서 교통이 편리하다. \n 길이 540m, 폭 25m 정도의 백사장과 평균수온 25℃로 따뜻해서  해수욕장으로는 최적지이다. \n 특히, 이곳의 백사장에 깔린 검은모래는 원적외선의 방사열이 높아서 모세 혈관을 확장시켜 혈액순환을 돕고 땀의 분비를 촉진시켜 준다고 한다.  그래서 신경통이나 각종 부인병에 효험이 있다고 한다. 그 효험이 절정에 이르는 매년 음력 4월 20일(양력 5월 하순~6월 \n 초순사이)이면 검은 모래가 눈을 뜬다 하여 검도모래찜질 한마당이 펼쳐진다. \n 해변 주변에 편의시설도 잘 갖춰져 있다. \n\n                   \n<br /><br />\n   * 모사금해수욕장<br />\n    만성리 해수욕장에서 2km쯤 더 들어가면 주위 경관이 뛰어나고 부드러운 모래해변과 \n    갯돌해변이 뒤섞인 해수욕장이 있다.\n    드라이브코스로도 적당하며, 여름철에는 피서객 뿐만 아니라 낚시꾼들도 많이 찾는다. <br>', '127.7447914615', '34.7771528453', 'http://tong.visitkorea.or.kr/cms/resource/82/1607882_image2_1.jpg', '0', '', '0', '2019-09-24', '2019-09-24'),
	('PLAN20190900000014', 1, 2, '고소동 천사벽화마을', '12', NULL, '전라남도 여수시 종화동', '59740', '2514658', '', ' 고소동 벽화마을은 여수에서 가장 오래된 자연부락으로, 언덕에 자리해 바다와 돌산대교, 거북선대교가 한눈에 들어온다. 임진왜란 당시에는 충무공 이순신 장군이 작전을 세우고 명령을 내리던 역사적인 곳이기도 하다. 2012 여수엑스포를 계기로 여수시와 고소동 주민들이 힘을 합쳐 담벼락에 벽화를 채우고 지리적 이점을 활용해 관광명소화 했다.  진남관부터 고소동 언덕을 지나 여수해양공원에 이르는 길이가 1004m라서 천사벽화골목이라고도 불린다. 골목은 7개 구간으로 나누어 여수엑스포 주제인 ‘해양’에 관련된 이야기를 비롯해 여수지역 역사와 문화, 풍경 그리고 이순신 장군과 수군들에 관한 그림 등을 벽화에 담았다. 전망 쉼터가 있으니 잠시 앉아 숨을 고르기 좋다. 마을 안에 자리한 충무공 대첩비각도 놓칠 수 없는 볼거리이다.  이곳에는 이순신 장군의 전적을 기린 좌수영대첩비(통제이공 수군대첩비·보물 571호)와 장군의 덕을 추모하는 타루비(보물 1288호)가 있다. 벽화골목이 끝나는 곳에는 여수해양공원이 있다. 여수 시민이 주말 나들이 장소로 즐겨 찾는 이곳은 밤에 특히 아름답다.<br>', '127.7405900393', '34.7383571176', 'http://tong.visitkorea.or.kr/cms/resource/77/2601677_image2_1.jpg', '0', '', '4321', '2019-09-24', '2019-09-24'),
	('PLAN20190900000014', 1, 3, '돌산 평사리 고니도래지', '12', NULL, '전라남도 여수시 돌산읍 안굴전길', '59771', '126736', '', '굴 양식장으로 이름난  돌산도의 굴전해안은 해마다 찬바람이 불기 시작하는 늦가을이면 고니떼가 날아든다. 흔히 \'백조(白鳥)\'로 불리는 고니는 순백의 우아한 자태와  힘찬 날개짓이 매우 아름다운 겨울철새이다. 더욱이 개체수가 많지 않아서 지난 1979년에 전라남도 기념물 제 43호로 지정되었다. 우리나라에 날아드는 고니류는 세종류 있는데,  돌산도의 무술목과 굴전리에서 볼 수 있는 것은 대부분 큰고니이다. 매년 늦가을에 50-150여 마리의 고니가 찾아들어 이듬해 2월 말이나 3월 초순 북녘을 향해 떠날 때까지 굴전해안에 머무른다.<br>', '127.7741770044', '34.6985204923', 'http://tong.visitkorea.or.kr/cms/resource/91/193391_image2_1.JPG', '0', '', '5385', '2019-09-24', '2019-09-24'),
	('PLAN20190900000014', 1, 4, '남해생선구이', '39', NULL, '전라남도 여수시 망마로 75', '59686', '790981', '061-692-3399', '', '127.6708622946', '34.7584425501', 'http://tong.visitkorea.or.kr/cms/resource/80/1973180_image2_1.jpg', '5000', '', '11565', '2019-09-24', '2019-09-24'),
	('PLAN20190900000014', 1, 5, '자연횟집', '39', NULL, '전라남도 여수시 대경도5길 25', '59766', '1814816', '061-666-3236', '자연횟집은 대경도에 위치한 갯장어(하모) 전문점이다. 자연횟집에 가려면 여수 극동항에서 대경도로 가는 배를 이용하면 된다. 배로 5분을 가면 경도에 도착한다. 자연횟집 방문전 전화를 주면 배 내리는 곳에서 바로 픽업이 가능하다. 자연횟집은 갯장어 (하모), 제철 각종 회와 매운탕을 메뉴로 하고 있으며, 단체예약이 가능하다.<br>', '127.7219812987', '34.7090545207', 'http://tong.visitkorea.or.kr/cms/resource/56/1340956_image2_1.jpg', '80000', '', '7207', '2019-09-24', '2019-09-24'),
	('PLAN20190900000014', 1, 6, '싱글벙글빵집', '39', NULL, '전라남도 여수시 관문1길 15', '59742', '2022607', '061-663-8797', '여주중앙초등학교, 여수여자중학교, 여수여자고등학교가 모여 있는 동네 인근에 위치한 싱글벙글빵집은 그야말로 추억의 옛날 빵을 맛볼 수 있는 소박한 동네 빵집이다. 샐러드빵, 고로케(크로켓), 팥도넛, 소시지빵, 크림빵, 찐빵을 판매하고 있다. 가격은 종류에 상관없이 개당 600원. 특히 마요네즈에 버무린 양배추와 분홍색 소시지가 들어간 샐러드빵은 모양새와 맛이 향수를 자극한다. 맛이 특별할 건 없지만, 친절하고 추억을 느낄 수 있다. 여행자들에게는 여수 여행 코스를 자세히 안내해주기도 한다.  <br>', '127.7422898001', '34.7419039723', 'http://tong.visitkorea.or.kr/cms/resource/75/2022375_image2_1.jpg', '10000', '', '4092', '2019-09-24', '2019-09-24'),
	('PLAN20190900000015', 1, 1, '강남 시티투어 - 트롤리버스', '12', NULL, '서울특별시 강남구 압구정로 161', '06001', '1949905', '', '강남의 주요 관광명소를 순환하는 셔틀버스로, 2016년 5월부터 세빛섬 정거장에서 강북을 여행하는 서울시티투어버스로 환승이 가능하도록 통합 운영 중이다.<br>', '127.0264344408', '37.5269874797', 'http://tong.visitkorea.or.kr/cms/resource/75/1258175_image2_1.jpg', '0', '', '0', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 1, 2, '경복궁', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126508', '', '※ 19_20 한국관광 100선 ※<br /><br />경복궁은 1395년 태조 이성계에 의해서 새로운 조선왕조의 법궁으로 지어졌다. 경복궁은 동궐(창덕궁)이나 서궐(경희궁)에 비해 위치가 북쪽에 있어 \'북궐\'이라 불리기도 했다. 경복궁(사적 제117호)은 5대 궁궐 가운데 으뜸의 규모와 건축미를 자랑한다. 경복궁 근정전에서 즉위식을 가진 왕들을 보면 제2대 정종, 제4대 세종, 제6대 단종, 제7대 세조, 제9대 성종, 제11대 중종, 제13대 명종 등이다. 경복궁은 임진왜란 때 상당수의 건물이 불타 없어진 아픔을 갖고 있으며, 고종 때에 흥선대원군의 주도 아래 7,700여칸에 이르는 건물들을 다시 세웠다. 그러나 또 다시 명성황후 시해사건이 일어나면서 왕조의 몰락과 함께 경복궁도 왕궁으로서의 기능을 상실하고 말았다. 경복궁에는 조선시대의 대표적인 건축물인 경회루와 향원정의 연못이 원형대로 남아 있으며, 근정전의 월대와 조각상들은 당시의 조각미술을 대표한다. 현재 흥례문 밖 서편에는 국립고궁 박물관이 위치하고 있고, 경복궁 내 향원정의 동편에는 국립민속 박물관이 위치하고 있다.<br /><br />* 주요문화재 <br />1) 경복궁(사적 제117호)<br />2) 경복궁 근정전(국보 제223호)<br />3) 경복궁 경회루(국보 제224호)<br />4) 경복궁 자경전(보물 제809호)<br />5) 경복궁 자경전 십장생 굴뚝(보물 제810호)<br />6) 경복궁 아미산굴뚝 (보물 제811호)<br />7) 경복궁 근정문 및 행각(보물 제812호)<br />8) 경복궁 풍기대(보물 제847호)<br>', '126.9769930325', '37.5788222356', 'http://tong.visitkorea.or.kr/cms/resource/40/1568040_image2_1.jpg', '0', '', '7224', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 1, 3, '관악산', '12', NULL, '서울특별시 관악구 관악로', '08826', '126480', '', '관악산(높이 632.2 m)은 1973년 관악구가 영등포구에서 분구되면서, 명산으로서 산 이름이 구의 명칭이 되어 관악구의 상징이자 자랑이 되고 있으며, 관악구 문화유산의 대부분이 관악산에서 비롯되었다. 1968년에 건설부 고시 제34호로 도시자연공원으로 지정되었으며 오늘날에는 수많은 서울 시민들이 즐겨 찾는 휴식처로 서울의 명소가 되었다.<br><br>곳곳에 드러난 암봉들이 깊은 골짜기와 어울려 험준한 산세를 이루고 있는 관악산은 산의 규모가 그다지 크지 않고 도심에서 가까워 누구나 하루 일정으로 산에 오를 수 있는데 봄에는 관악산 입구 쪽으로 벚꽃이 만발하고, 철쭉이 필 때는 철쭉제가 열리기도 한다. 봄철에 무리지어 피는 철쭉꽃과 여름의 짙은 녹음과, 계곡 깊은 곳에 동폭포, 서폭포의 물소리가 장엄하고 가을에는 단풍, 겨울에는 설경이 명산 관악의 면모를 자랑하고 있다.<br><br>관악산 정상에는 지상 레이다 관측소와 조선 태조 이성계가 서울을 도읍지로 정할 때 연주사와 원각사 두 절을 지어 화환에 대처했다고 하는 정상의 원각사와 연주암을 비롯하여 크고 작은 사찰과 암자가 있는데, 아슬아슬한 벼랑 위에 자리잡고 있는 연주대는 관악산의 모든 등산로가 집결하는 곳이다.<br><br>* 관악산 관문 - 관악산 입구에 설치되어 있는 문으로 전통 한옥식 팔각합작 지붕으로 되어 있다.', '126.9540987991', '37.4484036407', 'http://tong.visitkorea.or.kr/cms/resource/44/1857244_image2_1.jpg', '0', '', '14616', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 1, 4, '광화문', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126512', '', '※ 19_20 한국관광 100선 ※<br /><br />광화문은 경복궁의 남문이며, 궁성의 정문이다. 광화문은 국왕이 드나드는 정문이기도 했지만, 조선의 법궁인 경복궁의 정문이었기 때문에 다른 궁궐의 정문에 비해 그 규모와 격식 면에서도 매우 웅장하고 화려했다. 또한 광화문은 담장 끝 동쪽과 서쪽에 각각 동십자각과 서십자각을 두어 조선의 5대 궁궐 가운데 유일하게 궐문형식을 갖추고 있기도 했다. 광화문은 중층으로 된 문루를 받치는 기단석축에 세 개의 홍예로 이루어져 있는데, 중앙의 홍예로는 왕이, 좌우의 홍예로는 왕세자와 신하들이 출입하도록 하였다. 문루(門樓)에는 종을 걸어 때를 알리는 데 사용하였다. 이처럼 경복궁이 조선왕조의 법궁이라는 점은 광화문을 비롯한 성문의 형태에서도 확인할 수 있다. 다른 궁의 문들은 낮은 단 위에 문을 세우지만 경복궁의 문은 돌로 높은 석축을 쌓고 중앙에 홍예문을 내서 마치 성곽의 문과 같은 모습을 취한다. 광화문은 그 중에서도 홍예문을 셋이나 내서 가장 격식 높은 모습을 보여준다. 광화문이 법궁의 정문이라는 사실을 잘 드러내준다. 경복궁 창건 당시에는 특별한 이름이 없이 궁제에 따라 \'오문(午門)\'으로 부르다가, 태조 3년(1395) 정도전에 의해 \'정문(正門)\'으로 이름을 바꾸지만, 세종 8년(1426)에 경복궁을 수리하면서 집현전에서 \'광화문(光化門)\'이라 이름을 지어 올리면서 지금의 광화문이란 이름을 얻게 되었다.<br /><br />원래 광화문은 조선총독부 건물을 지으면서 궁성의 동문인 건춘문 북쪽으로 옮겼으나 한국전쟁 때 폭격을 맞아 문루가 부서지고 석축은 탄흔 투성이가 되었다. 그런 가운데 광화문이 현재의 모습으로 재건된 것은 1968년 박정희 정권에 이르러서다. 파괴된 문루를 다시 짓고 광화문을 현재의 자리로 옮겨 놓았던 것이다.그러나 새로 재건한 광화문은 철근 콘크리트로 지어졌으며, 재건 당시 광화문의 축을 경복궁의 중심축에 맞춘 것이 아니라 당시 중앙청으로 쓰이던 구 조선총독부청사의 축에 맞추어, 그 결과 3.5도 가량 본래의 축과 어긋나게 틀어지고 말았다. 뿐만 아니라 원래의 광화문 자리에서도 14.5미터 가량 뒤로 물러나 지금의 자리에 서있게 되었다. 이렇게 원형을 잃어버린 광화문을 복원하고자 2006년부터 광화문 철거작업을 시작해 3년 8개월의 복원 공사를 마치고 2010년 8월 15일 완공되었다. 화강암으로 육중한 기단을 만들고 그 위에 이층의 문루를 얹어 멀리 조망하기 좋을 뿐 아니라 궁궐 정문으로서의 위엄을 나타내고 있다. 기단에 있는 3개의 아치형 출입문은 경복궁이 조선의 정궁이라는 위엄을 과시한다. 돌로 만든 기단 위에는 흙을 구워 벽돌처럼 만든 \'전돌\'로 나지막한 담을 둘렀는데, 여기를 장식한 팔괘문양은 조선조 궁궐의 특성을 잘 반영하고있다.<br>', '126.9768042386', '37.5760725520', 'http://tong.visitkorea.or.kr/cms/resource/81/1075281_image2_1.jpg', '0', '', '14311', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 1, 5, '종묘광장공원', '12', NULL, '서울특별시 종로구 종로 157', '03135', '126492', '', '종묘는 조선 왕조의 왕과 왕비, 그리고 사후에 추존된 왕과 왕비의 신주를 모신 사당이다.\n 종묘 건축물은 문화적, 건축적 가치를 인정받아, 1995년 12월 유네스코 세계유산위원회의 정기총회 의결을 거쳐 세계문화유산으로 등록됐다.\n 종묘 앞 일대 39,669m²(1만 2천여 평)의 마당에 주차시설을 갖춘 종묘광장공원은 서울시민들의 휴식공간으로 널리 이용되고 있다. <br>공원 안에는 조선왕조 역대 임금들이 종묘를 드나들 \n 때 물을 마셨다는 우물이 다시 만들어졌고, 월남 이상재선생의 동상과 편의시설을 갖춘 녹지대가 만들어져 있다. \n서울시는 15,206m²(약 4,600평)에 달하는 주차광장을 문화예술의 광장으로 활용할 계획이며, 특히 젊은이들이 마음껏 활용할 수 있도록 자율적인 운영을 해 나갈 방침이다.<br>', '126.9944166605', '37.5711956572', 'http://tong.visitkorea.or.kr/cms/resource/90/1985990_image2_1.jpg', '0', '', '1647', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 1, 6, '성산일출봉 [유네스코 세계자연유산]', '12', NULL, '제주특별자치도 서귀포시 성산읍 일출로 284-12', '63643', '126435', '', '해발 180m인 성산 일출봉은 약 5,000년 전 제주도 수많은 분화구 중에서는 드물게 바닷속에서 수중폭발한 화산체이다. 용암이 물에 섞일 때 일어나는 폭발로 용암은 고운  화산재로 부서져 분화구 둘레에 원뿔형으로 쌓여 있다. 원래는 화산섬이었지만 신양해수욕장 쪽 땅과 섬  사이에 모래와 자갈이 쌓여 육지와 연결이 된 것이다. 일출봉 정상에는 지름 600m, 바닥면의 높이 해발 90m에 면적이 214,400㎡(성산일출봉 분화구의 면적)나 되는 분화구가 자리한다. 이곳에서  이장호 감독의  영화 \'공포의 외인구단\'의 한 장면이 촬영되기도 했다. 농사를 짓기도  했는데 지금은 억새밭을 이루며 우도가 손에 잡힐 듯 보인다.<br /> 예로부터 이곳 성산일출봉 정상에서 바라보는 일출광경은 영주10경(제주의 경승지) 중에서 으뜸이라 하였다.  넘실대는 푸른 바다 저편 수평선에서 이글거리며 솟아오르는 일출은 온 바다를 물들이고 보는 이의 마음까지도 붙잡아 놓으며 보는 이로 하여금 저절로 감탄케 한다. 지방기념물로 관리하다 2000년 7월 19일 천연기념물로 지정된 곳이다.<br>', '126.9415156012', '33.4581111174', 'http://tong.visitkorea.or.kr/cms/resource/85/1876185_image2_1.jpg', '0', '', '456373', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 1, 7, '해운대해수욕장', '12', NULL, '부산광역시 해운대구 해운대해변로 264', '48100', '126081', '', '* 2019 해수욕장 개장정보<br />- 기간: 6.1.~8.31<br />- 시간: 09:00~18:00 / 18:00~21:00<br />- 야간개장: 7.26~8.11<br /><br />* 부산 대표 해수욕장, 해운대해수욕장<br /><br />부산의 대표 해수욕장인 해운대해수욕장. 백사장의 길이 1.5km, 너비 30~50m, 평균수심 1m, 면적 58,400㎥의 규모로 넓은 백사장과 아름다운 해안선을 자랑하고 있으며 얕은 수심과 잔잔한 물결로 해수욕장의 최적 조건을 갖추고 있다.\'부산\' 하면 제일 먼저 떠올리는 곳이 해운대 해수욕장이라고 할 만큼 부산을 대표하는 명소이며, 해마다 여름철 피서객을 가늠하는 척도로 이용될 만큼 국내 최대 인파가 몰리는 곳이기도 하다. 특히, 해안선 주변에 크고 작은 빌딩들과 고급 호텔들이 우뚝 솟아있어 현대적이고 세련된 분위기의 해수욕장으로 유명하기 때문에 여름 휴가철뿐만 아니라 사시사철 젊은 열기로 붐비고 해외 관광객들에게도 잘 알려져 있어 외국인들이 많이 찾는 곳이다.<br /><br />* 해운대해수욕장의 다양한 축제와 즐길거리<br /><br />해운대해수욕장에서는 매년 정월 대보름날의 달맞이 축제를 진행하고 있다. 또한, 매년 겨울 주최하고 있는 북극곰수영대회는 이미 겨울철 대표 축제로 자리잡았다. 이외에도 모래 작품전, 부산 바다 축제 등 각종 크고 작은 행사들이 열리고 있다. 또한, 해수욕장 주변에 동백섬, 오륙도, 아쿠아리움 , 요트경기장, 벡스코 달맞이고개, 드라이브코스 등 볼거리가 많으며, 국내 1급 해수욕장답게 주변에는 일급 호텔을 비롯한 숙박, 오락시설 및 유흥 시설들이 잘 정비되어 있어 편안한 휴식을 즐길 수 있다.<br>', '129.1603078991', '35.1591243474', 'http://tong.visitkorea.or.kr/cms/resource/67/2612467_image2_1.jpg', '0', '', '278087', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 1, 8, '강릉 경포대', '12', NULL, '강원도 강릉시 경포로 365', '25461', '125790', '', '경포대는 관동팔경의 하나로 경포호수 북쪽 언덕에 있는 누각이다. 고려 충숙왕 13년(1326)에 강원도의 한 관리였던 박숙정이 당시 방해정 뒷산 인월사 옛터에 세웠던 것을 조선 중종 3년(1508)에 강릉부사 한급이 지금의 자리에 옮겼고, 여러 차례의 중수 끝에 현재의 모습을 갖추었다. 앞면 5칸·옆면 5칸 규모로 지붕은 옆면이 여덟 팔(八)자 모양인 팔작지붕이다. 모두 48개의 기둥으로 이루어졌으며 마루의 높이를 달리하는 입체적 평면을 하고 있다. 이름인 ‘경포대’ 전자체 현판은 유한지의 글씨이고, 해서체 현판은 이익회의 글씨이다.경포대 내부에는 숙종의 직접 지은 \'어제시\'와 율곡 이이가 10세에 지었다는 \'경포대부\'를 비롯해, 조하망의 상량문 등 수많은 명사와 시인묵객의 글이 게시돼 있다. 누각 주위에는 소나무와 상수리나무 들이 알맞게 우거져 운치있는 경관을 이루고 있다. 경포대와 주변호수는 1981년 강원 도립공원으로 지정됐고, 바닷가에는 유명한 경포해수욕장(길이 6km)이 있다.\n <br />* 시대 - 조선 중종3년(1508)<br>', '128.8965126086', '37.7955691591', 'http://tong.visitkorea.or.kr/cms/resource/86/2596286_image2_1.jpg', '0', '', '293634', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 2, 1, '광화문', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126512', '', '※ 19_20 한국관광 100선 ※<br /><br />광화문은 경복궁의 남문이며, 궁성의 정문이다. 광화문은 국왕이 드나드는 정문이기도 했지만, 조선의 법궁인 경복궁의 정문이었기 때문에 다른 궁궐의 정문에 비해 그 규모와 격식 면에서도 매우 웅장하고 화려했다. 또한 광화문은 담장 끝 동쪽과 서쪽에 각각 동십자각과 서십자각을 두어 조선의 5대 궁궐 가운데 유일하게 궐문형식을 갖추고 있기도 했다. 광화문은 중층으로 된 문루를 받치는 기단석축에 세 개의 홍예로 이루어져 있는데, 중앙의 홍예로는 왕이, 좌우의 홍예로는 왕세자와 신하들이 출입하도록 하였다. 문루(門樓)에는 종을 걸어 때를 알리는 데 사용하였다. 이처럼 경복궁이 조선왕조의 법궁이라는 점은 광화문을 비롯한 성문의 형태에서도 확인할 수 있다. 다른 궁의 문들은 낮은 단 위에 문을 세우지만 경복궁의 문은 돌로 높은 석축을 쌓고 중앙에 홍예문을 내서 마치 성곽의 문과 같은 모습을 취한다. 광화문은 그 중에서도 홍예문을 셋이나 내서 가장 격식 높은 모습을 보여준다. 광화문이 법궁의 정문이라는 사실을 잘 드러내준다. 경복궁 창건 당시에는 특별한 이름이 없이 궁제에 따라 \'오문(午門)\'으로 부르다가, 태조 3년(1395) 정도전에 의해 \'정문(正門)\'으로 이름을 바꾸지만, 세종 8년(1426)에 경복궁을 수리하면서 집현전에서 \'광화문(光化門)\'이라 이름을 지어 올리면서 지금의 광화문이란 이름을 얻게 되었다.<br /><br />원래 광화문은 조선총독부 건물을 지으면서 궁성의 동문인 건춘문 북쪽으로 옮겼으나 한국전쟁 때 폭격을 맞아 문루가 부서지고 석축은 탄흔 투성이가 되었다. 그런 가운데 광화문이 현재의 모습으로 재건된 것은 1968년 박정희 정권에 이르러서다. 파괴된 문루를 다시 짓고 광화문을 현재의 자리로 옮겨 놓았던 것이다.그러나 새로 재건한 광화문은 철근 콘크리트로 지어졌으며, 재건 당시 광화문의 축을 경복궁의 중심축에 맞춘 것이 아니라 당시 중앙청으로 쓰이던 구 조선총독부청사의 축에 맞추어, 그 결과 3.5도 가량 본래의 축과 어긋나게 틀어지고 말았다. 뿐만 아니라 원래의 광화문 자리에서도 14.5미터 가량 뒤로 물러나 지금의 자리에 서있게 되었다. 이렇게 원형을 잃어버린 광화문을 복원하고자 2006년부터 광화문 철거작업을 시작해 3년 8개월의 복원 공사를 마치고 2010년 8월 15일 완공되었다. 화강암으로 육중한 기단을 만들고 그 위에 이층의 문루를 얹어 멀리 조망하기 좋을 뿐 아니라 궁궐 정문으로서의 위엄을 나타내고 있다. 기단에 있는 3개의 아치형 출입문은 경복궁이 조선의 정궁이라는 위엄을 과시한다. 돌로 만든 기단 위에는 흙을 구워 벽돌처럼 만든 \'전돌\'로 나지막한 담을 둘렀는데, 여기를 장식한 팔괘문양은 조선조 궁궐의 특성을 잘 반영하고있다.<br>', '126.9768042386', '37.5760725520', 'http://tong.visitkorea.or.kr/cms/resource/81/1075281_image2_1.jpg', '0', '', '171089', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 2, 2, '경복궁', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126508', '', '※ 19_20 한국관광 100선 ※<br /><br />경복궁은 1395년 태조 이성계에 의해서 새로운 조선왕조의 법궁으로 지어졌다. 경복궁은 동궐(창덕궁)이나 서궐(경희궁)에 비해 위치가 북쪽에 있어 \'북궐\'이라 불리기도 했다. 경복궁(사적 제117호)은 5대 궁궐 가운데 으뜸의 규모와 건축미를 자랑한다. 경복궁 근정전에서 즉위식을 가진 왕들을 보면 제2대 정종, 제4대 세종, 제6대 단종, 제7대 세조, 제9대 성종, 제11대 중종, 제13대 명종 등이다. 경복궁은 임진왜란 때 상당수의 건물이 불타 없어진 아픔을 갖고 있으며, 고종 때에 흥선대원군의 주도 아래 7,700여칸에 이르는 건물들을 다시 세웠다. 그러나 또 다시 명성황후 시해사건이 일어나면서 왕조의 몰락과 함께 경복궁도 왕궁으로서의 기능을 상실하고 말았다. 경복궁에는 조선시대의 대표적인 건축물인 경회루와 향원정의 연못이 원형대로 남아 있으며, 근정전의 월대와 조각상들은 당시의 조각미술을 대표한다. 현재 흥례문 밖 서편에는 국립고궁 박물관이 위치하고 있고, 경복궁 내 향원정의 동편에는 국립민속 박물관이 위치하고 있다.<br /><br />* 주요문화재 <br />1) 경복궁(사적 제117호)<br />2) 경복궁 근정전(국보 제223호)<br />3) 경복궁 경회루(국보 제224호)<br />4) 경복궁 자경전(보물 제809호)<br />5) 경복궁 자경전 십장생 굴뚝(보물 제810호)<br />6) 경복궁 아미산굴뚝 (보물 제811호)<br />7) 경복궁 근정문 및 행각(보물 제812호)<br />8) 경복궁 풍기대(보물 제847호)<br>', '126.9769930325', '37.5788222356', 'http://tong.visitkorea.or.kr/cms/resource/40/1568040_image2_1.jpg', '0', '', '306', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 2, 3, '서귀포시', '12', NULL, '제주 서귀포시', '697-701', '125402', '', '2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와 남제주군이 통합되어 서귀포시로 새롭게 출발하였다. 대륙(러시아,중국)과 해양(일본,동남아)을\n 연결하는 요충지이며 천혜의 자연경관이 수려한 세계적인 휴양 관광지인 제주특별자치도의 남부지역이다.\n 전체 면적 870.68㎢이며 총인구 157,840명(2014년 9월 기준)에 이른다. \n 3읍 2면 12개 동으로 구성되어 있다.<br>\n <br>* 자연환경\n<br>\n 한라산(해발 1,950m)의 남쪽에 위치해 북고남저의 형태를 나타낸다. 한라산이 남쪽으로 치우쳐 있고 북사면보다 경사가 급해 산악 지세가 형성돼 있으며, 평야는\n 해안지대에 한정되어 분포한다.\n 또한 한라산을  주봉으로 하는 화산체의 경사면을 따라 법정악,미악, 각수암, 삼매봉 등 높고 낮은 기생화산들이 많이 솟아 있다.\n <br>이들 산간에서 발원한 효돈천, 정술내, 에이리내, 연외천, 악근천, 큰내, 창고천 \n 등 비교적 큰 하천들이 남쪽으로 흐르고 있으며 깊은 골짜기를 형성한 이들 하천은 하류에서 천지연, 정방, 천제연 등의 폭포를 이루고 바다로 흘러 들어간다.\n 온대권에 속하는 서귀포시는 대체로 온화한 편이나 강풍이 많고 돌발적 기상변화가 다발하고 있다.\n<br><br>\n\n * 역사\n <br>고대 - 탐라국의 일부로 삼한시대부터 중국 대륙 및 한반도와의 교역이 행해졌으며 498년(동성왕20)에 이르러 백제에 속하였다가, 백제가 멸망한 뒤 신라에 속하게 되었다.\n <br>고려 - 태조가 후삼국을 통일한 뒤 고려의 속국이 되었으며 1105년(숙종10)에 이르러 탐라군으로 개편되어 고려의 직접 관할에 들어갔다. 1271년 삼별초의 난 이후 원나라의 직속령이 되었으며, 1276년(충렬왕2) 원나라는  이곳에 목마장을 개설하여 망할 때까지 관할하였다. 1294년 고려에 환원되었고, 1367년(공민왕16)까지 여러 차례 귀속이 바뀌었다.  \n 1300년(충렬왕 26)에는 탐라현 산하에 14개 현촌이 설치 되었는데, 그 가운데 서귀포시에 해당되는 지역은 예재와 홍로이다.\n\n<br> 조선 - 1416년 한라산을 경계로 산남과 산북으로 나뉘었는데 산남의 정의현에 소속된 홍로와 대정현에 소속된 예재가 지금의 서귀포시 지역에 해당된다.  1608년(광해군1) 정의현은 동, 중, 서 3면, 대정현은 동, 서 2면으로 개편되었다. \n 1864년 (고종1) 정의현과 대정현은 군으로 승격돼  전라도 관찰사 관할하에 있다가 1880년 다시 현으로 환원되었다.\n <br>근대 - 1897년에는 정의현이 좌, 중, 우 3면으로, 대정현이 좌, 중 2개면으로 개편 되었으며, 1911년 정의현에 서중면을 증설함에 따라 그 동쪽을 동중면이라 하였고, 1913년에는 대정현에도 우면이 증설되었다. 1914년에는 정의, 대정, 두현이 제주군에 병합되었고, 이 때 정의현 좌면을 정의면, 대정현 우면을  대정면으로 개칭하였다. 1915년 도제 실시로  서귀는 제주도 우면, 중문은 좌면이 되었고,\n 1935년 서귀면과 중문면으로 개칭하게 되었다.\n<br>\n 현대 - 1946년 도제 실시로 남제주군  서귀면과 중문면이 되었다가, 1956년 서귀면이 읍으로 승격되었고, 1981년 7월 서귀읍과 중문면이 통합되어 시로 승격, 서귀포시가 되었다. 2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와\n 남제주군이 통합되어 서귀포시로 새롭게 출발되었다.<br> \n\n<bR>\n * 문화<br>\n 민속 - 집단놀이인 걸궁이 대표적 민속놀이인데 걸궁은 농악의 일종으로, 집집마다 돌며 굿을 쳐주며 잡귀를 쫓고 행운이 오게 하는 마을의 정월 놀이 행사이다.\n <br>동제 - 정월 첫 정일에 남성들이 주관하여 유교식으로 행하는 \'포제\' 와 정월 초하루에서 보름 사이에 여성들이 주관하여 심방을 불러행하는 \'당굿\' 이 있다.\n <br>', '126.5105853278', '33.2556182395', 'http://tong.visitkorea.or.kr/cms/resource/02/1941602_image2_1.jpg', '0', '', '481522', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 2, 4, '성산일출봉 [유네스코 세계자연유산]', '12', NULL, '제주특별자치도 서귀포시 성산읍 일출로 284-12', '63643', '126435', '', '해발 180m인 성산 일출봉은 약 5,000년 전 제주도 수많은 분화구 중에서는 드물게 바닷속에서 수중폭발한 화산체이다. 용암이 물에 섞일 때 일어나는 폭발로 용암은 고운  화산재로 부서져 분화구 둘레에 원뿔형으로 쌓여 있다. 원래는 화산섬이었지만 신양해수욕장 쪽 땅과 섬  사이에 모래와 자갈이 쌓여 육지와 연결이 된 것이다. 일출봉 정상에는 지름 600m, 바닥면의 높이 해발 90m에 면적이 214,400㎡(성산일출봉 분화구의 면적)나 되는 분화구가 자리한다. 이곳에서  이장호 감독의  영화 \'공포의 외인구단\'의 한 장면이 촬영되기도 했다. 농사를 짓기도  했는데 지금은 억새밭을 이루며 우도가 손에 잡힐 듯 보인다.<br /> 예로부터 이곳 성산일출봉 정상에서 바라보는 일출광경은 영주10경(제주의 경승지) 중에서 으뜸이라 하였다.  넘실대는 푸른 바다 저편 수평선에서 이글거리며 솟아오르는 일출은 온 바다를 물들이고 보는 이의 마음까지도 붙잡아 놓으며 보는 이로 하여금 저절로 감탄케 한다. 지방기념물로 관리하다 2000년 7월 19일 천연기념물로 지정된 곳이다.<br>', '126.9415156012', '33.4581111174', 'http://tong.visitkorea.or.kr/cms/resource/85/1876185_image2_1.jpg', '0', '', '45969', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 2, 5, '해운대해수욕장', '12', NULL, '부산광역시 해운대구 해운대해변로 264', '48100', '126081', '', '* 2019 해수욕장 개장정보<br />- 기간: 6.1.~8.31<br />- 시간: 09:00~18:00 / 18:00~21:00<br />- 야간개장: 7.26~8.11<br /><br />* 부산 대표 해수욕장, 해운대해수욕장<br /><br />부산의 대표 해수욕장인 해운대해수욕장. 백사장의 길이 1.5km, 너비 30~50m, 평균수심 1m, 면적 58,400㎥의 규모로 넓은 백사장과 아름다운 해안선을 자랑하고 있으며 얕은 수심과 잔잔한 물결로 해수욕장의 최적 조건을 갖추고 있다.\'부산\' 하면 제일 먼저 떠올리는 곳이 해운대 해수욕장이라고 할 만큼 부산을 대표하는 명소이며, 해마다 여름철 피서객을 가늠하는 척도로 이용될 만큼 국내 최대 인파가 몰리는 곳이기도 하다. 특히, 해안선 주변에 크고 작은 빌딩들과 고급 호텔들이 우뚝 솟아있어 현대적이고 세련된 분위기의 해수욕장으로 유명하기 때문에 여름 휴가철뿐만 아니라 사시사철 젊은 열기로 붐비고 해외 관광객들에게도 잘 알려져 있어 외국인들이 많이 찾는 곳이다.<br /><br />* 해운대해수욕장의 다양한 축제와 즐길거리<br /><br />해운대해수욕장에서는 매년 정월 대보름날의 달맞이 축제를 진행하고 있다. 또한, 매년 겨울 주최하고 있는 북극곰수영대회는 이미 겨울철 대표 축제로 자리잡았다. 이외에도 모래 작품전, 부산 바다 축제 등 각종 크고 작은 행사들이 열리고 있다. 또한, 해수욕장 주변에 동백섬, 오륙도, 아쿠아리움 , 요트경기장, 벡스코 달맞이고개, 드라이브코스 등 볼거리가 많으며, 국내 1급 해수욕장답게 주변에는 일급 호텔을 비롯한 숙박, 오락시설 및 유흥 시설들이 잘 정비되어 있어 편안한 휴식을 즐길 수 있다.<br>', '129.1603078991', '35.1591243474', 'http://tong.visitkorea.or.kr/cms/resource/67/2612467_image2_1.jpg', '0', '', '278087', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 3, 1, '광화문', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126512', '', '※ 19_20 한국관광 100선 ※<br /><br />광화문은 경복궁의 남문이며, 궁성의 정문이다. 광화문은 국왕이 드나드는 정문이기도 했지만, 조선의 법궁인 경복궁의 정문이었기 때문에 다른 궁궐의 정문에 비해 그 규모와 격식 면에서도 매우 웅장하고 화려했다. 또한 광화문은 담장 끝 동쪽과 서쪽에 각각 동십자각과 서십자각을 두어 조선의 5대 궁궐 가운데 유일하게 궐문형식을 갖추고 있기도 했다. 광화문은 중층으로 된 문루를 받치는 기단석축에 세 개의 홍예로 이루어져 있는데, 중앙의 홍예로는 왕이, 좌우의 홍예로는 왕세자와 신하들이 출입하도록 하였다. 문루(門樓)에는 종을 걸어 때를 알리는 데 사용하였다. 이처럼 경복궁이 조선왕조의 법궁이라는 점은 광화문을 비롯한 성문의 형태에서도 확인할 수 있다. 다른 궁의 문들은 낮은 단 위에 문을 세우지만 경복궁의 문은 돌로 높은 석축을 쌓고 중앙에 홍예문을 내서 마치 성곽의 문과 같은 모습을 취한다. 광화문은 그 중에서도 홍예문을 셋이나 내서 가장 격식 높은 모습을 보여준다. 광화문이 법궁의 정문이라는 사실을 잘 드러내준다. 경복궁 창건 당시에는 특별한 이름이 없이 궁제에 따라 \'오문(午門)\'으로 부르다가, 태조 3년(1395) 정도전에 의해 \'정문(正門)\'으로 이름을 바꾸지만, 세종 8년(1426)에 경복궁을 수리하면서 집현전에서 \'광화문(光化門)\'이라 이름을 지어 올리면서 지금의 광화문이란 이름을 얻게 되었다.<br /><br />원래 광화문은 조선총독부 건물을 지으면서 궁성의 동문인 건춘문 북쪽으로 옮겼으나 한국전쟁 때 폭격을 맞아 문루가 부서지고 석축은 탄흔 투성이가 되었다. 그런 가운데 광화문이 현재의 모습으로 재건된 것은 1968년 박정희 정권에 이르러서다. 파괴된 문루를 다시 짓고 광화문을 현재의 자리로 옮겨 놓았던 것이다.그러나 새로 재건한 광화문은 철근 콘크리트로 지어졌으며, 재건 당시 광화문의 축을 경복궁의 중심축에 맞춘 것이 아니라 당시 중앙청으로 쓰이던 구 조선총독부청사의 축에 맞추어, 그 결과 3.5도 가량 본래의 축과 어긋나게 틀어지고 말았다. 뿐만 아니라 원래의 광화문 자리에서도 14.5미터 가량 뒤로 물러나 지금의 자리에 서있게 되었다. 이렇게 원형을 잃어버린 광화문을 복원하고자 2006년부터 광화문 철거작업을 시작해 3년 8개월의 복원 공사를 마치고 2010년 8월 15일 완공되었다. 화강암으로 육중한 기단을 만들고 그 위에 이층의 문루를 얹어 멀리 조망하기 좋을 뿐 아니라 궁궐 정문으로서의 위엄을 나타내고 있다. 기단에 있는 3개의 아치형 출입문은 경복궁이 조선의 정궁이라는 위엄을 과시한다. 돌로 만든 기단 위에는 흙을 구워 벽돌처럼 만든 \'전돌\'로 나지막한 담을 둘렀는데, 여기를 장식한 팔괘문양은 조선조 궁궐의 특성을 잘 반영하고있다.<br>', '126.9768042386', '37.5760725520', 'http://tong.visitkorea.or.kr/cms/resource/81/1075281_image2_1.jpg', '0', '', '332185', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 3, 2, '경복궁', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126508', '', '※ 19_20 한국관광 100선 ※<br /><br />경복궁은 1395년 태조 이성계에 의해서 새로운 조선왕조의 법궁으로 지어졌다. 경복궁은 동궐(창덕궁)이나 서궐(경희궁)에 비해 위치가 북쪽에 있어 \'북궐\'이라 불리기도 했다. 경복궁(사적 제117호)은 5대 궁궐 가운데 으뜸의 규모와 건축미를 자랑한다. 경복궁 근정전에서 즉위식을 가진 왕들을 보면 제2대 정종, 제4대 세종, 제6대 단종, 제7대 세조, 제9대 성종, 제11대 중종, 제13대 명종 등이다. 경복궁은 임진왜란 때 상당수의 건물이 불타 없어진 아픔을 갖고 있으며, 고종 때에 흥선대원군의 주도 아래 7,700여칸에 이르는 건물들을 다시 세웠다. 그러나 또 다시 명성황후 시해사건이 일어나면서 왕조의 몰락과 함께 경복궁도 왕궁으로서의 기능을 상실하고 말았다. 경복궁에는 조선시대의 대표적인 건축물인 경회루와 향원정의 연못이 원형대로 남아 있으며, 근정전의 월대와 조각상들은 당시의 조각미술을 대표한다. 현재 흥례문 밖 서편에는 국립고궁 박물관이 위치하고 있고, 경복궁 내 향원정의 동편에는 국립민속 박물관이 위치하고 있다.<br /><br />* 주요문화재 <br />1) 경복궁(사적 제117호)<br />2) 경복궁 근정전(국보 제223호)<br />3) 경복궁 경회루(국보 제224호)<br />4) 경복궁 자경전(보물 제809호)<br />5) 경복궁 자경전 십장생 굴뚝(보물 제810호)<br />6) 경복궁 아미산굴뚝 (보물 제811호)<br />7) 경복궁 근정문 및 행각(보물 제812호)<br />8) 경복궁 풍기대(보물 제847호)<br>', '126.9769930325', '37.5788222356', 'http://tong.visitkorea.or.kr/cms/resource/40/1568040_image2_1.jpg', '0', '', '306', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 3, 3, '서귀포시', '12', NULL, '제주 서귀포시', '697-701', '125402', '', '2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와 남제주군이 통합되어 서귀포시로 새롭게 출발하였다. 대륙(러시아,중국)과 해양(일본,동남아)을\n 연결하는 요충지이며 천혜의 자연경관이 수려한 세계적인 휴양 관광지인 제주특별자치도의 남부지역이다.\n 전체 면적 870.68㎢이며 총인구 157,840명(2014년 9월 기준)에 이른다. \n 3읍 2면 12개 동으로 구성되어 있다.<br>\n <br>* 자연환경\n<br>\n 한라산(해발 1,950m)의 남쪽에 위치해 북고남저의 형태를 나타낸다. 한라산이 남쪽으로 치우쳐 있고 북사면보다 경사가 급해 산악 지세가 형성돼 있으며, 평야는\n 해안지대에 한정되어 분포한다.\n 또한 한라산을  주봉으로 하는 화산체의 경사면을 따라 법정악,미악, 각수암, 삼매봉 등 높고 낮은 기생화산들이 많이 솟아 있다.\n <br>이들 산간에서 발원한 효돈천, 정술내, 에이리내, 연외천, 악근천, 큰내, 창고천 \n 등 비교적 큰 하천들이 남쪽으로 흐르고 있으며 깊은 골짜기를 형성한 이들 하천은 하류에서 천지연, 정방, 천제연 등의 폭포를 이루고 바다로 흘러 들어간다.\n 온대권에 속하는 서귀포시는 대체로 온화한 편이나 강풍이 많고 돌발적 기상변화가 다발하고 있다.\n<br><br>\n\n * 역사\n <br>고대 - 탐라국의 일부로 삼한시대부터 중국 대륙 및 한반도와의 교역이 행해졌으며 498년(동성왕20)에 이르러 백제에 속하였다가, 백제가 멸망한 뒤 신라에 속하게 되었다.\n <br>고려 - 태조가 후삼국을 통일한 뒤 고려의 속국이 되었으며 1105년(숙종10)에 이르러 탐라군으로 개편되어 고려의 직접 관할에 들어갔다. 1271년 삼별초의 난 이후 원나라의 직속령이 되었으며, 1276년(충렬왕2) 원나라는  이곳에 목마장을 개설하여 망할 때까지 관할하였다. 1294년 고려에 환원되었고, 1367년(공민왕16)까지 여러 차례 귀속이 바뀌었다.  \n 1300년(충렬왕 26)에는 탐라현 산하에 14개 현촌이 설치 되었는데, 그 가운데 서귀포시에 해당되는 지역은 예재와 홍로이다.\n\n<br> 조선 - 1416년 한라산을 경계로 산남과 산북으로 나뉘었는데 산남의 정의현에 소속된 홍로와 대정현에 소속된 예재가 지금의 서귀포시 지역에 해당된다.  1608년(광해군1) 정의현은 동, 중, 서 3면, 대정현은 동, 서 2면으로 개편되었다. \n 1864년 (고종1) 정의현과 대정현은 군으로 승격돼  전라도 관찰사 관할하에 있다가 1880년 다시 현으로 환원되었다.\n <br>근대 - 1897년에는 정의현이 좌, 중, 우 3면으로, 대정현이 좌, 중 2개면으로 개편 되었으며, 1911년 정의현에 서중면을 증설함에 따라 그 동쪽을 동중면이라 하였고, 1913년에는 대정현에도 우면이 증설되었다. 1914년에는 정의, 대정, 두현이 제주군에 병합되었고, 이 때 정의현 좌면을 정의면, 대정현 우면을  대정면으로 개칭하였다. 1915년 도제 실시로  서귀는 제주도 우면, 중문은 좌면이 되었고,\n 1935년 서귀면과 중문면으로 개칭하게 되었다.\n<br>\n 현대 - 1946년 도제 실시로 남제주군  서귀면과 중문면이 되었다가, 1956년 서귀면이 읍으로 승격되었고, 1981년 7월 서귀읍과 중문면이 통합되어 시로 승격, 서귀포시가 되었다. 2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와\n 남제주군이 통합되어 서귀포시로 새롭게 출발되었다.<br> \n\n<bR>\n * 문화<br>\n 민속 - 집단놀이인 걸궁이 대표적 민속놀이인데 걸궁은 농악의 일종으로, 집집마다 돌며 굿을 쳐주며 잡귀를 쫓고 행운이 오게 하는 마을의 정월 놀이 행사이다.\n <br>동제 - 정월 첫 정일에 남성들이 주관하여 유교식으로 행하는 \'포제\' 와 정월 초하루에서 보름 사이에 여성들이 주관하여 심방을 불러행하는 \'당굿\' 이 있다.\n <br>', '126.5105853278', '33.2556182395', 'http://tong.visitkorea.or.kr/cms/resource/02/1941602_image2_1.jpg', '0', '', '481522', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 3, 4, '성산일출봉 [유네스코 세계자연유산]', '12', NULL, '제주특별자치도 서귀포시 성산읍 일출로 284-12', '63643', '126435', '', '해발 180m인 성산 일출봉은 약 5,000년 전 제주도 수많은 분화구 중에서는 드물게 바닷속에서 수중폭발한 화산체이다. 용암이 물에 섞일 때 일어나는 폭발로 용암은 고운  화산재로 부서져 분화구 둘레에 원뿔형으로 쌓여 있다. 원래는 화산섬이었지만 신양해수욕장 쪽 땅과 섬  사이에 모래와 자갈이 쌓여 육지와 연결이 된 것이다. 일출봉 정상에는 지름 600m, 바닥면의 높이 해발 90m에 면적이 214,400㎡(성산일출봉 분화구의 면적)나 되는 분화구가 자리한다. 이곳에서  이장호 감독의  영화 \'공포의 외인구단\'의 한 장면이 촬영되기도 했다. 농사를 짓기도  했는데 지금은 억새밭을 이루며 우도가 손에 잡힐 듯 보인다.<br /> 예로부터 이곳 성산일출봉 정상에서 바라보는 일출광경은 영주10경(제주의 경승지) 중에서 으뜸이라 하였다.  넘실대는 푸른 바다 저편 수평선에서 이글거리며 솟아오르는 일출은 온 바다를 물들이고 보는 이의 마음까지도 붙잡아 놓으며 보는 이로 하여금 저절로 감탄케 한다. 지방기념물로 관리하다 2000년 7월 19일 천연기념물로 지정된 곳이다.<br>', '126.9415156012', '33.4581111174', 'http://tong.visitkorea.or.kr/cms/resource/85/1876185_image2_1.jpg', '0', '', '45969', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 3, 5, '해운대해수욕장', '12', NULL, '부산광역시 해운대구 해운대해변로 264', '48100', '126081', '', '* 2019 해수욕장 개장정보<br />- 기간: 6.1.~8.31<br />- 시간: 09:00~18:00 / 18:00~21:00<br />- 야간개장: 7.26~8.11<br /><br />* 부산 대표 해수욕장, 해운대해수욕장<br /><br />부산의 대표 해수욕장인 해운대해수욕장. 백사장의 길이 1.5km, 너비 30~50m, 평균수심 1m, 면적 58,400㎥의 규모로 넓은 백사장과 아름다운 해안선을 자랑하고 있으며 얕은 수심과 잔잔한 물결로 해수욕장의 최적 조건을 갖추고 있다.\'부산\' 하면 제일 먼저 떠올리는 곳이 해운대 해수욕장이라고 할 만큼 부산을 대표하는 명소이며, 해마다 여름철 피서객을 가늠하는 척도로 이용될 만큼 국내 최대 인파가 몰리는 곳이기도 하다. 특히, 해안선 주변에 크고 작은 빌딩들과 고급 호텔들이 우뚝 솟아있어 현대적이고 세련된 분위기의 해수욕장으로 유명하기 때문에 여름 휴가철뿐만 아니라 사시사철 젊은 열기로 붐비고 해외 관광객들에게도 잘 알려져 있어 외국인들이 많이 찾는 곳이다.<br /><br />* 해운대해수욕장의 다양한 축제와 즐길거리<br /><br />해운대해수욕장에서는 매년 정월 대보름날의 달맞이 축제를 진행하고 있다. 또한, 매년 겨울 주최하고 있는 북극곰수영대회는 이미 겨울철 대표 축제로 자리잡았다. 이외에도 모래 작품전, 부산 바다 축제 등 각종 크고 작은 행사들이 열리고 있다. 또한, 해수욕장 주변에 동백섬, 오륙도, 아쿠아리움 , 요트경기장, 벡스코 달맞이고개, 드라이브코스 등 볼거리가 많으며, 국내 1급 해수욕장답게 주변에는 일급 호텔을 비롯한 숙박, 오락시설 및 유흥 시설들이 잘 정비되어 있어 편안한 휴식을 즐길 수 있다.<br>', '129.1603078991', '35.1591243474', 'http://tong.visitkorea.or.kr/cms/resource/67/2612467_image2_1.jpg', '0', '', '278087', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 3, 6, '강릉 경포대', '12', NULL, '강원도 강릉시 경포로 365', '25461', '125790', '', '경포대는 관동팔경의 하나로 경포호수 북쪽 언덕에 있는 누각이다. 고려 충숙왕 13년(1326)에 강원도의 한 관리였던 박숙정이 당시 방해정 뒷산 인월사 옛터에 세웠던 것을 조선 중종 3년(1508)에 강릉부사 한급이 지금의 자리에 옮겼고, 여러 차례의 중수 끝에 현재의 모습을 갖추었다. 앞면 5칸·옆면 5칸 규모로 지붕은 옆면이 여덟 팔(八)자 모양인 팔작지붕이다. 모두 48개의 기둥으로 이루어졌으며 마루의 높이를 달리하는 입체적 평면을 하고 있다. 이름인 ‘경포대’ 전자체 현판은 유한지의 글씨이고, 해서체 현판은 이익회의 글씨이다.경포대 내부에는 숙종의 직접 지은 \'어제시\'와 율곡 이이가 10세에 지었다는 \'경포대부\'를 비롯해, 조하망의 상량문 등 수많은 명사와 시인묵객의 글이 게시돼 있다. 누각 주위에는 소나무와 상수리나무 들이 알맞게 우거져 운치있는 경관을 이루고 있다. 경포대와 주변호수는 1981년 강원 도립공원으로 지정됐고, 바닷가에는 유명한 경포해수욕장(길이 6km)이 있다.\n <br />* 시대 - 조선 중종3년(1508)<br>', '128.8965126086', '37.7955691591', 'http://tong.visitkorea.or.kr/cms/resource/86/2596286_image2_1.jpg', '0', '', '293634', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 4, 1, '광화문', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126512', '', '※ 19_20 한국관광 100선 ※<br /><br />광화문은 경복궁의 남문이며, 궁성의 정문이다. 광화문은 국왕이 드나드는 정문이기도 했지만, 조선의 법궁인 경복궁의 정문이었기 때문에 다른 궁궐의 정문에 비해 그 규모와 격식 면에서도 매우 웅장하고 화려했다. 또한 광화문은 담장 끝 동쪽과 서쪽에 각각 동십자각과 서십자각을 두어 조선의 5대 궁궐 가운데 유일하게 궐문형식을 갖추고 있기도 했다. 광화문은 중층으로 된 문루를 받치는 기단석축에 세 개의 홍예로 이루어져 있는데, 중앙의 홍예로는 왕이, 좌우의 홍예로는 왕세자와 신하들이 출입하도록 하였다. 문루(門樓)에는 종을 걸어 때를 알리는 데 사용하였다. 이처럼 경복궁이 조선왕조의 법궁이라는 점은 광화문을 비롯한 성문의 형태에서도 확인할 수 있다. 다른 궁의 문들은 낮은 단 위에 문을 세우지만 경복궁의 문은 돌로 높은 석축을 쌓고 중앙에 홍예문을 내서 마치 성곽의 문과 같은 모습을 취한다. 광화문은 그 중에서도 홍예문을 셋이나 내서 가장 격식 높은 모습을 보여준다. 광화문이 법궁의 정문이라는 사실을 잘 드러내준다. 경복궁 창건 당시에는 특별한 이름이 없이 궁제에 따라 \'오문(午門)\'으로 부르다가, 태조 3년(1395) 정도전에 의해 \'정문(正門)\'으로 이름을 바꾸지만, 세종 8년(1426)에 경복궁을 수리하면서 집현전에서 \'광화문(光化門)\'이라 이름을 지어 올리면서 지금의 광화문이란 이름을 얻게 되었다.<br /><br />원래 광화문은 조선총독부 건물을 지으면서 궁성의 동문인 건춘문 북쪽으로 옮겼으나 한국전쟁 때 폭격을 맞아 문루가 부서지고 석축은 탄흔 투성이가 되었다. 그런 가운데 광화문이 현재의 모습으로 재건된 것은 1968년 박정희 정권에 이르러서다. 파괴된 문루를 다시 짓고 광화문을 현재의 자리로 옮겨 놓았던 것이다.그러나 새로 재건한 광화문은 철근 콘크리트로 지어졌으며, 재건 당시 광화문의 축을 경복궁의 중심축에 맞춘 것이 아니라 당시 중앙청으로 쓰이던 구 조선총독부청사의 축에 맞추어, 그 결과 3.5도 가량 본래의 축과 어긋나게 틀어지고 말았다. 뿐만 아니라 원래의 광화문 자리에서도 14.5미터 가량 뒤로 물러나 지금의 자리에 서있게 되었다. 이렇게 원형을 잃어버린 광화문을 복원하고자 2006년부터 광화문 철거작업을 시작해 3년 8개월의 복원 공사를 마치고 2010년 8월 15일 완공되었다. 화강암으로 육중한 기단을 만들고 그 위에 이층의 문루를 얹어 멀리 조망하기 좋을 뿐 아니라 궁궐 정문으로서의 위엄을 나타내고 있다. 기단에 있는 3개의 아치형 출입문은 경복궁이 조선의 정궁이라는 위엄을 과시한다. 돌로 만든 기단 위에는 흙을 구워 벽돌처럼 만든 \'전돌\'로 나지막한 담을 둘렀는데, 여기를 장식한 팔괘문양은 조선조 궁궐의 특성을 잘 반영하고있다.<br>', '126.9768042386', '37.5760725520', 'http://tong.visitkorea.or.kr/cms/resource/81/1075281_image2_1.jpg', '0', '', '171089', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 4, 2, '경복궁', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126508', '', '※ 19_20 한국관광 100선 ※<br /><br />경복궁은 1395년 태조 이성계에 의해서 새로운 조선왕조의 법궁으로 지어졌다. 경복궁은 동궐(창덕궁)이나 서궐(경희궁)에 비해 위치가 북쪽에 있어 \'북궐\'이라 불리기도 했다. 경복궁(사적 제117호)은 5대 궁궐 가운데 으뜸의 규모와 건축미를 자랑한다. 경복궁 근정전에서 즉위식을 가진 왕들을 보면 제2대 정종, 제4대 세종, 제6대 단종, 제7대 세조, 제9대 성종, 제11대 중종, 제13대 명종 등이다. 경복궁은 임진왜란 때 상당수의 건물이 불타 없어진 아픔을 갖고 있으며, 고종 때에 흥선대원군의 주도 아래 7,700여칸에 이르는 건물들을 다시 세웠다. 그러나 또 다시 명성황후 시해사건이 일어나면서 왕조의 몰락과 함께 경복궁도 왕궁으로서의 기능을 상실하고 말았다. 경복궁에는 조선시대의 대표적인 건축물인 경회루와 향원정의 연못이 원형대로 남아 있으며, 근정전의 월대와 조각상들은 당시의 조각미술을 대표한다. 현재 흥례문 밖 서편에는 국립고궁 박물관이 위치하고 있고, 경복궁 내 향원정의 동편에는 국립민속 박물관이 위치하고 있다.<br /><br />* 주요문화재 <br />1) 경복궁(사적 제117호)<br />2) 경복궁 근정전(국보 제223호)<br />3) 경복궁 경회루(국보 제224호)<br />4) 경복궁 자경전(보물 제809호)<br />5) 경복궁 자경전 십장생 굴뚝(보물 제810호)<br />6) 경복궁 아미산굴뚝 (보물 제811호)<br />7) 경복궁 근정문 및 행각(보물 제812호)<br />8) 경복궁 풍기대(보물 제847호)<br>', '126.9769930325', '37.5788222356', 'http://tong.visitkorea.or.kr/cms/resource/40/1568040_image2_1.jpg', '0', '', '306', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 4, 3, '서귀포시', '12', NULL, '제주 서귀포시', '697-701', '125402', '', '2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와 남제주군이 통합되어 서귀포시로 새롭게 출발하였다. 대륙(러시아,중국)과 해양(일본,동남아)을\n 연결하는 요충지이며 천혜의 자연경관이 수려한 세계적인 휴양 관광지인 제주특별자치도의 남부지역이다.\n 전체 면적 870.68㎢이며 총인구 157,840명(2014년 9월 기준)에 이른다. \n 3읍 2면 12개 동으로 구성되어 있다.<br>\n <br>* 자연환경\n<br>\n 한라산(해발 1,950m)의 남쪽에 위치해 북고남저의 형태를 나타낸다. 한라산이 남쪽으로 치우쳐 있고 북사면보다 경사가 급해 산악 지세가 형성돼 있으며, 평야는\n 해안지대에 한정되어 분포한다.\n 또한 한라산을  주봉으로 하는 화산체의 경사면을 따라 법정악,미악, 각수암, 삼매봉 등 높고 낮은 기생화산들이 많이 솟아 있다.\n <br>이들 산간에서 발원한 효돈천, 정술내, 에이리내, 연외천, 악근천, 큰내, 창고천 \n 등 비교적 큰 하천들이 남쪽으로 흐르고 있으며 깊은 골짜기를 형성한 이들 하천은 하류에서 천지연, 정방, 천제연 등의 폭포를 이루고 바다로 흘러 들어간다.\n 온대권에 속하는 서귀포시는 대체로 온화한 편이나 강풍이 많고 돌발적 기상변화가 다발하고 있다.\n<br><br>\n\n * 역사\n <br>고대 - 탐라국의 일부로 삼한시대부터 중국 대륙 및 한반도와의 교역이 행해졌으며 498년(동성왕20)에 이르러 백제에 속하였다가, 백제가 멸망한 뒤 신라에 속하게 되었다.\n <br>고려 - 태조가 후삼국을 통일한 뒤 고려의 속국이 되었으며 1105년(숙종10)에 이르러 탐라군으로 개편되어 고려의 직접 관할에 들어갔다. 1271년 삼별초의 난 이후 원나라의 직속령이 되었으며, 1276년(충렬왕2) 원나라는  이곳에 목마장을 개설하여 망할 때까지 관할하였다. 1294년 고려에 환원되었고, 1367년(공민왕16)까지 여러 차례 귀속이 바뀌었다.  \n 1300년(충렬왕 26)에는 탐라현 산하에 14개 현촌이 설치 되었는데, 그 가운데 서귀포시에 해당되는 지역은 예재와 홍로이다.\n\n<br> 조선 - 1416년 한라산을 경계로 산남과 산북으로 나뉘었는데 산남의 정의현에 소속된 홍로와 대정현에 소속된 예재가 지금의 서귀포시 지역에 해당된다.  1608년(광해군1) 정의현은 동, 중, 서 3면, 대정현은 동, 서 2면으로 개편되었다. \n 1864년 (고종1) 정의현과 대정현은 군으로 승격돼  전라도 관찰사 관할하에 있다가 1880년 다시 현으로 환원되었다.\n <br>근대 - 1897년에는 정의현이 좌, 중, 우 3면으로, 대정현이 좌, 중 2개면으로 개편 되었으며, 1911년 정의현에 서중면을 증설함에 따라 그 동쪽을 동중면이라 하였고, 1913년에는 대정현에도 우면이 증설되었다. 1914년에는 정의, 대정, 두현이 제주군에 병합되었고, 이 때 정의현 좌면을 정의면, 대정현 우면을  대정면으로 개칭하였다. 1915년 도제 실시로  서귀는 제주도 우면, 중문은 좌면이 되었고,\n 1935년 서귀면과 중문면으로 개칭하게 되었다.\n<br>\n 현대 - 1946년 도제 실시로 남제주군  서귀면과 중문면이 되었다가, 1956년 서귀면이 읍으로 승격되었고, 1981년 7월 서귀읍과 중문면이 통합되어 시로 승격, 서귀포시가 되었다. 2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와\n 남제주군이 통합되어 서귀포시로 새롭게 출발되었다.<br> \n\n<bR>\n * 문화<br>\n 민속 - 집단놀이인 걸궁이 대표적 민속놀이인데 걸궁은 농악의 일종으로, 집집마다 돌며 굿을 쳐주며 잡귀를 쫓고 행운이 오게 하는 마을의 정월 놀이 행사이다.\n <br>동제 - 정월 첫 정일에 남성들이 주관하여 유교식으로 행하는 \'포제\' 와 정월 초하루에서 보름 사이에 여성들이 주관하여 심방을 불러행하는 \'당굿\' 이 있다.\n <br>', '126.5105853278', '33.2556182395', 'http://tong.visitkorea.or.kr/cms/resource/02/1941602_image2_1.jpg', '0', '', '481522', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 4, 4, '성산일출봉 [유네스코 세계자연유산]', '12', NULL, '제주특별자치도 서귀포시 성산읍 일출로 284-12', '63643', '126435', '', '해발 180m인 성산 일출봉은 약 5,000년 전 제주도 수많은 분화구 중에서는 드물게 바닷속에서 수중폭발한 화산체이다. 용암이 물에 섞일 때 일어나는 폭발로 용암은 고운  화산재로 부서져 분화구 둘레에 원뿔형으로 쌓여 있다. 원래는 화산섬이었지만 신양해수욕장 쪽 땅과 섬  사이에 모래와 자갈이 쌓여 육지와 연결이 된 것이다. 일출봉 정상에는 지름 600m, 바닥면의 높이 해발 90m에 면적이 214,400㎡(성산일출봉 분화구의 면적)나 되는 분화구가 자리한다. 이곳에서  이장호 감독의  영화 \'공포의 외인구단\'의 한 장면이 촬영되기도 했다. 농사를 짓기도  했는데 지금은 억새밭을 이루며 우도가 손에 잡힐 듯 보인다.<br /> 예로부터 이곳 성산일출봉 정상에서 바라보는 일출광경은 영주10경(제주의 경승지) 중에서 으뜸이라 하였다.  넘실대는 푸른 바다 저편 수평선에서 이글거리며 솟아오르는 일출은 온 바다를 물들이고 보는 이의 마음까지도 붙잡아 놓으며 보는 이로 하여금 저절로 감탄케 한다. 지방기념물로 관리하다 2000년 7월 19일 천연기념물로 지정된 곳이다.<br>', '126.9415156012', '33.4581111174', 'http://tong.visitkorea.or.kr/cms/resource/85/1876185_image2_1.jpg', '0', '', '45969', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 4, 5, '해운대해수욕장', '12', NULL, '부산광역시 해운대구 해운대해변로 264', '48100', '126081', '', '* 2019 해수욕장 개장정보<br />- 기간: 6.1.~8.31<br />- 시간: 09:00~18:00 / 18:00~21:00<br />- 야간개장: 7.26~8.11<br /><br />* 부산 대표 해수욕장, 해운대해수욕장<br /><br />부산의 대표 해수욕장인 해운대해수욕장. 백사장의 길이 1.5km, 너비 30~50m, 평균수심 1m, 면적 58,400㎥의 규모로 넓은 백사장과 아름다운 해안선을 자랑하고 있으며 얕은 수심과 잔잔한 물결로 해수욕장의 최적 조건을 갖추고 있다.\'부산\' 하면 제일 먼저 떠올리는 곳이 해운대 해수욕장이라고 할 만큼 부산을 대표하는 명소이며, 해마다 여름철 피서객을 가늠하는 척도로 이용될 만큼 국내 최대 인파가 몰리는 곳이기도 하다. 특히, 해안선 주변에 크고 작은 빌딩들과 고급 호텔들이 우뚝 솟아있어 현대적이고 세련된 분위기의 해수욕장으로 유명하기 때문에 여름 휴가철뿐만 아니라 사시사철 젊은 열기로 붐비고 해외 관광객들에게도 잘 알려져 있어 외국인들이 많이 찾는 곳이다.<br /><br />* 해운대해수욕장의 다양한 축제와 즐길거리<br /><br />해운대해수욕장에서는 매년 정월 대보름날의 달맞이 축제를 진행하고 있다. 또한, 매년 겨울 주최하고 있는 북극곰수영대회는 이미 겨울철 대표 축제로 자리잡았다. 이외에도 모래 작품전, 부산 바다 축제 등 각종 크고 작은 행사들이 열리고 있다. 또한, 해수욕장 주변에 동백섬, 오륙도, 아쿠아리움 , 요트경기장, 벡스코 달맞이고개, 드라이브코스 등 볼거리가 많으며, 국내 1급 해수욕장답게 주변에는 일급 호텔을 비롯한 숙박, 오락시설 및 유흥 시설들이 잘 정비되어 있어 편안한 휴식을 즐길 수 있다.<br>', '129.1603078991', '35.1591243474', 'http://tong.visitkorea.or.kr/cms/resource/67/2612467_image2_1.jpg', '0', '', '278087', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 4, 6, '강릉 경포대', '12', NULL, '강원도 강릉시 경포로 365', '25461', '125790', '', '경포대는 관동팔경의 하나로 경포호수 북쪽 언덕에 있는 누각이다. 고려 충숙왕 13년(1326)에 강원도의 한 관리였던 박숙정이 당시 방해정 뒷산 인월사 옛터에 세웠던 것을 조선 중종 3년(1508)에 강릉부사 한급이 지금의 자리에 옮겼고, 여러 차례의 중수 끝에 현재의 모습을 갖추었다. 앞면 5칸·옆면 5칸 규모로 지붕은 옆면이 여덟 팔(八)자 모양인 팔작지붕이다. 모두 48개의 기둥으로 이루어졌으며 마루의 높이를 달리하는 입체적 평면을 하고 있다. 이름인 ‘경포대’ 전자체 현판은 유한지의 글씨이고, 해서체 현판은 이익회의 글씨이다.경포대 내부에는 숙종의 직접 지은 \'어제시\'와 율곡 이이가 10세에 지었다는 \'경포대부\'를 비롯해, 조하망의 상량문 등 수많은 명사와 시인묵객의 글이 게시돼 있다. 누각 주위에는 소나무와 상수리나무 들이 알맞게 우거져 운치있는 경관을 이루고 있다. 경포대와 주변호수는 1981년 강원 도립공원으로 지정됐고, 바닷가에는 유명한 경포해수욕장(길이 6km)이 있다.\n <br />* 시대 - 조선 중종3년(1508)<br>', '128.8965126086', '37.7955691591', 'http://tong.visitkorea.or.kr/cms/resource/86/2596286_image2_1.jpg', '0', '', '293634', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 5, 1, '광화문', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126512', '', '※ 19_20 한국관광 100선 ※<br /><br />광화문은 경복궁의 남문이며, 궁성의 정문이다. 광화문은 국왕이 드나드는 정문이기도 했지만, 조선의 법궁인 경복궁의 정문이었기 때문에 다른 궁궐의 정문에 비해 그 규모와 격식 면에서도 매우 웅장하고 화려했다. 또한 광화문은 담장 끝 동쪽과 서쪽에 각각 동십자각과 서십자각을 두어 조선의 5대 궁궐 가운데 유일하게 궐문형식을 갖추고 있기도 했다. 광화문은 중층으로 된 문루를 받치는 기단석축에 세 개의 홍예로 이루어져 있는데, 중앙의 홍예로는 왕이, 좌우의 홍예로는 왕세자와 신하들이 출입하도록 하였다. 문루(門樓)에는 종을 걸어 때를 알리는 데 사용하였다. 이처럼 경복궁이 조선왕조의 법궁이라는 점은 광화문을 비롯한 성문의 형태에서도 확인할 수 있다. 다른 궁의 문들은 낮은 단 위에 문을 세우지만 경복궁의 문은 돌로 높은 석축을 쌓고 중앙에 홍예문을 내서 마치 성곽의 문과 같은 모습을 취한다. 광화문은 그 중에서도 홍예문을 셋이나 내서 가장 격식 높은 모습을 보여준다. 광화문이 법궁의 정문이라는 사실을 잘 드러내준다. 경복궁 창건 당시에는 특별한 이름이 없이 궁제에 따라 \'오문(午門)\'으로 부르다가, 태조 3년(1395) 정도전에 의해 \'정문(正門)\'으로 이름을 바꾸지만, 세종 8년(1426)에 경복궁을 수리하면서 집현전에서 \'광화문(光化門)\'이라 이름을 지어 올리면서 지금의 광화문이란 이름을 얻게 되었다.<br /><br />원래 광화문은 조선총독부 건물을 지으면서 궁성의 동문인 건춘문 북쪽으로 옮겼으나 한국전쟁 때 폭격을 맞아 문루가 부서지고 석축은 탄흔 투성이가 되었다. 그런 가운데 광화문이 현재의 모습으로 재건된 것은 1968년 박정희 정권에 이르러서다. 파괴된 문루를 다시 짓고 광화문을 현재의 자리로 옮겨 놓았던 것이다.그러나 새로 재건한 광화문은 철근 콘크리트로 지어졌으며, 재건 당시 광화문의 축을 경복궁의 중심축에 맞춘 것이 아니라 당시 중앙청으로 쓰이던 구 조선총독부청사의 축에 맞추어, 그 결과 3.5도 가량 본래의 축과 어긋나게 틀어지고 말았다. 뿐만 아니라 원래의 광화문 자리에서도 14.5미터 가량 뒤로 물러나 지금의 자리에 서있게 되었다. 이렇게 원형을 잃어버린 광화문을 복원하고자 2006년부터 광화문 철거작업을 시작해 3년 8개월의 복원 공사를 마치고 2010년 8월 15일 완공되었다. 화강암으로 육중한 기단을 만들고 그 위에 이층의 문루를 얹어 멀리 조망하기 좋을 뿐 아니라 궁궐 정문으로서의 위엄을 나타내고 있다. 기단에 있는 3개의 아치형 출입문은 경복궁이 조선의 정궁이라는 위엄을 과시한다. 돌로 만든 기단 위에는 흙을 구워 벽돌처럼 만든 \'전돌\'로 나지막한 담을 둘렀는데, 여기를 장식한 팔괘문양은 조선조 궁궐의 특성을 잘 반영하고있다.<br>', '126.9768042386', '37.5760725520', 'http://tong.visitkorea.or.kr/cms/resource/81/1075281_image2_1.jpg', '0', '', '171089', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 5, 2, '경복궁', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126508', '', '※ 19_20 한국관광 100선 ※<br /><br />경복궁은 1395년 태조 이성계에 의해서 새로운 조선왕조의 법궁으로 지어졌다. 경복궁은 동궐(창덕궁)이나 서궐(경희궁)에 비해 위치가 북쪽에 있어 \'북궐\'이라 불리기도 했다. 경복궁(사적 제117호)은 5대 궁궐 가운데 으뜸의 규모와 건축미를 자랑한다. 경복궁 근정전에서 즉위식을 가진 왕들을 보면 제2대 정종, 제4대 세종, 제6대 단종, 제7대 세조, 제9대 성종, 제11대 중종, 제13대 명종 등이다. 경복궁은 임진왜란 때 상당수의 건물이 불타 없어진 아픔을 갖고 있으며, 고종 때에 흥선대원군의 주도 아래 7,700여칸에 이르는 건물들을 다시 세웠다. 그러나 또 다시 명성황후 시해사건이 일어나면서 왕조의 몰락과 함께 경복궁도 왕궁으로서의 기능을 상실하고 말았다. 경복궁에는 조선시대의 대표적인 건축물인 경회루와 향원정의 연못이 원형대로 남아 있으며, 근정전의 월대와 조각상들은 당시의 조각미술을 대표한다. 현재 흥례문 밖 서편에는 국립고궁 박물관이 위치하고 있고, 경복궁 내 향원정의 동편에는 국립민속 박물관이 위치하고 있다.<br /><br />* 주요문화재 <br />1) 경복궁(사적 제117호)<br />2) 경복궁 근정전(국보 제223호)<br />3) 경복궁 경회루(국보 제224호)<br />4) 경복궁 자경전(보물 제809호)<br />5) 경복궁 자경전 십장생 굴뚝(보물 제810호)<br />6) 경복궁 아미산굴뚝 (보물 제811호)<br />7) 경복궁 근정문 및 행각(보물 제812호)<br />8) 경복궁 풍기대(보물 제847호)<br>', '126.9769930325', '37.5788222356', 'http://tong.visitkorea.or.kr/cms/resource/40/1568040_image2_1.jpg', '0', '', '306', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 5, 3, '서귀포시', '12', NULL, '제주 서귀포시', '697-701', '125402', '', '2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와 남제주군이 통합되어 서귀포시로 새롭게 출발하였다. 대륙(러시아,중국)과 해양(일본,동남아)을\n 연결하는 요충지이며 천혜의 자연경관이 수려한 세계적인 휴양 관광지인 제주특별자치도의 남부지역이다.\n 전체 면적 870.68㎢이며 총인구 157,840명(2014년 9월 기준)에 이른다. \n 3읍 2면 12개 동으로 구성되어 있다.<br>\n <br>* 자연환경\n<br>\n 한라산(해발 1,950m)의 남쪽에 위치해 북고남저의 형태를 나타낸다. 한라산이 남쪽으로 치우쳐 있고 북사면보다 경사가 급해 산악 지세가 형성돼 있으며, 평야는\n 해안지대에 한정되어 분포한다.\n 또한 한라산을  주봉으로 하는 화산체의 경사면을 따라 법정악,미악, 각수암, 삼매봉 등 높고 낮은 기생화산들이 많이 솟아 있다.\n <br>이들 산간에서 발원한 효돈천, 정술내, 에이리내, 연외천, 악근천, 큰내, 창고천 \n 등 비교적 큰 하천들이 남쪽으로 흐르고 있으며 깊은 골짜기를 형성한 이들 하천은 하류에서 천지연, 정방, 천제연 등의 폭포를 이루고 바다로 흘러 들어간다.\n 온대권에 속하는 서귀포시는 대체로 온화한 편이나 강풍이 많고 돌발적 기상변화가 다발하고 있다.\n<br><br>\n\n * 역사\n <br>고대 - 탐라국의 일부로 삼한시대부터 중국 대륙 및 한반도와의 교역이 행해졌으며 498년(동성왕20)에 이르러 백제에 속하였다가, 백제가 멸망한 뒤 신라에 속하게 되었다.\n <br>고려 - 태조가 후삼국을 통일한 뒤 고려의 속국이 되었으며 1105년(숙종10)에 이르러 탐라군으로 개편되어 고려의 직접 관할에 들어갔다. 1271년 삼별초의 난 이후 원나라의 직속령이 되었으며, 1276년(충렬왕2) 원나라는  이곳에 목마장을 개설하여 망할 때까지 관할하였다. 1294년 고려에 환원되었고, 1367년(공민왕16)까지 여러 차례 귀속이 바뀌었다.  \n 1300년(충렬왕 26)에는 탐라현 산하에 14개 현촌이 설치 되었는데, 그 가운데 서귀포시에 해당되는 지역은 예재와 홍로이다.\n\n<br> 조선 - 1416년 한라산을 경계로 산남과 산북으로 나뉘었는데 산남의 정의현에 소속된 홍로와 대정현에 소속된 예재가 지금의 서귀포시 지역에 해당된다.  1608년(광해군1) 정의현은 동, 중, 서 3면, 대정현은 동, 서 2면으로 개편되었다. \n 1864년 (고종1) 정의현과 대정현은 군으로 승격돼  전라도 관찰사 관할하에 있다가 1880년 다시 현으로 환원되었다.\n <br>근대 - 1897년에는 정의현이 좌, 중, 우 3면으로, 대정현이 좌, 중 2개면으로 개편 되었으며, 1911년 정의현에 서중면을 증설함에 따라 그 동쪽을 동중면이라 하였고, 1913년에는 대정현에도 우면이 증설되었다. 1914년에는 정의, 대정, 두현이 제주군에 병합되었고, 이 때 정의현 좌면을 정의면, 대정현 우면을  대정면으로 개칭하였다. 1915년 도제 실시로  서귀는 제주도 우면, 중문은 좌면이 되었고,\n 1935년 서귀면과 중문면으로 개칭하게 되었다.\n<br>\n 현대 - 1946년 도제 실시로 남제주군  서귀면과 중문면이 되었다가, 1956년 서귀면이 읍으로 승격되었고, 1981년 7월 서귀읍과 중문면이 통합되어 시로 승격, 서귀포시가 되었다. 2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와\n 남제주군이 통합되어 서귀포시로 새롭게 출발되었다.<br> \n\n<bR>\n * 문화<br>\n 민속 - 집단놀이인 걸궁이 대표적 민속놀이인데 걸궁은 농악의 일종으로, 집집마다 돌며 굿을 쳐주며 잡귀를 쫓고 행운이 오게 하는 마을의 정월 놀이 행사이다.\n <br>동제 - 정월 첫 정일에 남성들이 주관하여 유교식으로 행하는 \'포제\' 와 정월 초하루에서 보름 사이에 여성들이 주관하여 심방을 불러행하는 \'당굿\' 이 있다.\n <br>', '126.5105853278', '33.2556182395', 'http://tong.visitkorea.or.kr/cms/resource/02/1941602_image2_1.jpg', '0', '', '481522', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 5, 4, '성산일출봉 [유네스코 세계자연유산]', '12', NULL, '제주특별자치도 서귀포시 성산읍 일출로 284-12', '63643', '126435', '', '해발 180m인 성산 일출봉은 약 5,000년 전 제주도 수많은 분화구 중에서는 드물게 바닷속에서 수중폭발한 화산체이다. 용암이 물에 섞일 때 일어나는 폭발로 용암은 고운  화산재로 부서져 분화구 둘레에 원뿔형으로 쌓여 있다. 원래는 화산섬이었지만 신양해수욕장 쪽 땅과 섬  사이에 모래와 자갈이 쌓여 육지와 연결이 된 것이다. 일출봉 정상에는 지름 600m, 바닥면의 높이 해발 90m에 면적이 214,400㎡(성산일출봉 분화구의 면적)나 되는 분화구가 자리한다. 이곳에서  이장호 감독의  영화 \'공포의 외인구단\'의 한 장면이 촬영되기도 했다. 농사를 짓기도  했는데 지금은 억새밭을 이루며 우도가 손에 잡힐 듯 보인다.<br /> 예로부터 이곳 성산일출봉 정상에서 바라보는 일출광경은 영주10경(제주의 경승지) 중에서 으뜸이라 하였다.  넘실대는 푸른 바다 저편 수평선에서 이글거리며 솟아오르는 일출은 온 바다를 물들이고 보는 이의 마음까지도 붙잡아 놓으며 보는 이로 하여금 저절로 감탄케 한다. 지방기념물로 관리하다 2000년 7월 19일 천연기념물로 지정된 곳이다.<br>', '126.9415156012', '33.4581111174', 'http://tong.visitkorea.or.kr/cms/resource/85/1876185_image2_1.jpg', '0', '', '45969', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 5, 5, '해운대해수욕장', '12', NULL, '부산광역시 해운대구 해운대해변로 264', '48100', '126081', '', '* 2019 해수욕장 개장정보<br />- 기간: 6.1.~8.31<br />- 시간: 09:00~18:00 / 18:00~21:00<br />- 야간개장: 7.26~8.11<br /><br />* 부산 대표 해수욕장, 해운대해수욕장<br /><br />부산의 대표 해수욕장인 해운대해수욕장. 백사장의 길이 1.5km, 너비 30~50m, 평균수심 1m, 면적 58,400㎥의 규모로 넓은 백사장과 아름다운 해안선을 자랑하고 있으며 얕은 수심과 잔잔한 물결로 해수욕장의 최적 조건을 갖추고 있다.\'부산\' 하면 제일 먼저 떠올리는 곳이 해운대 해수욕장이라고 할 만큼 부산을 대표하는 명소이며, 해마다 여름철 피서객을 가늠하는 척도로 이용될 만큼 국내 최대 인파가 몰리는 곳이기도 하다. 특히, 해안선 주변에 크고 작은 빌딩들과 고급 호텔들이 우뚝 솟아있어 현대적이고 세련된 분위기의 해수욕장으로 유명하기 때문에 여름 휴가철뿐만 아니라 사시사철 젊은 열기로 붐비고 해외 관광객들에게도 잘 알려져 있어 외국인들이 많이 찾는 곳이다.<br /><br />* 해운대해수욕장의 다양한 축제와 즐길거리<br /><br />해운대해수욕장에서는 매년 정월 대보름날의 달맞이 축제를 진행하고 있다. 또한, 매년 겨울 주최하고 있는 북극곰수영대회는 이미 겨울철 대표 축제로 자리잡았다. 이외에도 모래 작품전, 부산 바다 축제 등 각종 크고 작은 행사들이 열리고 있다. 또한, 해수욕장 주변에 동백섬, 오륙도, 아쿠아리움 , 요트경기장, 벡스코 달맞이고개, 드라이브코스 등 볼거리가 많으며, 국내 1급 해수욕장답게 주변에는 일급 호텔을 비롯한 숙박, 오락시설 및 유흥 시설들이 잘 정비되어 있어 편안한 휴식을 즐길 수 있다.<br>', '129.1603078991', '35.1591243474', 'http://tong.visitkorea.or.kr/cms/resource/67/2612467_image2_1.jpg', '0', '', '278087', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 6, 1, '광화문', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126512', '', '※ 19_20 한국관광 100선 ※<br /><br />광화문은 경복궁의 남문이며, 궁성의 정문이다. 광화문은 국왕이 드나드는 정문이기도 했지만, 조선의 법궁인 경복궁의 정문이었기 때문에 다른 궁궐의 정문에 비해 그 규모와 격식 면에서도 매우 웅장하고 화려했다. 또한 광화문은 담장 끝 동쪽과 서쪽에 각각 동십자각과 서십자각을 두어 조선의 5대 궁궐 가운데 유일하게 궐문형식을 갖추고 있기도 했다. 광화문은 중층으로 된 문루를 받치는 기단석축에 세 개의 홍예로 이루어져 있는데, 중앙의 홍예로는 왕이, 좌우의 홍예로는 왕세자와 신하들이 출입하도록 하였다. 문루(門樓)에는 종을 걸어 때를 알리는 데 사용하였다. 이처럼 경복궁이 조선왕조의 법궁이라는 점은 광화문을 비롯한 성문의 형태에서도 확인할 수 있다. 다른 궁의 문들은 낮은 단 위에 문을 세우지만 경복궁의 문은 돌로 높은 석축을 쌓고 중앙에 홍예문을 내서 마치 성곽의 문과 같은 모습을 취한다. 광화문은 그 중에서도 홍예문을 셋이나 내서 가장 격식 높은 모습을 보여준다. 광화문이 법궁의 정문이라는 사실을 잘 드러내준다. 경복궁 창건 당시에는 특별한 이름이 없이 궁제에 따라 \'오문(午門)\'으로 부르다가, 태조 3년(1395) 정도전에 의해 \'정문(正門)\'으로 이름을 바꾸지만, 세종 8년(1426)에 경복궁을 수리하면서 집현전에서 \'광화문(光化門)\'이라 이름을 지어 올리면서 지금의 광화문이란 이름을 얻게 되었다.<br /><br />원래 광화문은 조선총독부 건물을 지으면서 궁성의 동문인 건춘문 북쪽으로 옮겼으나 한국전쟁 때 폭격을 맞아 문루가 부서지고 석축은 탄흔 투성이가 되었다. 그런 가운데 광화문이 현재의 모습으로 재건된 것은 1968년 박정희 정권에 이르러서다. 파괴된 문루를 다시 짓고 광화문을 현재의 자리로 옮겨 놓았던 것이다.그러나 새로 재건한 광화문은 철근 콘크리트로 지어졌으며, 재건 당시 광화문의 축을 경복궁의 중심축에 맞춘 것이 아니라 당시 중앙청으로 쓰이던 구 조선총독부청사의 축에 맞추어, 그 결과 3.5도 가량 본래의 축과 어긋나게 틀어지고 말았다. 뿐만 아니라 원래의 광화문 자리에서도 14.5미터 가량 뒤로 물러나 지금의 자리에 서있게 되었다. 이렇게 원형을 잃어버린 광화문을 복원하고자 2006년부터 광화문 철거작업을 시작해 3년 8개월의 복원 공사를 마치고 2010년 8월 15일 완공되었다. 화강암으로 육중한 기단을 만들고 그 위에 이층의 문루를 얹어 멀리 조망하기 좋을 뿐 아니라 궁궐 정문으로서의 위엄을 나타내고 있다. 기단에 있는 3개의 아치형 출입문은 경복궁이 조선의 정궁이라는 위엄을 과시한다. 돌로 만든 기단 위에는 흙을 구워 벽돌처럼 만든 \'전돌\'로 나지막한 담을 둘렀는데, 여기를 장식한 팔괘문양은 조선조 궁궐의 특성을 잘 반영하고있다.<br>', '126.9768042386', '37.5760725520', 'http://tong.visitkorea.or.kr/cms/resource/81/1075281_image2_1.jpg', '0', '', '332185', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 6, 2, '경복궁', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126508', '', '※ 19_20 한국관광 100선 ※<br /><br />경복궁은 1395년 태조 이성계에 의해서 새로운 조선왕조의 법궁으로 지어졌다. 경복궁은 동궐(창덕궁)이나 서궐(경희궁)에 비해 위치가 북쪽에 있어 \'북궐\'이라 불리기도 했다. 경복궁(사적 제117호)은 5대 궁궐 가운데 으뜸의 규모와 건축미를 자랑한다. 경복궁 근정전에서 즉위식을 가진 왕들을 보면 제2대 정종, 제4대 세종, 제6대 단종, 제7대 세조, 제9대 성종, 제11대 중종, 제13대 명종 등이다. 경복궁은 임진왜란 때 상당수의 건물이 불타 없어진 아픔을 갖고 있으며, 고종 때에 흥선대원군의 주도 아래 7,700여칸에 이르는 건물들을 다시 세웠다. 그러나 또 다시 명성황후 시해사건이 일어나면서 왕조의 몰락과 함께 경복궁도 왕궁으로서의 기능을 상실하고 말았다. 경복궁에는 조선시대의 대표적인 건축물인 경회루와 향원정의 연못이 원형대로 남아 있으며, 근정전의 월대와 조각상들은 당시의 조각미술을 대표한다. 현재 흥례문 밖 서편에는 국립고궁 박물관이 위치하고 있고, 경복궁 내 향원정의 동편에는 국립민속 박물관이 위치하고 있다.<br /><br />* 주요문화재 <br />1) 경복궁(사적 제117호)<br />2) 경복궁 근정전(국보 제223호)<br />3) 경복궁 경회루(국보 제224호)<br />4) 경복궁 자경전(보물 제809호)<br />5) 경복궁 자경전 십장생 굴뚝(보물 제810호)<br />6) 경복궁 아미산굴뚝 (보물 제811호)<br />7) 경복궁 근정문 및 행각(보물 제812호)<br />8) 경복궁 풍기대(보물 제847호)<br>', '126.9769930325', '37.5788222356', 'http://tong.visitkorea.or.kr/cms/resource/40/1568040_image2_1.jpg', '0', '', '306', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 6, 3, '서귀포시', '12', NULL, '제주 서귀포시', '697-701', '125402', '', '2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와 남제주군이 통합되어 서귀포시로 새롭게 출발하였다. 대륙(러시아,중국)과 해양(일본,동남아)을\n 연결하는 요충지이며 천혜의 자연경관이 수려한 세계적인 휴양 관광지인 제주특별자치도의 남부지역이다.\n 전체 면적 870.68㎢이며 총인구 157,840명(2014년 9월 기준)에 이른다. \n 3읍 2면 12개 동으로 구성되어 있다.<br>\n <br>* 자연환경\n<br>\n 한라산(해발 1,950m)의 남쪽에 위치해 북고남저의 형태를 나타낸다. 한라산이 남쪽으로 치우쳐 있고 북사면보다 경사가 급해 산악 지세가 형성돼 있으며, 평야는\n 해안지대에 한정되어 분포한다.\n 또한 한라산을  주봉으로 하는 화산체의 경사면을 따라 법정악,미악, 각수암, 삼매봉 등 높고 낮은 기생화산들이 많이 솟아 있다.\n <br>이들 산간에서 발원한 효돈천, 정술내, 에이리내, 연외천, 악근천, 큰내, 창고천 \n 등 비교적 큰 하천들이 남쪽으로 흐르고 있으며 깊은 골짜기를 형성한 이들 하천은 하류에서 천지연, 정방, 천제연 등의 폭포를 이루고 바다로 흘러 들어간다.\n 온대권에 속하는 서귀포시는 대체로 온화한 편이나 강풍이 많고 돌발적 기상변화가 다발하고 있다.\n<br><br>\n\n * 역사\n <br>고대 - 탐라국의 일부로 삼한시대부터 중국 대륙 및 한반도와의 교역이 행해졌으며 498년(동성왕20)에 이르러 백제에 속하였다가, 백제가 멸망한 뒤 신라에 속하게 되었다.\n <br>고려 - 태조가 후삼국을 통일한 뒤 고려의 속국이 되었으며 1105년(숙종10)에 이르러 탐라군으로 개편되어 고려의 직접 관할에 들어갔다. 1271년 삼별초의 난 이후 원나라의 직속령이 되었으며, 1276년(충렬왕2) 원나라는  이곳에 목마장을 개설하여 망할 때까지 관할하였다. 1294년 고려에 환원되었고, 1367년(공민왕16)까지 여러 차례 귀속이 바뀌었다.  \n 1300년(충렬왕 26)에는 탐라현 산하에 14개 현촌이 설치 되었는데, 그 가운데 서귀포시에 해당되는 지역은 예재와 홍로이다.\n\n<br> 조선 - 1416년 한라산을 경계로 산남과 산북으로 나뉘었는데 산남의 정의현에 소속된 홍로와 대정현에 소속된 예재가 지금의 서귀포시 지역에 해당된다.  1608년(광해군1) 정의현은 동, 중, 서 3면, 대정현은 동, 서 2면으로 개편되었다. \n 1864년 (고종1) 정의현과 대정현은 군으로 승격돼  전라도 관찰사 관할하에 있다가 1880년 다시 현으로 환원되었다.\n <br>근대 - 1897년에는 정의현이 좌, 중, 우 3면으로, 대정현이 좌, 중 2개면으로 개편 되었으며, 1911년 정의현에 서중면을 증설함에 따라 그 동쪽을 동중면이라 하였고, 1913년에는 대정현에도 우면이 증설되었다. 1914년에는 정의, 대정, 두현이 제주군에 병합되었고, 이 때 정의현 좌면을 정의면, 대정현 우면을  대정면으로 개칭하였다. 1915년 도제 실시로  서귀는 제주도 우면, 중문은 좌면이 되었고,\n 1935년 서귀면과 중문면으로 개칭하게 되었다.\n<br>\n 현대 - 1946년 도제 실시로 남제주군  서귀면과 중문면이 되었다가, 1956년 서귀면이 읍으로 승격되었고, 1981년 7월 서귀읍과 중문면이 통합되어 시로 승격, 서귀포시가 되었다. 2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와\n 남제주군이 통합되어 서귀포시로 새롭게 출발되었다.<br> \n\n<bR>\n * 문화<br>\n 민속 - 집단놀이인 걸궁이 대표적 민속놀이인데 걸궁은 농악의 일종으로, 집집마다 돌며 굿을 쳐주며 잡귀를 쫓고 행운이 오게 하는 마을의 정월 놀이 행사이다.\n <br>동제 - 정월 첫 정일에 남성들이 주관하여 유교식으로 행하는 \'포제\' 와 정월 초하루에서 보름 사이에 여성들이 주관하여 심방을 불러행하는 \'당굿\' 이 있다.\n <br>', '126.5105853278', '33.2556182395', 'http://tong.visitkorea.or.kr/cms/resource/02/1941602_image2_1.jpg', '0', '', '481522', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 6, 4, '성산일출봉 [유네스코 세계자연유산]', '12', NULL, '제주특별자치도 서귀포시 성산읍 일출로 284-12', '63643', '126435', '', '해발 180m인 성산 일출봉은 약 5,000년 전 제주도 수많은 분화구 중에서는 드물게 바닷속에서 수중폭발한 화산체이다. 용암이 물에 섞일 때 일어나는 폭발로 용암은 고운  화산재로 부서져 분화구 둘레에 원뿔형으로 쌓여 있다. 원래는 화산섬이었지만 신양해수욕장 쪽 땅과 섬  사이에 모래와 자갈이 쌓여 육지와 연결이 된 것이다. 일출봉 정상에는 지름 600m, 바닥면의 높이 해발 90m에 면적이 214,400㎡(성산일출봉 분화구의 면적)나 되는 분화구가 자리한다. 이곳에서  이장호 감독의  영화 \'공포의 외인구단\'의 한 장면이 촬영되기도 했다. 농사를 짓기도  했는데 지금은 억새밭을 이루며 우도가 손에 잡힐 듯 보인다.<br /> 예로부터 이곳 성산일출봉 정상에서 바라보는 일출광경은 영주10경(제주의 경승지) 중에서 으뜸이라 하였다.  넘실대는 푸른 바다 저편 수평선에서 이글거리며 솟아오르는 일출은 온 바다를 물들이고 보는 이의 마음까지도 붙잡아 놓으며 보는 이로 하여금 저절로 감탄케 한다. 지방기념물로 관리하다 2000년 7월 19일 천연기념물로 지정된 곳이다.<br>', '126.9415156012', '33.4581111174', 'http://tong.visitkorea.or.kr/cms/resource/85/1876185_image2_1.jpg', '0', '', '45969', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 6, 5, '해운대해수욕장', '12', NULL, '부산광역시 해운대구 해운대해변로 264', '48100', '126081', '', '* 2019 해수욕장 개장정보<br />- 기간: 6.1.~8.31<br />- 시간: 09:00~18:00 / 18:00~21:00<br />- 야간개장: 7.26~8.11<br /><br />* 부산 대표 해수욕장, 해운대해수욕장<br /><br />부산의 대표 해수욕장인 해운대해수욕장. 백사장의 길이 1.5km, 너비 30~50m, 평균수심 1m, 면적 58,400㎥의 규모로 넓은 백사장과 아름다운 해안선을 자랑하고 있으며 얕은 수심과 잔잔한 물결로 해수욕장의 최적 조건을 갖추고 있다.\'부산\' 하면 제일 먼저 떠올리는 곳이 해운대 해수욕장이라고 할 만큼 부산을 대표하는 명소이며, 해마다 여름철 피서객을 가늠하는 척도로 이용될 만큼 국내 최대 인파가 몰리는 곳이기도 하다. 특히, 해안선 주변에 크고 작은 빌딩들과 고급 호텔들이 우뚝 솟아있어 현대적이고 세련된 분위기의 해수욕장으로 유명하기 때문에 여름 휴가철뿐만 아니라 사시사철 젊은 열기로 붐비고 해외 관광객들에게도 잘 알려져 있어 외국인들이 많이 찾는 곳이다.<br /><br />* 해운대해수욕장의 다양한 축제와 즐길거리<br /><br />해운대해수욕장에서는 매년 정월 대보름날의 달맞이 축제를 진행하고 있다. 또한, 매년 겨울 주최하고 있는 북극곰수영대회는 이미 겨울철 대표 축제로 자리잡았다. 이외에도 모래 작품전, 부산 바다 축제 등 각종 크고 작은 행사들이 열리고 있다. 또한, 해수욕장 주변에 동백섬, 오륙도, 아쿠아리움 , 요트경기장, 벡스코 달맞이고개, 드라이브코스 등 볼거리가 많으며, 국내 1급 해수욕장답게 주변에는 일급 호텔을 비롯한 숙박, 오락시설 및 유흥 시설들이 잘 정비되어 있어 편안한 휴식을 즐길 수 있다.<br>', '129.1603078991', '35.1591243474', 'http://tong.visitkorea.or.kr/cms/resource/67/2612467_image2_1.jpg', '0', '', '278087', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 7, 1, '경복궁', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126508', '', '※ 19_20 한국관광 100선 ※<br /><br />경복궁은 1395년 태조 이성계에 의해서 새로운 조선왕조의 법궁으로 지어졌다. 경복궁은 동궐(창덕궁)이나 서궐(경희궁)에 비해 위치가 북쪽에 있어 \'북궐\'이라 불리기도 했다. 경복궁(사적 제117호)은 5대 궁궐 가운데 으뜸의 규모와 건축미를 자랑한다. 경복궁 근정전에서 즉위식을 가진 왕들을 보면 제2대 정종, 제4대 세종, 제6대 단종, 제7대 세조, 제9대 성종, 제11대 중종, 제13대 명종 등이다. 경복궁은 임진왜란 때 상당수의 건물이 불타 없어진 아픔을 갖고 있으며, 고종 때에 흥선대원군의 주도 아래 7,700여칸에 이르는 건물들을 다시 세웠다. 그러나 또 다시 명성황후 시해사건이 일어나면서 왕조의 몰락과 함께 경복궁도 왕궁으로서의 기능을 상실하고 말았다. 경복궁에는 조선시대의 대표적인 건축물인 경회루와 향원정의 연못이 원형대로 남아 있으며, 근정전의 월대와 조각상들은 당시의 조각미술을 대표한다. 현재 흥례문 밖 서편에는 국립고궁 박물관이 위치하고 있고, 경복궁 내 향원정의 동편에는 국립민속 박물관이 위치하고 있다.<br /><br />* 주요문화재 <br />1) 경복궁(사적 제117호)<br />2) 경복궁 근정전(국보 제223호)<br />3) 경복궁 경회루(국보 제224호)<br />4) 경복궁 자경전(보물 제809호)<br />5) 경복궁 자경전 십장생 굴뚝(보물 제810호)<br />6) 경복궁 아미산굴뚝 (보물 제811호)<br />7) 경복궁 근정문 및 행각(보물 제812호)<br />8) 경복궁 풍기대(보물 제847호)<br>', '126.9769930325', '37.5788222356', 'http://tong.visitkorea.or.kr/cms/resource/40/1568040_image2_1.jpg', '0', '', '332420', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 7, 2, '서귀포시', '12', NULL, '제주 서귀포시', '697-701', '125402', '', '2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와 남제주군이 통합되어 서귀포시로 새롭게 출발하였다. 대륙(러시아,중국)과 해양(일본,동남아)을\n 연결하는 요충지이며 천혜의 자연경관이 수려한 세계적인 휴양 관광지인 제주특별자치도의 남부지역이다.\n 전체 면적 870.68㎢이며 총인구 157,840명(2014년 9월 기준)에 이른다. \n 3읍 2면 12개 동으로 구성되어 있다.<br>\n <br>* 자연환경\n<br>\n 한라산(해발 1,950m)의 남쪽에 위치해 북고남저의 형태를 나타낸다. 한라산이 남쪽으로 치우쳐 있고 북사면보다 경사가 급해 산악 지세가 형성돼 있으며, 평야는\n 해안지대에 한정되어 분포한다.\n 또한 한라산을  주봉으로 하는 화산체의 경사면을 따라 법정악,미악, 각수암, 삼매봉 등 높고 낮은 기생화산들이 많이 솟아 있다.\n <br>이들 산간에서 발원한 효돈천, 정술내, 에이리내, 연외천, 악근천, 큰내, 창고천 \n 등 비교적 큰 하천들이 남쪽으로 흐르고 있으며 깊은 골짜기를 형성한 이들 하천은 하류에서 천지연, 정방, 천제연 등의 폭포를 이루고 바다로 흘러 들어간다.\n 온대권에 속하는 서귀포시는 대체로 온화한 편이나 강풍이 많고 돌발적 기상변화가 다발하고 있다.\n<br><br>\n\n * 역사\n <br>고대 - 탐라국의 일부로 삼한시대부터 중국 대륙 및 한반도와의 교역이 행해졌으며 498년(동성왕20)에 이르러 백제에 속하였다가, 백제가 멸망한 뒤 신라에 속하게 되었다.\n <br>고려 - 태조가 후삼국을 통일한 뒤 고려의 속국이 되었으며 1105년(숙종10)에 이르러 탐라군으로 개편되어 고려의 직접 관할에 들어갔다. 1271년 삼별초의 난 이후 원나라의 직속령이 되었으며, 1276년(충렬왕2) 원나라는  이곳에 목마장을 개설하여 망할 때까지 관할하였다. 1294년 고려에 환원되었고, 1367년(공민왕16)까지 여러 차례 귀속이 바뀌었다.  \n 1300년(충렬왕 26)에는 탐라현 산하에 14개 현촌이 설치 되었는데, 그 가운데 서귀포시에 해당되는 지역은 예재와 홍로이다.\n\n<br> 조선 - 1416년 한라산을 경계로 산남과 산북으로 나뉘었는데 산남의 정의현에 소속된 홍로와 대정현에 소속된 예재가 지금의 서귀포시 지역에 해당된다.  1608년(광해군1) 정의현은 동, 중, 서 3면, 대정현은 동, 서 2면으로 개편되었다. \n 1864년 (고종1) 정의현과 대정현은 군으로 승격돼  전라도 관찰사 관할하에 있다가 1880년 다시 현으로 환원되었다.\n <br>근대 - 1897년에는 정의현이 좌, 중, 우 3면으로, 대정현이 좌, 중 2개면으로 개편 되었으며, 1911년 정의현에 서중면을 증설함에 따라 그 동쪽을 동중면이라 하였고, 1913년에는 대정현에도 우면이 증설되었다. 1914년에는 정의, 대정, 두현이 제주군에 병합되었고, 이 때 정의현 좌면을 정의면, 대정현 우면을  대정면으로 개칭하였다. 1915년 도제 실시로  서귀는 제주도 우면, 중문은 좌면이 되었고,\n 1935년 서귀면과 중문면으로 개칭하게 되었다.\n<br>\n 현대 - 1946년 도제 실시로 남제주군  서귀면과 중문면이 되었다가, 1956년 서귀면이 읍으로 승격되었고, 1981년 7월 서귀읍과 중문면이 통합되어 시로 승격, 서귀포시가 되었다. 2006년 7월 1일 제주특별자치도의 출범으로 종전의 서귀포시와\n 남제주군이 통합되어 서귀포시로 새롭게 출발되었다.<br> \n\n<bR>\n * 문화<br>\n 민속 - 집단놀이인 걸궁이 대표적 민속놀이인데 걸궁은 농악의 일종으로, 집집마다 돌며 굿을 쳐주며 잡귀를 쫓고 행운이 오게 하는 마을의 정월 놀이 행사이다.\n <br>동제 - 정월 첫 정일에 남성들이 주관하여 유교식으로 행하는 \'포제\' 와 정월 초하루에서 보름 사이에 여성들이 주관하여 심방을 불러행하는 \'당굿\' 이 있다.\n <br>', '126.5105853278', '33.2556182395', 'http://tong.visitkorea.or.kr/cms/resource/02/1941602_image2_1.jpg', '0', '', '481522', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 7, 3, '성산일출봉 [유네스코 세계자연유산]', '12', NULL, '제주특별자치도 서귀포시 성산읍 일출로 284-12', '63643', '126435', '', '해발 180m인 성산 일출봉은 약 5,000년 전 제주도 수많은 분화구 중에서는 드물게 바닷속에서 수중폭발한 화산체이다. 용암이 물에 섞일 때 일어나는 폭발로 용암은 고운  화산재로 부서져 분화구 둘레에 원뿔형으로 쌓여 있다. 원래는 화산섬이었지만 신양해수욕장 쪽 땅과 섬  사이에 모래와 자갈이 쌓여 육지와 연결이 된 것이다. 일출봉 정상에는 지름 600m, 바닥면의 높이 해발 90m에 면적이 214,400㎡(성산일출봉 분화구의 면적)나 되는 분화구가 자리한다. 이곳에서  이장호 감독의  영화 \'공포의 외인구단\'의 한 장면이 촬영되기도 했다. 농사를 짓기도  했는데 지금은 억새밭을 이루며 우도가 손에 잡힐 듯 보인다.<br /> 예로부터 이곳 성산일출봉 정상에서 바라보는 일출광경은 영주10경(제주의 경승지) 중에서 으뜸이라 하였다.  넘실대는 푸른 바다 저편 수평선에서 이글거리며 솟아오르는 일출은 온 바다를 물들이고 보는 이의 마음까지도 붙잡아 놓으며 보는 이로 하여금 저절로 감탄케 한다. 지방기념물로 관리하다 2000년 7월 19일 천연기념물로 지정된 곳이다.<br>', '126.9415156012', '33.4581111174', 'http://tong.visitkorea.or.kr/cms/resource/85/1876185_image2_1.jpg', '0', '', '45969', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 7, 4, '해운대해수욕장', '12', NULL, '부산광역시 해운대구 해운대해변로 264', '48100', '126081', '', '* 2019 해수욕장 개장정보<br />- 기간: 6.1.~8.31<br />- 시간: 09:00~18:00 / 18:00~21:00<br />- 야간개장: 7.26~8.11<br /><br />* 부산 대표 해수욕장, 해운대해수욕장<br /><br />부산의 대표 해수욕장인 해운대해수욕장. 백사장의 길이 1.5km, 너비 30~50m, 평균수심 1m, 면적 58,400㎥의 규모로 넓은 백사장과 아름다운 해안선을 자랑하고 있으며 얕은 수심과 잔잔한 물결로 해수욕장의 최적 조건을 갖추고 있다.\'부산\' 하면 제일 먼저 떠올리는 곳이 해운대 해수욕장이라고 할 만큼 부산을 대표하는 명소이며, 해마다 여름철 피서객을 가늠하는 척도로 이용될 만큼 국내 최대 인파가 몰리는 곳이기도 하다. 특히, 해안선 주변에 크고 작은 빌딩들과 고급 호텔들이 우뚝 솟아있어 현대적이고 세련된 분위기의 해수욕장으로 유명하기 때문에 여름 휴가철뿐만 아니라 사시사철 젊은 열기로 붐비고 해외 관광객들에게도 잘 알려져 있어 외국인들이 많이 찾는 곳이다.<br /><br />* 해운대해수욕장의 다양한 축제와 즐길거리<br /><br />해운대해수욕장에서는 매년 정월 대보름날의 달맞이 축제를 진행하고 있다. 또한, 매년 겨울 주최하고 있는 북극곰수영대회는 이미 겨울철 대표 축제로 자리잡았다. 이외에도 모래 작품전, 부산 바다 축제 등 각종 크고 작은 행사들이 열리고 있다. 또한, 해수욕장 주변에 동백섬, 오륙도, 아쿠아리움 , 요트경기장, 벡스코 달맞이고개, 드라이브코스 등 볼거리가 많으며, 국내 1급 해수욕장답게 주변에는 일급 호텔을 비롯한 숙박, 오락시설 및 유흥 시설들이 잘 정비되어 있어 편안한 휴식을 즐길 수 있다.<br>', '129.1603078991', '35.1591243474', 'http://tong.visitkorea.or.kr/cms/resource/67/2612467_image2_1.jpg', '0', '', '278087', '2019-09-25', '2019-09-25'),
	('PLAN20190900000015', 8, 1, '경복궁', '12', NULL, '서울특별시 종로구 사직로 161', '03045', '126508', '', '※ 19_20 한국관광 100선 ※<br /><br />경복궁은 1395년 태조 이성계에 의해서 새로운 조선왕조의 법궁으로 지어졌다. 경복궁은 동궐(창덕궁)이나 서궐(경희궁)에 비해 위치가 북쪽에 있어 \'북궐\'이라 불리기도 했다. 경복궁(사적 제117호)은 5대 궁궐 가운데 으뜸의 규모와 건축미를 자랑한다. 경복궁 근정전에서 즉위식을 가진 왕들을 보면 제2대 정종, 제4대 세종, 제6대 단종, 제7대 세조, 제9대 성종, 제11대 중종, 제13대 명종 등이다. 경복궁은 임진왜란 때 상당수의 건물이 불타 없어진 아픔을 갖고 있으며, 고종 때에 흥선대원군의 주도 아래 7,700여칸에 이르는 건물들을 다시 세웠다. 그러나 또 다시 명성황후 시해사건이 일어나면서 왕조의 몰락과 함께 경복궁도 왕궁으로서의 기능을 상실하고 말았다. 경복궁에는 조선시대의 대표적인 건축물인 경회루와 향원정의 연못이 원형대로 남아 있으며, 근정전의 월대와 조각상들은 당시의 조각미술을 대표한다. 현재 흥례문 밖 서편에는 국립고궁 박물관이 위치하고 있고, 경복궁 내 향원정의 동편에는 국립민속 박물관이 위치하고 있다.<br /><br />* 주요문화재 <br />1) 경복궁(사적 제117호)<br />2) 경복궁 근정전(국보 제223호)<br />3) 경복궁 경회루(국보 제224호)<br />4) 경복궁 자경전(보물 제809호)<br />5) 경복궁 자경전 십장생 굴뚝(보물 제810호)<br />6) 경복궁 아미산굴뚝 (보물 제811호)<br />7) 경복궁 근정문 및 행각(보물 제812호)<br />8) 경복궁 풍기대(보물 제847호)<br>', '126.9769930325', '37.5788222356', 'http://tong.visitkorea.or.kr/cms/resource/40/1568040_image2_1.jpg', '0', '', '332420', '2019-09-25', '2019-09-25'),
